UPDATE `locales_page_text` SET `text_loc4`='<HTML><BODY><H1 align="center">军阶概述</H1><BR/><BR/><IMG src="Interface\PvPRankBadges\PvPRankAlliance\" align="left"/><BR/><P align="right">What follows are</P><P align="right">the military ranks</P><P align="right">of the Alliance,</P><P align="right">proceeded by such</P><P align="right">information as is</P><P align="right">known for the races</P><P align="right">of the Horde. Each</P><P align="right">is presented in two</P><P align="right">sections, the officers and then the enlisted, with the ranks of each listed in descending order from highest to lowest. Long live the Alliance!</P></BODY></HTML>' WHERE `entry`=2654;
