UPDATE script_texts SET content_loc4='我的觉醒已经完成了！你们的死期到了！' WHERE entry=-1580005; /* content: My awakening is complete! You shall all perish! */
UPDATE script_texts SET content_loc4='快！我的心志快要完全丧失了！' WHERE entry=-1580008; /* content: Hurry! There is not much of me left! */
UPDATE script_texts SET content_loc4='我欠你们一份情。当我们战胜基尔加丹时，整个世界都会感激你们的。' WHERE entry=-1580009; /* content: I am forever in your debt. Once we have triumphed over Kil\'jaeden, this entire world will be in your debt as well. */
UPDATE script_texts SET content_loc4='你的痛苦就是我的欢乐！' WHERE entry=-1580012; /* content: Your misery is my delight! */
UPDATE script_texts SET content_loc4='我就要这么看着你流血！' WHERE entry=-1580013; /* content: I will watch you bleed! */
UPDATE script_texts SET content_loc4='没听说过吗？我永远属于赢的一方！' WHERE entry=-1580015; /* content: Haven\'t you heard? I always win! */
UPDATE script_texts SET content_loc4='很好，我已经把你们玩够了！' WHERE entry=-1580016; /* content: I have toyed with you long enough! */
UPDATE script_texts SET content_loc4='可笑的大蜥蜴！你只有死路一条！' WHERE entry=-1580017; /* content: Puny lizard! Death is the only answer you\'ll find here! */
UPDATE script_texts SET content_loc4='我要粉碎你！' WHERE entry=-1580019; /* content: I will crush you! */
UPDATE script_texts SET content_loc4='有点意思，但我还在等待真正的挑战！' WHERE entry=-1580020; /* content: That was fun, but I still await a true challenge! */
UPDATE script_texts SET content_loc4='死吧，小虫子！' WHERE entry=-1580023; /* content: Perish, insect! */
UPDATE script_texts SET content_loc4='你就是一块肉！' WHERE entry=-1580024; /* content: You are meat! */
UPDATE script_texts SET content_loc4='不堪一击！' WHERE entry=-1580025; /* content: Too easy! */
UPDATE script_texts SET content_loc4='来呀，挑战我吧！' WHERE entry=-1580026; /* content: Bring the fight to me! */
UPDATE script_texts SET content_loc4='又一场光荣的战斗！' WHERE entry=-1580027; /* content: Another day, another glorious battle! */
UPDATE script_texts SET content_loc4='我为杀戮而生！' WHERE entry=-1580028; /* content: I live for this! */
UPDATE script_texts SET content_loc4='等等，朋友们！送这只恶魔下地狱之前，我还有些事要问它。' WHERE entry=-1580031; /* content: Hold, friends! There is information to be had before this devil meets his fate! */
UPDATE script_texts SET content_loc4='安薇娜在哪，恶魔？卡雷怎么了？' WHERE entry=-1580032; /* content: Where is Anveena, demon? What has become of Kalec? */
UPDATE script_texts SET content_loc4='你必须说出他们的下落！' WHERE entry=-1580033; /* content: You will tell me where they are! */
UPDATE script_texts SET content_loc4='基尔加丹万岁！所有敌人都要死！' WHERE entry=-1580036; /* content: Glory to Kil\'jaeden! Death to all who oppose! */
UPDATE script_texts SET content_loc4='为了主人，杀了你！' WHERE entry=-1580037; /* content: I kill for the master! */
UPDATE script_texts SET content_loc4='混乱……' WHERE entry=-1580049; /* content: Chaos... */
UPDATE script_texts SET content_loc4='火焰将在阴影中燃烧！' WHERE entry=-1580052; /* content: Shadow to the aid of fire! */
UPDATE script_texts SET content_loc4='奥蕾塞丝！你的火焰会在我心中燃烧下去！' WHERE entry=-1580053; /* content: Alythess! Your fire burns within me! */
UPDATE script_texts SET content_loc4='Ee-nok Kryul！' WHERE entry=-1580055; /* content: Ee-nok Kryul! */
UPDATE script_texts SET content_loc4='我……要消失了。' WHERE entry=-1580056; /* content: I... fade. */
UPDATE script_texts SET content_loc4='时间，这是你永远也无法拥有的奢侈之物。' WHERE entry=-1580057; /* content: Time is a luxury you no longer possess! */
UPDATE script_texts SET content_loc4='阴影将在火焰下存在！' WHERE entry=-1580058; /* content: Fire to the aid of shadow! */
UPDATE script_texts SET content_loc4='萨洛拉丝！' WHERE entry=-1580059; /* content: Sacrolash! */
UPDATE script_texts SET content_loc4='你们的好运到此为止了！' WHERE entry=-1580063; /* content: Your luck has run its course! */
UPDATE script_texts SET content_loc4='我所有的计划都是为了这一刻的到来！' WHERE entry=-1580064; /* content: All my plans have led to this! */
UPDATE script_texts SET content_loc4='我的等待太漫长了！' WHERE entry=-1580066; /* content: I have waited long enough! */
UPDATE script_texts SET content_loc4='要是让我失望的话，就准备接受惩罚吧！' WHERE entry=-1580067; /* content: Fail me and suffer for eternity! */
UPDATE script_texts SET content_loc4='抽取她的力量！抽空她，直到她只剩下一具空空的躯壳为止！' WHERE entry=-1580068; /* content: Drain the girl! Drain her power until there is nothing but a vacant shell! */
UPDATE script_texts SET content_loc4='混乱！' WHERE entry=-1580074; /* content: Chaos! */
UPDATE script_texts SET content_loc4='毁灭！' WHERE entry=-1580075; /* content: Destruction! */
UPDATE script_texts SET content_loc4='毁灭！' WHERE entry=-1580076; /* content: Oblivion! */
UPDATE script_texts SET content_loc4='我是不会失败的！这个世界注定要毁灭！' WHERE entry=-1580077; /* content: I will not be denied! This world shall fall! */
UPDATE script_texts SET content_loc4='啊啊啊！太阳之井的能量……开始……对抗我！你们都做了些什么？你们都做了些什么？？' WHERE entry=-1580079; /* content: Aggghh! The powers of the Sunwell... turn... against me! What have you done? What have you done??? */
UPDATE script_texts SET content_loc4='我现在只效忠于主人。' WHERE entry=-1580082; /* content: I serve only the Master now. */
UPDATE script_texts SET content_loc4='但我似乎迷失了……，我找不到回去的路。' WHERE entry=-1580084; /* content: But I\'m... lost. I cannot find my way back. */
UPDATE script_texts SET content_loc4='卡雷……卡雷？' WHERE entry=-1580086; /* content: Kalec... Kalec? */
UPDATE script_texts SET content_loc4='我又将能量灌入了另一颗宝珠！快去使用它！' WHERE entry=-1580092; /* content: I have empowered another orb! Use it quickly! */
UPDATE script_texts SET content_loc4='又有一颗宝珠准备好了！快点行动！' WHERE entry=-1580093; /* content: Another orb is ready! Make haste! */
UPDATE script_texts SET content_loc4='这是我所能做的一切了！力量现在掌握在你们的手中！' WHERE entry=-1580094; /* content: I have channeled all I can! The power is in your hands! */
UPDATE script_texts SET content_loc4='凡人英雄们，很早以前的预言之中就昭示了你们今天的胜利。我那堕落兄弟的凄惨号叫将会响彻整个宇宙——这也会把希望带给那些仍然在反抗着燃烧军团的人们。' WHERE entry=-1580095; /* content: Mortal heroes - your victory here today was foretold long ago. My brother\'s anguished cry of defeat will echo across the universe - bringing renewed hope to all those who still stand against the Burning Crusade. */
UPDATE script_texts SET content_loc4='你们之前消灭的那只熵魔，曾经是一位高贵的纳鲁，他名叫穆鲁。生前，他一直将代表着光明与希望的能量带给世人。不过，有一段时间，一些迷茫的孩子却囚禁了它，偷取这些能量。' WHERE entry=-1580098; /* content: The creature Entropius, whom you were forced to destroy, was once the noble naaru, M\'uru. In life, M\'uru channeled vast energies of LIGHT and HOPE. For a time, a misguided few sought to steal those energies... */
UPDATE script_texts SET content_loc4='我们的傲慢是无法宽恕的。我们竟然加害于一位如此高贵的存在，这样的罪孽永远也无法弥补。' WHERE entry=-1580099; /* content: Our arrogance was unpardonable. We damned one of the most noble beings of all. We may never atone for this sin. */
UPDATE script_texts SET content_loc4='多么神圣的灵魂！我感觉到……那么博爱……那么高贵……这……不是语言可以形容的……' WHERE entry=-1580104; /* content: Blessed ancestors! I feel it... so much love... so much grace... there are... no words... impossible to describe... */
UPDATE script_texts SET content_loc4='救赎，孩子。它在等待着我们所有人。' WHERE entry=-1580105; /* content: Salvation, young one. It waits for us all. */
UPDATE script_texts SET content_loc4='%s深深地吸了一口气。' WHERE entry=-1580107; /* content: %s takes a deep breath. */
UPDATE script_texts SET content_loc4='你们只会浪费我的时间！' WHERE entry=-1585000; /* content: You only waste my time! */
UPDATE script_texts SET content_loc4='我的渴求是无限的！' WHERE entry=-1585001; /* content: My hunger knows no bounds! */
UPDATE script_texts SET content_loc4='别打扰我了！' WHERE entry=-1585003; /* content: Enough distractions! */
UPDATE script_texts SET content_loc4='我是不可战胜的！' WHERE entry=-1585004; /* content: I am invincible! */
UPDATE script_texts SET content_loc4='无……法……控制。' WHERE entry=-1585008; /* content: Un...con...tainable. */
UPDATE script_texts SET content_loc4='吞……噬。' WHERE entry=-1585010; /* content: Con...sume. */
UPDATE script_texts SET content_loc4='现在我可真有点火了。' WHERE entry=-1585015; /* content: Now I\'m getting annoyed. */
UPDATE script_texts SET content_loc4='这就是一个好的开始。' WHERE entry=-1585017; /* content: I call that a good start. */
UPDATE script_texts SET content_loc4='“一”这个数字，可真令人感到孤独。' WHERE entry=-1585020; /* content: One is such a lonely number. */
UPDATE script_texts SET content_loc4='一场完胜，绝对的完胜。' WHERE entry=-1585021; /* content: It\'s been a kick, really. */
UPDATE script_texts SET content_loc4='复仇之炎，燃烧吧！' WHERE entry=-1585024; /* content: Vengeance burns! */
UPDATE script_texts SET content_loc4='洛丹伦的士兵都听着！立即前往斯坦索姆入口处集合，等待阿尔萨斯王子的进一步指示。' WHERE entry=-1595000; /* content: All soldiers of Lordaeron should immediately report to the entrance of Stratholme, and await further orders from Prince Arthas. */
UPDATE script_texts SET content_loc4='干得漂亮！到斯坦索姆的大门去，我会交给你下一项任务！' WHERE entry=-1595001; /* content: Good work with the crates! Come talk to me in front of Stratholme for your next assignment! */
UPDATE script_texts SET content_loc4='节庆小巷城门附近发现天灾军团踪迹！' WHERE entry=-1595003; /* content: Scourge forces have been spotted near the Festival Lane Gate! */
UPDATE script_texts SET content_loc4='国王广场的喷泉附近发现天灾军团踪迹！' WHERE entry=-1595004; /* content: Scourge forces have been spotted near the King\'s Square fountain! */
UPDATE script_texts SET content_loc4='市场小径城门附近发现天灾军团踪迹！' WHERE entry=-1595005; /* content: Scourge forces have been spotted near the Market Row Gate! */
UPDATE script_texts SET content_loc4='市政厅附近发现天灾军团踪迹！' WHERE entry=-1595006; /* content: Scourge forces have been spotted near the Town Hall! */
UPDATE script_texts SET content_loc4='长者广场城门附近发现天灾军团踪迹！' WHERE entry=-1595007; /* content: Scourge forces have been spotted near the Elder\'s Square Gate! */
UPDATE script_texts SET content_loc4='立即与我在市政厅会合，勇士。我们必须直接挑战玛尔加尼斯。' WHERE entry=-1595008; /* content: Champions, meet me at the Town Hall at once. We must take the fight to Mal\'Ganis. */
UPDATE script_texts SET content_loc4='跟我来，我知道路。' WHERE entry=-1595009; /* content: Follow me, I know the way through. */
UPDATE script_texts SET content_loc4='啊，是阿尔萨斯王子。您来的正是时候。' WHERE entry=-1595010; /* content: Ah, you\'ve finally arrived Prince Arthas. You\'re here just in the nick of time. */
UPDATE script_texts SET content_loc4='是的，我很高兴能在灾难大爆发之前找到你。' WHERE entry=-1595011; /* content: Yes, I\'m glad I could get to you before the plague. */
UPDATE script_texts SET content_loc4='这是什么巫术？' WHERE entry=-1595012; /* content: What is this sorcery? */
UPDATE script_texts SET content_loc4='阿尔萨斯，你不需要弄明白这是什么。你唯一要做的，就是等死。' WHERE entry=-1595013; /* content: There\'s no need for you to understand, Arthas. All you need to do is die. */
UPDATE script_texts SET content_loc4='看来玛尔加尼斯除了天灾军团的亡灵大军外，还另有秘密武器。我们得抓紧时间了。' WHERE entry=-1595014; /* content: Mal\'Ganis appears to have more than Scourge in his arsenal. We should make haste. */
UPDATE script_texts SET content_loc4='又是这种邪恶的巫术！提高警惕！' WHERE entry=-1595015; /* content: More vile sorcery! Be ready for anything! */
UPDATE script_texts SET content_loc4='我们走吧。' WHERE entry=-1595016; /* content: Let\'s move on. */
UPDATE script_texts SET content_loc4='小心背后，我们被包围了。' WHERE entry=-1595017; /* content: Watch your backs: they have us surrounded in this hall. */
UPDATE script_texts SET content_loc4='玛尔加尼斯不会让我们好过的。' WHERE entry=-1595018; /* content: Mal\'Ganis is not making this easy. */
UPDATE script_texts SET content_loc4='它们还真是够顽强的。' WHERE entry=-1595019; /* content: They\'re very persistent. */
UPDATE script_texts SET content_loc4='他还会派些什么东西来阻挡我们？' WHERE entry=-1595020; /* content: What else can he put in my way? */
UPDATE script_texts SET content_loc4='阿尔萨斯·米奈希尔王子殿下，今天，你的灵魂已经被某种强大的黑暗力量侵蚀。你想要以死亡净化这座城市，现在你也要面对毁灭的命运了。' WHERE entry=-1595021; /* content: Prince Arthas Menethil, on this day, a powerful darkness has taken hold of your soul. The death you are destined to visit upon others will this day be your own. */
UPDATE script_texts SET content_loc4='为了洛丹伦，我会竭尽所能，无论是你的言论还是行为，都无法阻止我。' WHERE entry=-1595022; /* content: I do what I must for Lordaeron, and neither your words nor your actions will stop me. */
UPDATE script_texts SET content_loc4='通向玛尔加尼斯的最短路线就在前面那个书架之后。' WHERE entry=-1595023; /* content: The quickest path to Mal\'Ganis lies behind that bookshelf ahead. */
UPDATE script_texts SET content_loc4='这只需要一小会儿。' WHERE entry=-1595024; /* content: This will only take a moment. */
UPDATE script_texts SET content_loc4='我确信这条秘道还通着。' WHERE entry=-1595025; /* content: I\'m relieved this secret passage still works. */
UPDATE script_texts SET content_loc4='我们尽快离开这里。否则，就算不被亡灵杀死，也要被火烧死。' WHERE entry=-1595026; /* content: Let\'s move through here as quickly as possible. If the undead don\'t kill us, the fires might. */
UPDATE script_texts SET content_loc4='休息一会，你们抓紧时间喘口气，我们马上就得重新上路。' WHERE entry=-1595027; /* content: Rest a moment and clear your lungs, but we must move again soon. */
UPDATE script_texts SET content_loc4='就这样吧，我们必须继续上路了。玛尔加尼斯在等着我们呢。' WHERE entry=-1595028; /* content: That\'s enough; we must move again. Mal\'Ganis awaits. */
UPDATE script_texts SET content_loc4='圣光在上。市场小径还没有着火。玛尔加尼斯应该就躲藏在前面的十字军广场。你们准备好继续前进的话，就告诉我。' WHERE entry=-1595029; /* content: At last some good luck. Market Row has not caught fire yet. Mal\'Ganis is supposed to be in Crusaders\' Square, which is just ahead. Tell me when you\'re ready to move forward. */
UPDATE script_texts SET content_loc4='正义必将得到伸张。' WHERE entry=-1595030; /* content: Justice will be done. */
UPDATE script_texts SET content_loc4='让我们来做个了断吧，玛尔加尼斯。' WHERE entry=-1595031; /* content: We\'re going to finish this right now, Mal\'Ganis. Just you... and me. */
UPDATE script_texts SET content_loc4='你的旅程才刚开始，年轻的王子。集合你的部队，到诺森德再次挑战我。在那里，我们将了结彼此之间的恩怨，你将了解到你真正的命运。' WHERE entry=-1595032; /* content: Your journey has just begun, young prince. Gather your forces and meet me in the arctic land of Northrend. It is there that we shall settle the score between us. It is there that your true destiny will unfold. */
UPDATE script_texts SET content_loc4='就算你跑到天涯海角，我也一定要抓到你！听到了吗？天涯海角！' WHERE entry=-1595033; /* content: I\'ll hunt you to the ends of the earth if I have to! Do you hear me? To the ends of the earth! */
UPDATE script_texts SET content_loc4='你们表现得很出色。玛尔加尼斯留下的一切都是你们的了，把它们收下作为奖励吧。现在我要马上开始计划远征诺森德了。' WHERE entry=-1595034; /* content: You performed well this day. Anything that Mal\'Ganis has left behind is yours. Take it as your reward. I must now begin plans for an expedition to Northrend. */
UPDATE script_texts SET content_loc4='保护你们的王子，洛丹伦的士兵们，我需要帮助！' WHERE entry=-1595035; /* content: Protect your prince, soldiers of Lordaeron! I am in need of aid! */
UPDATE script_texts SET content_loc4='玛尔加尼斯必将为此付出代价。' WHERE entry=-1595037; /* content: Mal\'Ganis will pay for this. */
UPDATE script_texts SET content_loc4='我不能放过你。' WHERE entry=-1595038; /* content: I can\'t afford to spare you. */
UPDATE script_texts SET content_loc4='又少了一个障碍。' WHERE entry=-1595039; /* content: One less obstacle to deal with. */
UPDATE script_texts SET content_loc4='血肉永远无法胜过钢铁！' WHERE entry=-1599002; /* content: Flesh is no match for iron! */
UPDATE script_texts SET content_loc4='钢铁的大军将令整个世界窒息！' WHERE entry=-1599003; /* content: Armies of iron will smother the world! */
UPDATE script_texts SET content_loc4='为什么会这样呢？' WHERE entry=-1599006; /* content: Why must it be this way? */
UPDATE script_texts SET content_loc4='是你逼我这么做的！' WHERE entry=-1599007; /* content: You had it coming! */
UPDATE script_texts SET content_loc4='失去了这么多时间……你们永远也无法挽回！' WHERE entry=-1599010; /* content: So much lost time... that you\'ll never get back! */
UPDATE script_texts SET content_loc4='希望这牺牲是值得的！' WHERE entry=-1599019; /* content: I hope this is all worth it! */
UPDATE script_texts SET content_loc4='是该收获答案了！让我们一路杀过去吧！' WHERE entry=-1599020; /* content: Time to get some answers! Let\'s get this show on the road! */
UPDATE script_texts SET content_loc4='玛多兰在上，这玩意到底干了些什么？哦！等等，我马上就搞定它了……' WHERE entry=-1599023; /* content: What in the name o\' Madoran did THAT do? Oh! Wait: I just about got it... */
UPDATE script_texts SET content_loc4='嗯，你们帮我看着点外面。我这样的强者只要锤两下就能搞定这破烂……' WHERE entry=-1599026; /* content: Now keep an eye out! I\'ll have this licked in two shakes of a-- */
UPDATE script_texts SET content_loc4='噢，见鬼，好像有点不太妙，看来我们遇到点小麻烦啦……' WHERE entry=-1599028; /* content: Oh, that doesn\'t sound good. We might have a complication or two... */
UPDATE script_texts SET content_loc4='嘿，破机器，怎么样？你想跟我来硬的？太可惜了，这是属于我的游戏！' WHERE entry=-1599030; /* content: Ah, you want to play hardball, eh? That\'s just my game! */
UPDATE script_texts SET content_loc4='再给我几分钟，马上就能……啊噢……' WHERE entry=-1599031; /* content: Couple more minutes and I\'ll-- */
UPDATE script_texts SET content_loc4='提高？这算是好消息吗？' WHERE entry=-1599033; /* content: Heightened? What\'s the good news? */
UPDATE script_texts SET content_loc4='哈！终于找到问题出在哪儿了！我马上就能搞定了……' WHERE entry=-1599034; /* content: So that was the problem? Now I\'m makin\' progress... */
UPDATE script_texts SET content_loc4='威胁指数过高。虚空分析程序关闭。启动清理协议。' WHERE entry=-1599035; /* content: Critical threat index. Void analysis diverted. Initiating sanitization protocol. */
UPDATE script_texts SET content_loc4='等等！你想清理谁啊！有我在，没门！' WHERE entry=-1599036; /* content: Hang on! Nobody\'s gonna\' be sanitized as long as I have a say in it! */
UPDATE script_texts SET content_loc4='什么守护者？' WHERE entry=-1599052; /* content: What protectors? */
UPDATE script_texts SET content_loc4='等等！等等！你是说艾塞尔与瓦尼尔之间爆发了战争？为什么？' WHERE entry=-1599056; /* content: Hold everything! The Aesir and Vanir went to war? Why? */
UPDATE script_texts SET content_loc4='本质上的确如此。' WHERE entry=-1599059; /* content: Essentially that is correct. */
UPDATE script_texts SET content_loc4='洛肯？！这下我们碰到大麻烦了……虽然铁矮人已经被全部摆平了，但我敢打赌不知道什么鬼地方还有台机器，正不停向外吐着铁维库人！' WHERE entry=-1599062; /* content: Loken?! That\'s downright bothersome... We might\'ve neutralized the iron dwarves, but I\'d lay odds there\'s another machine somewhere else churnin\' out a whole mess o\' these iron vrykul! */
UPDATE script_texts SET content_loc4='让我们看看入口附近的大门后面藏着什么。我会悄悄地潜过去。去那里和我碰面吧，我会带你们进去吧。' WHERE entry=-1599064; /* content: I think it\'s time to see what\'s behind the door near the entrance. I\'m going to sneak over there, nice and quiet. Meet me at the door and I\'ll get us in. */
UPDATE script_texts SET content_loc4='好饿啊！必须吃饭！' WHERE entry=-1600001; /* content: So hungry! Must feed! */
UPDATE script_texts SET content_loc4='要爆炸啦！' WHERE entry=-1600003; /* content: Corpse go boom! */
UPDATE script_texts SET content_loc4='这一切……都是毫无意义的。' WHERE entry=-1600006; /* content: Your efforts... are in vain. */
UPDATE script_texts SET content_loc4='协助防御！快点，废物们！' WHERE entry=-1600008; /* content: Bolster my defenses! Hurry, curse you! */
UPDATE script_texts SET content_loc4='很快你们就会发现一切都是徒劳无功。' WHERE entry=-1600009; /* content: Surely you can see the futility of it all! */
UPDATE script_texts SET content_loc4='放弃吧，老老实实地受死吧。' WHERE entry=-1600010; /* content: Just give up and die already! */
UPDATE script_texts SET content_loc4='%s呼喊着寻求帮助。' WHERE entry=-1600011; /* content: %s calls for assistance. */
