UPDATE script_texts SET content_loc4='如果我的分析结果表明确实存在系统级的污染，对这颗行星进行结构重组无疑是最妥当的方案。不要插手。' WHERE entry=-1603108; /* content: It is in the universe\'s best interest to re-originate this planet should my analysis find systemic corruption. Do not interfere. */
UPDATE script_texts SET content_loc4='我必须这么做。' WHERE entry=-1603112; /* content: I do what I must. */
UPDATE script_texts SET content_loc4='看吧，这创世的神器！' WHERE entry=-1603115; /* content: Behold the tools of creation! */
