UPDATE `db_script_string` SET `content_loc2`='Tout de suite, monsieur.' WHERE `entry`=2000005316;
UPDATE `db_script_string` SET `content_loc2`='Voici votre pain, $Gmonsieur:madame;.' WHERE `entry`=2000005317;
UPDATE `db_script_string` SET `content_loc2`='Aaahh... Très bien. Dehors, maintenant !' WHERE `entry`=2000005318;
UPDATE `db_script_string` SET `content_loc2`='Comme vous voudrez, monsieur.' WHERE `entry`=2000005319;
UPDATE `db_script_string` SET `content_loc2`='Est-ce que je peux aller chercher plus de cailloux pour mon collier, Papa ?' WHERE `entry`=2000005320;
UPDATE `db_script_string` SET `content_loc2`='Oui tu peux, Okla. Mais ne t\'éloigne pas trop de la maison.' WHERE `entry`=2000005321;
UPDATE `db_script_string` SET `content_loc2`='Dabu ! Je ferai attention, papa !' WHERE `entry`=2000005322;
UPDATE `db_script_string` SET `content_loc2`='Ooohh... Ce caillou est joli et tout coloré. Voyons si je peux en trouver d\'autres...' WHERE `entry`=2000005323;
UPDATE `db_script_string` SET `content_loc2`='Hmm... Ce caillou est joli, mais trop petit.' WHERE `entry`=2000005324;
UPDATE `db_script_string` SET `content_loc2`='C\'est un rocher brillant. Je vais le mettre dans mon seau. Ok, j\'ai juste besoin de quelques autres …' WHERE `entry`=2000005325;
UPDATE `db_script_string` SET `content_loc2`='Je parie que mon ami Gruk voudrait ce caillou pour la lance qu\'il fabrique ! Il est très coupant... J\'espère qu\'il lui plaira.' WHERE `entry`=2000005326;
UPDATE `db_script_string` SET `content_loc2`='Voilà... Ça devrait être assez pour faire un collier. C\'est sûrement l\'heure de rentrer à la maison, de toute façon.' WHERE `entry`=2000005327;
UPDATE `db_script_string` SET `content_loc2`='Je t\'ai apporté un cadeau pour ta pendaison de crémaillère, Jarl.' WHERE `entry`=2000005328;
UPDATE `db_script_string` SET `content_loc2`='Allez, ouvre-le, je suis sûr que ça va te plaire.' WHERE `entry`=2000005329;
UPDATE `db_script_string` SET `content_loc2`='Jarl ?' WHERE `entry`=2000005330;
UPDATE `db_script_string` SET `content_loc2`='J\'en veux pas. Sauf si ça se mange.' WHERE `entry`=2000005331;
UPDATE `db_script_string` SET `content_loc2`='Jarl, qu\'est-ce qu\'il y a ? Tu n\'es plus toi-même depuis mon arrivée.' WHERE `entry`=2000005332;
UPDATE `db_script_string` SET `content_loc2`='Y a rien. Tu veux m\'aider à préparer du brouet de cuisses de grenouilles ?' WHERE `entry`=2000005333;
UPDATE `db_script_string` SET `content_loc2`='Jarl, je suis venu te rendre visite ! Et je te trouve dans une cabane vide, au milieu de nulle part, à bredouiller des trucs à propos de grenouilles ? Tu as un problème !' WHERE `entry`=2000005334;
UPDATE `db_script_string` SET `content_loc2`='Je suis déterminée à en apprendre davantage… Alors, si vous avez encore besoin de mon aide, vous saurez où me trouver.' WHERE `entry`=2000005335;
UPDATE `db_script_string` SET `content_loc2`='Anaya...? Mes yeux me jouent-ils des tours ? Est-ce vraiment vous ?' WHERE `entry`=2000005336;
UPDATE `db_script_string` SET `content_loc2`='Les temps ont été cruels envers vous et moi, mon amour, mais je peux vous l\'assurer, et nous sommes enfin réunis.' WHERE `entry`=2000005337;
UPDATE `db_script_string` SET `content_loc2`='Faut-il que le destin soit si cruel qu\'il ne nous permette que cela après une séparation de mille ans...' WHERE `entry`=2000005338;
UPDATE `db_script_string` SET `content_loc2`='Me haïssez-vous, mon amour ? Car j\'ai été forcé de détruire votre enveloppe vivante afin que votre esprit soit libéré d\'une triste servitude.' WHERE `entry`=2000005339;
UPDATE `db_script_string` SET `content_loc2`='Ne laissez pas votre cœur se troubler, bien-aimé. Vous m\'avez libérée de l\'esclavage et pour cette raison, je vous aime d\'autant plus.' WHERE `entry`=2000005340;
UPDATE `db_script_string` SET `content_loc2`='Malheureusement, même cela doit être abrégé... Les liens qui me rattachent à ce monde faiblissent et m\'éloignent...' WHERE `entry`=2000005341;
UPDATE `db_script_string` SET `content_loc2`='Non ! Anaya... Anaya ! Ne me quittez pas ! Je vous en prie...' WHERE `entry`=2000005342;
UPDATE `db_script_string` SET `content_loc2`='Adieu, Cerellean, jusqu\'à ce que nous soyons à nouveau réunis...' WHERE `entry`=2000005343;
UPDATE `db_script_string` SET `content_loc2`='Comment, mon amour ? Comment trouverai-je la force de faire face à ce monde vieillissant sans vous avoir à mes côtés...' WHERE `entry`=2000005345;
UPDATE `db_script_string` SET `content_loc2`='Merci, $N. Et bonne chance à vous.' WHERE `entry`=2000005346;
UPDATE `db_script_string` SET `content_loc2`='Les chutes de la Bondissante longent les montagnes à l\'est et au nord.' WHERE `entry`=2000005347;
UPDATE `db_script_string` SET `content_loc2`='Qu\'est-ce que ça signifie, Mor\'ghor ?' WHERE `entry`=2000005348;
UPDATE `db_script_string` SET `content_loc2`='Silence !' WHERE `entry`=2000005349;
UPDATE `db_script_string` SET `content_loc2`='Imbécile abscons. Pauvre bouffon incroyablement incompétent...' WHERE `entry`=2000005350;
UPDATE `db_script_string` SET `content_loc2`='C\'est ÇA, votre héros ?' WHERE `entry`=2000005351;
UPDATE `db_script_string` SET `content_loc2`='Imbécile, on vous a trompé.' WHERE `entry`=2000005352;
UPDATE `db_script_string` SET `content_loc2`='Toute... cette... opération... EST COMPROMISE !' WHERE `entry`=2000005353;
UPDATE `db_script_string` SET `content_loc2`='J\'attends les morceaux de la carcasse de cet insecte à mon repaire dans l\'heure. Échouez, et vous subirez un sort bien pire que la mort.' WHERE `entry`=2000005354;
UPDATE `db_script_string` SET `content_loc2`='%s commence à manipuler la Pierre de Relu au-dessus des deux fossiles.' WHERE `entry`=2000005356;
UPDATE `db_script_string` SET `content_loc2`='Qui a invoqué Aman ?' WHERE `entry`=2000005357;
UPDATE `db_script_string` SET `content_loc2`='Ah, je vois que vous avez du mal avec les reliques du passé.' WHERE `entry`=2000005358;
UPDATE `db_script_string` SET `content_loc2`='Prenez garde, même vos créateurs sont faillibles.' WHERE `entry`=2000005359;
UPDATE `db_script_string` SET `content_loc2`='À fouiller trop loin dans votre passé, vous risquez de voir votre avenir prendre fin d\'une manière abrupte.' WHERE `entry`=2000005360;
UPDATE `db_script_string` SET `content_loc2`='Salutations, Gezhe ! Dix-sept commandes supplémentaires ont été émises dans le secteur Hara\'samid. Le marché de la veldarite lourde s\'est quasiment effondré, comme vous l\'aviez prédit.' WHERE `entry`=2000005362;
UPDATE `db_script_string` SET `content_loc2`='Les exportations d\'Heraazi sont en augmentation, maître. Nous avons d\'ores et déjà soudoyé les officiers taxateurs, et les marchandises de contrebande commenceront à arriver dans quelques jours.' WHERE `entry`=2000005363;
UPDATE `db_script_string` SET `content_loc2`='Cela n\'a rien de très surprenant. Revenez quand vous aurez de vraies nouvelles, merci.' WHERE `entry`=2000005364;
UPDATE `db_script_string` SET `content_loc2`='Comme prévu. Très bien, disposez.' WHERE `entry`=2000005365;
UPDATE `db_script_string` SET `content_loc2`='Avec votre permission, maître…' WHERE `entry`=2000005366;
UPDATE `db_script_string` SET `content_loc2`='Si vous êtes ici, cela signifie que vous êtes $Gprêt:prête; à entamer une étude sur l\'invocation des cohortes démoniaques en vue de réaliser votre union. Nous commencerons par les créatures les plus humbles que vous serez en mesure d\'invoquer puis nous continuerons. Commençons.' WHERE `entry`=2000005367;
UPDATE `db_script_string` SET `content_loc2`='La créature que vous pourrez invoquer sans aucune difficulté est le diablotin. Vous devriez déjà être en mesure de créer ce serviteur, mais afin de vous délivrer un enseignement complet je vais commencer par lui.' WHERE `entry`=2000005368;
UPDATE `db_script_string` SET `content_loc2`='Cette ignoble petite créature est le diablotin. Il est petit et faible, et donc quasiment inutile en tant que bouclier de chair, et sa capacité à infliger des dégâts est au mieux médiocre. Il vaut mieux utiliser cette créature pour soutenir un groupe assez grand.' WHERE `entry`=2000005369;
UPDATE `db_script_string` SET `content_loc2`='Maintenant que vous avez eu la chance d\'étudier un diablotin, passons au prochain serviteur que vous serez en mesure d\'invoquer, le marcheur du Vide.' WHERE `entry`=2000005370;
UPDATE `db_script_string` SET `content_loc2`='Cette entité démoniaque est connue sous le nom de "marcheur du Vide". Sa force et son endurance, qui ne sont pas à négliger, sont parfaitement adaptées à la défense. Envoyez-la attaquer vos ennemis, puis utilisez-la comme un bouclier pendant que vous employez vos sorts et vos techniques pour drainer la vie hors du corps de votre adversaire.' WHERE `entry`=2000005371;
UPDATE `db_script_string` SET `content_loc2`='Si vous n\'en avez jamais vu, c\'est un spectacle à voir. Une créature très impressionnante, à la fois sur le champ de bataille et en dehors. À présent, jetons un œil à celle que tous les étudiants mâles attendent. La succube.' WHERE `entry`=2000005372;
UPDATE `db_script_string` SET `content_loc2`='Voyons. Mis à part les diversions évidentes qu\'une telle servante occasionnera envers vos ennemis mâles, elle est également capable de causer des dommages d\'une ampleur impressionnante. Cependant, sa faible résistance la rend quasiment inutile en tant que bouclier.' WHERE `entry`=2000005373;
UPDATE `db_script_string` SET `content_loc2`='Étudiez assidûment, et un jour vous pourriez être capable d\'en invoquer une par vous-même. Mais pour le moment, passons au chasseur corrompu.' WHERE `entry`=2000005374;
UPDATE `db_script_string` SET `content_loc2`='Ce que vous voyez devant vous est un chasseur corrompu. Les talents naturels de cette créature lui permettent de neutraliser des sorts. Ce talent, ainsi que d\'autres capacités, la rendent sans égale face à un adversaires doté de pouvoirs magiques.' WHERE `entry`=2000005375;
UPDATE `db_script_string` SET `content_loc2`='Lorsque vous êtes face à un lanceur de sorts quelconque, cette bête sauvage est votre meilleur ami. Maintenant, intéressons-nous à quelque chose d\'un peu différent. La créature suivante vous aidera à voyager et facilitera grandement vos futurs déplacements. Jetons un coup d\'œil sur un palefroi corrompu.' WHERE `entry`=2000005376;
UPDATE `db_script_string` SET `content_loc2`='Je ne pense pas que vous ayez eu de nombreuses occasions de voir une telle créature. Ces chevaux-démons rendront vos voyages beaucoup plus rapides si vous les utilisez comme montures. Du moins tant que vous pourrez les contrôler, ce qui n\'est pas facile. Avant de faire une tentative, soyez sûrs d\'être prêts.' WHERE `entry`=2000005377;
UPDATE `db_script_string` SET `content_loc2`='Voilà. Votre leçon sur l\'invocation est terminée. Un nouveau cours est sur le point de commencer, alors si vous souhaitez réviser, n\'hésitez pas à rester.' WHERE `entry`=2000005378;
UPDATE `db_script_string` SET `content_loc2`='Ouf ! Mon chapeau porte-bonheur est là. Je l\'ai cherché partout.' WHERE `entry`=2000005379;
UPDATE `db_script_string` SET `content_loc2`='Je ne sais pas ce qui m\'a pris de sortir sans mon épée. Je l\'aurais emportée si je l\'avais vue ici…' WHERE `entry`=2000005380;
UPDATE `db_script_string` SET `content_loc2`='Il n\'est pas concevable que je sorte sans ma cape. J\'espère qu\'elle est ici…' WHERE `entry`=2000005381;
UPDATE `db_script_string` SET `content_loc2`='La voilà ! J\'aurais pourtant juré qu\'elle n\'y était pas la dernière fois que j\'ai regardé…' WHERE `entry`=2000005382;
UPDATE `db_script_string` SET `content_loc2`='Je n\'arrive pas à m\'endormir si on ne me raconte pas une belle histoire. Maintenant, je suis sûre de bien me reposer.' WHERE `entry`=2000005383;
UPDATE `db_script_string` SET `content_loc2`='Quel est ce bruit ? On aurait dit un mugissement.' WHERE `entry`=2000005384;
UPDATE `db_script_string` SET `content_loc2`='Grimclaw rugit sur Terenthis pour attirer son attention.' WHERE `entry`=2000005385;
UPDATE `db_script_string` SET `content_loc2`='Griffe-sinistre ?! Doucement mon ami... Où est ton maître Volcor ?' WHERE `entry`=2000005386;
UPDATE `db_script_string` SET `content_loc2`='Grimclaw commence à gémir et à rugir à Terenthis tout en écrasant ses pattes sur le sol.' WHERE `entry`=2000005387;
UPDATE `db_script_string` SET `content_loc2`='Doucement, doucement mon ami. Un moment...' WHERE `entry`=2000005388;
UPDATE `db_script_string` SET `content_loc2`='Terenthis commence à jeter un sort sur Grimclaw.' WHERE `entry`=2000005389;
UPDATE `db_script_string` SET `content_loc2`='Là... Ça devrait vous faire du bien. Maintenant, racontez-moi ce qui s\'est passé, Griffe-sinistre.' WHERE `entry`=2000005390;
UPDATE `db_script_string` SET `content_loc2`='Grimclaw rugit davantage à Terenthis, mais cette fois le druide semble comprendre l\'ours.' WHERE `entry`=2000005391;
UPDATE `db_script_string` SET `content_loc2`='Je comprends, mon ami. Je trouverai quelqu\'un pour venir en aide à votre maître. Retournez auprès de lui maintenant, ou tout du moins, restez à proximité.' WHERE `entry`=2000005392;
UPDATE `db_script_string` SET `content_loc2`='Grimclaw rugit en reconnaissance à Terenthis.' WHERE `entry`=2000005393;
UPDATE `db_script_string` SET `content_loc2`='Si vous avez le temps, vous pourriez donner un coup de main à Griffe-sinistre et son maître Volcor. Si cela vous intéresse, faites-le-moi savoir...' WHERE `entry`=2000005394;
UPDATE `db_script_string` SET `content_loc2`='J\'en ai assez de vos pleurnicheries incessantes, capitaine. Vous êtes capitaine de quoi, au fait ? Franchement, quel culot !' WHERE `entry`=2000005395;
UPDATE `db_script_string` SET `content_loc2`='Et si vous preniez un marteau et commenciez à taper sur ces structures au lieu de nous demander si c\'est fait toutes les 5 minutes ?' WHERE `entry`=2000005396;
UPDATE `db_script_string` SET `content_loc2`='%s hausse les épaules.' WHERE `entry`=2000005397;
UPDATE `db_script_string` SET `content_loc2`='D\'accord, retour à la planche à dessin.' WHERE `entry`=2000005398;
UPDATE `db_script_string` SET `content_loc2`='Excusez-moi un moment, je commence mes expériences…' WHERE `entry`=2000005400;
UPDATE `db_script_string` SET `content_loc2`='Intéressant…' WHERE `entry`=2000005401;
UPDATE `db_script_string` SET `content_loc2`='Mais qu\'est-ce que c\'est ?' WHERE `entry`=2000005402;
UPDATE `db_script_string` SET `content_loc2`='Oh là là !' WHERE `entry`=2000005403;
UPDATE `db_script_string` SET `content_loc2`='Si mon sang ne s\'était figé il y a bien longtemps, il serait en train de bouillir de rage…' WHERE `entry`=2000005404;
UPDATE `db_script_string` SET `content_loc2`='Maçons... euuhhh... Défias, soyez sur vos gardes : ce soir, l\'ancre rouillée coulera.' WHERE `entry`=2000005407;
UPDATE `db_script_string` SET `content_loc2`='Le patron veut que toutes les mains soient en état d\'alerte. L\'ancre rouillée coule ce soir.' WHERE `entry`=2000005408;
UPDATE `db_script_string` SET `content_loc2`='Le prêtre s\'agenouille et prie silencieusement.' WHERE `entry`=2000005409;
UPDATE `db_script_string` SET `content_loc2`='Lumière, donne-moi la force.' WHERE `entry`=2000005410;
UPDATE `db_script_string` SET `content_loc2`='Lumière, conduis les défunts de l\'illusion vers la vérité, de l\'ombre vers la Lumière et de la mort vers l\'immortalité.' WHERE `entry`=2000005411;
UPDATE `db_script_string` SET `content_loc2`='Nooooon !!' WHERE `entry`=2000005415;
UPDATE `db_script_string` SET `content_loc2`='Mes amis… Je vous ai laissé tomber…' WHERE `entry`=2000005416;
UPDATE `db_script_string` SET `content_loc2`='Non, mon ami… c\'est nous… c\'est nous qui vous laissons tomber.' WHERE `entry`=2000005417;
UPDATE `db_script_string` SET `content_loc2`='Tout est prêt, porteguerre.' WHERE `entry`=2000005418;
UPDATE `db_script_string` SET `content_loc2`='Le seigneur funeste Kazzak sera content. Il faut augmenter la cadence de vos attaques. Détruisez les bastions orcs et nains au plus vite.' WHERE `entry`=2000005419;
UPDATE `db_script_string` SET `content_loc2`='Porteguerre, cela impliquera de faire donner tous les infernaux du bastion. Cela pourrait nous rendre vulnérables à une contre-attaque.' WHERE `entry`=2000005420;
UPDATE `db_script_string` SET `content_loc2`='Ne vous inquiétez pas. J\'ai augmenté le rythme de production à la Forgemort. Vous aurez tous les infernaux qu\'il vous faudra pour exécuter vos ordres. N\'échouez pas, Jovaan.' WHERE `entry`=2000005421;
UPDATE `db_script_string` SET `content_loc2`='Il sera fait selon vos ordres, porteguerre. Une dernière question, si je puis…' WHERE `entry`=2000005422;
UPDATE `db_script_string` SET `content_loc2`='Oui ?' WHERE `entry`=2000005423;
UPDATE `db_script_string` SET `content_loc2`='Y a quoi dans la caisse ?' WHERE `entry`=2000005424;
UPDATE `db_script_string` SET `content_loc2`='La caisse ? Je n\'ai pas envoyé de caisse, Jovaan. N\'avez-vous rien de plus important à faire ? Allez vous en occuper !' WHERE `entry`=2000005425;
UPDATE `db_script_string` SET `content_loc2`='Je vais commencer à ne plus demander gentiment, homme-poisson.' WHERE `entry`=2000005426;
UPDATE `db_script_string` SET `content_loc2`='Où est la Formule de damnation ? Je sais qu\'une des trois parties se trouve dans ce village.' WHERE `entry`=2000005427;
UPDATE `db_script_string` SET `content_loc2`='Qu\'est-sssce que... vous voulez... peau-lissse ?' WHERE `entry`=2000005428;
UPDATE `db_script_string` SET `content_loc2`='Les nagas… n\'ont pas… ssscette formule…' WHERE `entry`=2000005429;
UPDATE `db_script_string` SET `content_loc2`='Non… Bien sûr que non… Où avais-la tête ? Pourquoi auriez-vous un fragment de la Formule de damnation ?' WHERE `entry`=2000005430;
UPDATE `db_script_string` SET `content_loc2`='Je me sens tellement bête. Peut-être que je devrais vous détacher et vous laisser partir, non ?' WHERE `entry`=2000005431;
UPDATE `db_script_string` SET `content_loc2`='Ssssi… Sssssi… Laissssez-moi…' WHERE `entry`=2000005432;
UPDATE `db_script_string` SET `content_loc2`='EST-CE QUE J\'AI VRAIMENT L\'AIR SI BÊTE QUE ÇA, NAGA ? MAINTENANT TU VAS SOUFFRIR !' WHERE `entry`=2000005433;
UPDATE `db_script_string` SET `content_loc2`='%s crache sur le naga glissentaille.' WHERE `entry`=2000005434;
UPDATE `db_script_string` SET `content_loc2`='DEBOUT, SALE TAS DE FUMIER ! DEBOUT AVANT QUE JE T\'ACHÈVE !' WHERE `entry`=2000005435;
UPDATE `db_script_string` SET `content_loc2`='%s reprend conscience.' WHERE `entry`=2000005436;
UPDATE `db_script_string` SET `content_loc2`='Cesssez de frapper le commandant glissssentaille, orc ! Je vais parler ! La forrrmule… Elle est dissssimulée dans un coffre à la halte de Glisssentaille… Les nagas conssservent la clé…' WHERE `entry`=2000005438;
UPDATE `db_script_string` SET `content_loc2`='GLISSSSENTAILLE !' WHERE `entry`=2000005439;
UPDATE `db_script_string` SET `content_loc2`='Une voix profonde, à vous glacer les os, retentit en provenance de %s…' WHERE `entry`=2000005440;
UPDATE `db_script_string` SET `content_loc2`='Que voulez-vous de moi, chaman ?' WHERE `entry`=2000005443;
UPDATE `db_script_string` SET `content_loc2`='N\'avez-vous donc pas d\'yeux pour voir ? N\'avez-vous pas vu les démons qui sont à notre porte ? Je n\'ai pas assez de soldats pour en détacher ailleurs !' WHERE `entry`=2000005444;
UPDATE `db_script_string` SET `content_loc2`='Priez vos dieux, chaman. Peut-être vous viendront-ils en aide. La Horde, elle, ne le peut pas…' WHERE `entry`=2000005445;
UPDATE `db_script_string` SET `content_loc2`='Silence ! La rupture est sur le point d\'avoir lieu !' WHERE `entry`=2000005446;
UPDATE `db_script_string` SET `content_loc2`='Soyez les témoins de la puissance implacable de notre sombre maître !' WHERE `entry`=2000005447;
UPDATE `db_script_string` SET `content_loc2`='Grâce à ce don, je vais raser cette terre et la reconstruire !' WHERE `entry`=2000005448;
UPDATE `db_script_string` SET `content_loc2`='Regardez ! Voyez la terre se rompre devant nous ! Voyez l\'énergie affluer ! Elle nourrira nos armées…' WHERE `entry`=2000005449;
UPDATE `db_script_string` SET `content_loc2`='Jamais la puissance ne nous manquera ! Notre avenir est assuré ! Soyez éblouis par ma gloire !' WHERE `entry`=2000005450;
UPDATE `db_script_string` SET `content_loc2`='C\'est… maintenant… fait…' WHERE `entry`=2000005451;
UPDATE `db_script_string` SET `content_loc2`='Déchirez cette terre, Gul\'dan ! Rompez le lien !' WHERE `entry`=2000005452;
UPDATE `db_script_string` SET `content_loc2`='Ne faites pas ça, Gul\'dan ! Les éléments ne nous le pardonneront jamais !' WHERE `entry`=2000005453;
UPDATE `db_script_string` SET `content_loc2`='Ils nous ont abandonnés ! Ne l\'oubliez jamais !' WHERE `entry`=2000005455;
UPDATE `db_script_string` SET `content_loc2`='Nous prendrons à la terre ce qu\'elle refuse de nous donner !' WHERE `entry`=2000005456;
UPDATE `db_script_string` SET `content_loc2`='Stop ! Arrêtez, il le faut !' WHERE `entry`=2000005457;
UPDATE `db_script_string` SET `content_loc2`='Vous nous avez damnés… nous sommes perdus.' WHERE `entry`=2000005458;
UPDATE `db_script_string` SET `content_loc2`='Si vous voulez la clé, il va falloir m\'attraper !' WHERE `entry`=2000005460;
UPDATE `db_script_string` SET `content_loc2`='Poursuivez-moi si vous l\'osez ! Je m\'enfuis sans hésiter !' WHERE `entry`=2000005461;
UPDATE `db_script_string` SET `content_loc2`='Pourquoi voudriez-vous me faire souffrir ? Venez. L\'amitié peut nous réunir !' WHERE `entry`=2000005462;
UPDATE `db_script_string` SET `content_loc2`='MEURS ? Vous faites pleurer Pusillin !' WHERE `entry`=2000005463;
UPDATE `db_script_string` SET `content_loc2`='Dites bonjour à mes petits amis !' WHERE `entry`=2000005464;
UPDATE `db_script_string` SET `content_loc2`='À vos ordres, chef !' WHERE `entry`=2000005465;
UPDATE `db_script_string` SET `content_loc2`='Allie la force des géants d\'Ombrelune à la nôtre, Corok. Le maître est un chef juste. Il s\'assurera de bien prendre soin de ceux qui ont aidé à la sujétion des dissidents de ce monde.' WHERE `entry`=2000005466;
UPDATE `db_script_string` SET `content_loc2`='Alors vous deviendrez les ennemis de mon seigneur, Illidan. Les ennemis d\'Illidan sont anéantis. Tes géants sont-ils capables de résister à une attaque des forces de la halte de l\'Éclipse ?' WHERE `entry`=2000005468;
UPDATE `db_script_string` SET `content_loc2`='Non… C\'est bien ce qu\'il me semblait…' WHERE `entry`=2000005469;
UPDATE `db_script_string` SET `content_loc2`='Nous nous retrouverons… Un jour.' WHERE `entry`=2000005471;
UPDATE `db_script_string` SET `content_loc2`='La mort incarnée est à nouveau lâchée sur le monde ! Teron Fielsang est de retour…' WHERE `entry`=2000005472;
