UPDATE `db_script_string` SET `content_loc2`='Leur malheur fait vraiment mon bonheur…' WHERE `entry`=2000005486;
UPDATE `db_script_string` SET `content_loc2`='L\'heure de frapper est venue. Supprimez Icarius.' WHERE `entry`=2000005487;
