UPDATE `db_script_string` SET `content_loc2`='Reculez, $N. Les esprits pourraient s\'en prendre à vous.' WHERE `entry`=2000005489;
UPDATE `db_script_string` SET `content_loc2`='Pourquoi nous avez-vous capturés ?' WHERE `entry`=2000005490;
UPDATE `db_script_string` SET `content_loc2`='Qu\'espérez-vous en retirer ?' WHERE `entry`=2000005491;
