UPDATE `db_script_string` SET `content_loc2`='Hé ! Arrêtez de fouiner dans ma cave ! Ouste !' WHERE `entry`=2000005580;
UPDATE `db_script_string` SET `content_loc2`='Qu\'est-ce que vous fichiez dans ma cave ? Il y a pénurie de nourriture, alors la dernière chose dont j\'ai besoin, c\'est des étrangers qui viennent fouiner dans mes provisions ! Allez ouste !' WHERE `entry`=2000005581;
UPDATE `db_script_string` SET `content_loc2`='Toute cette agitation, ça me semble légèrement paranoïaque, non ?' WHERE `entry`=2000005582;
UPDATE `db_script_string` SET `content_loc2`='Les ordres, c\'est les ordres. Si le prince dit de sauter…' WHERE `entry`=2000005583;
