UPDATE db_script_string SET content_loc2='Merci, $r. Je vais maintenant vous débarrasser de cette misérable canaille.' WHERE entry=2000000193; /* content: Thank you, $r. I will take this miserable cur from you now. */
UPDATE db_script_string SET content_loc2='Pauvre Dorius. Si jamais je mets la main sur ces Sombrefer, je vous jure que...' WHERE entry=2000000194; /* content: Poor Dorius. If I ever get my hands on those Dark Irons, so help me... */
UPDATE db_script_string SET content_loc2='Parfait. Tant que vous ne vous y frayez pas un chemin à grands coups d\'épée, ils n\'y verront que du feu.' WHERE entry=2000000194; /* content: Perfect.  As long as you don\'t go hacking and slashing your way through, they\'ll never know the difference. */
UPDATE db_script_string SET content_loc2='Il s\'enfuit !' WHERE entry=2000000195; /* content: It\'s getting away! */
UPDATE db_script_string SET content_loc2='Comme vous voulez…' WHERE entry=2000000195; /* content: As you wish... */
UPDATE db_script_string SET content_loc2='Raliq t\'apprendre leçon maintenant !' WHERE entry=2000000196; /* content: Raliq teach you lesson now! */
UPDATE db_script_string SET content_loc2='F\'woooroonn ! Coosh\'coosh rien payer ! Meurs !' WHERE entry=2000000197; /* content: F\'woooroonn! Coosh\'coosh pay nothing! Die! */
UPDATE db_script_string SET content_loc2='Je choisis la troisième option : VOUS TUER !' WHERE entry=2000000198; /* content: I choose the third option: KILLING YOU! */
UPDATE db_script_string SET content_loc2='Je suis libre ! L\'esprit des eaux va revenir auprès de mon peuple. Il nous apportera la sagesse indispensable pour survivre sur ce terrain austère. Je suis votre débiteur, $n.' WHERE entry=2000000199; /* content: I\'m free!  The spirit of water returns to my people.  It will bring us the wisdom we need to survive in this harsh land.  I am in your debt, $N. */
UPDATE db_script_string SET content_loc2='Maman ? Est-ce que je peux caresser Flopp ?' WHERE entry=2000000200; /* content: Mommy? Can I pet Fizzles? */
UPDATE db_script_string SET content_loc2='Vous m\'avez libéré ! Les vents parlent à nouveau à mon peuple et lui apportent leur force. Soyez-en $gremercié, étranger:remerciée, étrangère;.' WHERE entry=2000000200; /* content: You\'ve freed me!  The winds speak to my people once again and grant us their strength.  I thank you, stranger. */
UPDATE db_script_string SET content_loc2='Couchez-vous !' WHERE entry=2000000201; /* content: Duck! */
UPDATE db_script_string SET content_loc2='Ne vous approchez pas trop du lac, il est hanté !' WHERE entry=2000000202; /* content: Don\'t go close to the lake! It\'s haunted! */
UPDATE db_script_string SET content_loc2='Athrikus est venu nous voir. Il nous a dit qu\'il pouvait nous sauver du mal. Il nous a jeté un sort pour nous protéger du mal.' WHERE entry=2000000202; /* content: Athrikus came to us. He told us that he could save us from harm. He cast a spell upon us to protect us from harm. */
UPDATE db_script_string SET content_loc2='Lorsque les tremblements cessèrent, ses véritables motifs devinrent apparents. Nous étions pris au piège et il drainait lentement nos pouvoirs.' WHERE entry=2000000203; /* content: When the shaking stopped, his true motives were revealed. We were trapped, and he was slowly draining our powers. */
UPDATE db_script_string SET content_loc2='Ils étaient des centaines emprisonnés par ce sort. Il n\'en reste plus que quelques-uns dans leurs prisons. Il nous parlera bien un jour.' WHERE entry=2000000204; /* content: There were hundreds imprisoned by his spell. Now only a few remain in their prisons. He would speak to us sometimes. */
UPDATE db_script_string SET content_loc2='Par le grand Gazleu !' WHERE entry=2000000205; /* content: Great Gazlowe! */
UPDATE db_script_string SET content_loc2='Il avait peur que son pouvoir ne diminue et que bientôt même nous, les dernières de ses précieuses gemmes d\'âme, ne nous affaiblissions et mourions.' WHERE entry=2000000205; /* content: He worried that his power weakened, that soon even we last of his precious soulgems would fade and die. */
UPDATE db_script_string SET content_loc2='J\'ai un autre relevé du néant.' WHERE entry=2000000206; /* content: I have another reading from the nether. */
UPDATE db_script_string SET content_loc2='%s renifle l\'élixir puis l\'avale goulûment !' WHERE entry=2000000207; /* content: %s sniffs the elixir then eagerly digs in! */
UPDATE db_script_string SET content_loc2='Cela fait longtemps, Bethor, mon ami.' WHERE entry=2000000208; /* content: It has been a long time, Bethor, my friend. */
UPDATE db_script_string SET content_loc2='Quand nous en aurons le temps, il faudra que nous parlions longuement. Car nous avons beaucoup à nous dire.' WHERE entry=2000000209; /* content: When time permits, we must speak at length.  For we have much to discuss. */
UPDATE db_script_string SET content_loc2='Et merci, $n. Sans votre aide, je n\'aurais peut-être jamais trouvé le chemin qui mène aux Réprouvés.' WHERE entry=2000000210; /* content: And thank you, $N.  Without your aid I may never have found my way to the Forsaken. */
UPDATE db_script_string SET content_loc2='Je suis partante pour aller en chasse après ce tour de garde.' WHERE entry=2000000210; /* content: I\'m up to looking into that bounty after this shift. */
UPDATE db_script_string SET content_loc2='Adieu, mon ami.' WHERE entry=2000000211; /* content: Farewell, my friend. */
UPDATE db_script_string SET content_loc2='Soyez le premier à profiter de l\'incroyable puissance du Démaculoblaster électrostatique et rendez tous vos voisins jaloux !' WHERE entry=2000000212; /* content: Be the first on your block to own the incredible Electrostatic Maculation Blaster! */
UPDATE db_script_string SET content_loc2='%s convoque un messager…' WHERE entry=2000000212; /* content: %s summons a messenger... */
UPDATE db_script_string SET content_loc2='%s remet un message à l\'apothicaire Zamah.' WHERE entry=2000000214; /* content: %s gives message to Apothecary Zamah. */
UPDATE db_script_string SET content_loc2='Taches de vin, taches d\'huile, et même les taches de fond, aucune tache ne résiste au Démaculoblaster électrostatique !' WHERE entry=2000000214; /* content: Wine stains, grass stains, even pet stains are no match for the Electrostatic Maculation Blaster! */
UPDATE db_script_string SET content_loc2='N\'attendez plus ! Commandez dès à présent et vous recevrez en plus gratuitement ce superbe Moule à gaufres ultrakinétique !' WHERE entry=2000000215; /* content: Don\'t wait! Order now and receive my Ultra-Kinetic Waffle Iron at no additional charge! */
UPDATE db_script_string SET content_loc2='Ah, oui. Je connais bien Loken.' WHERE entry=2000000216; /* content: Ah, yes. Loken is well known to me. */
UPDATE db_script_string SET content_loc2='Ça avance, Grillegaufre. Pourquoi n\'iriez-vous pas voir ailleurs s\'il n\'y a pas une présentation de produit à faire ?' WHERE entry=2000000216; /* content: It\'s coming along, Wafflefry. Haven\'t you got a product demonstration to do or something? */
UPDATE db_script_string SET content_loc2='C\'est lui qui commande les nains de fer dans leur guerre contre nous.' WHERE entry=2000000217; /* content: It is he who commands the sons of iron in their war against us. */
UPDATE db_script_string SET content_loc2='Depuis son repaire secret, il supervise les préparatifs de la guerre. Ils veulent exterminer les géants de pierre !' WHERE entry=2000000218; /* content: From his hiding place, he oversees their preparations for war with the goal of exterminating the stone giants! */
UPDATE db_script_string SET content_loc2='Moi aussi il me pourchasse ! Je resterais bien sur mes gardes, si j\'étais vous, $gétranger:étrangère;. On ne sait jamais derrière quel arbre il peut se cacher !' WHERE entry=2000000219; /* content: He\'s out to get me, too! I\'d be careful around here if I was you, traveler. You never know which tree he\'s hiding behind! */
UPDATE db_script_string SET content_loc2='C\'est pas quelque chose que demanderait Joe le Boiteux. Mais, ouais, je connais Loken.' WHERE entry=2000000220; /* content: That\'s not something Limpy Joe would ask! But yeah, I know Loken. */
UPDATE db_script_string SET content_loc2='Il se pourrait que je décide d\'aller chasser ce Déshérité bestial, maintenant que sa tête est mise à prix.' WHERE entry=2000000220; /* content: I might just have to go hunting for that Wretched beast now that there\'s a bounty on his head. */
UPDATE db_script_string SET content_loc2='Là-haut, sur la colline, $n.' WHERE entry=2000000222; /* content: Up here on the hill, $N. */
UPDATE db_script_string SET content_loc2='%s revêt ses habits en fourrure de loup.' WHERE entry=2000000223; /* content: %s dons his wolf-fur vestments. */
UPDATE db_script_string SET content_loc2='%s hurle en réponse à l\'appel de Malukaz.' WHERE entry=2000000224; /* content: %s howls in response to Malukaz\'s call. */
UPDATE db_script_string SET content_loc2='%s manifeste son accord.' WHERE entry=2000000225; /* content: %s expresses its approval. */
UPDATE db_script_string SET content_loc2='Que notre lien avec vous ne soit plus jamais rompu, esprit.' WHERE entry=2000000226; /* content: May our bond with you never be broken again, spirit. */
UPDATE db_script_string SET content_loc2='Nous avons réussi ! Thrall serait fier de nous.' WHERE entry=2000000227; /* content: We have done it! Thrall would be proud. */
UPDATE db_script_string SET content_loc2='Urtrak tuer vous !' WHERE entry=2000000228; /* content: Urtrak kill you! */
UPDATE db_script_string SET content_loc2='La vengeance est nôtre ! Attaquez, mes frères !' WHERE entry=2000000229; /* content: Vengeance is ours!  Attack my brothers! */
UPDATE db_script_string SET content_loc2='Le mélange raté se renverse et semble prendre vie !' WHERE entry=2000000230; /* content: The failed concoction spills on the floor and appears to come to life! */
UPDATE db_script_string SET content_loc2='Hé, que quelqu\'un m\'aide à rattraper cette chose !' WHERE entry=2000000231; /* content: Hey, someone help me catch this thing! */
UPDATE db_script_string SET content_loc2='Il est vivant !' WHERE entry=2000000231; /* content: It\'s alive! */
UPDATE db_script_string SET content_loc2='Est-ce que c\'est VOUS qui avez interrompu mon dîner ? Alors, est-ce vous ?' WHERE entry=2000000235; /* content: Are YOU the one who interrupted my dinner? Well, are you? */
UPDATE db_script_string SET content_loc2='Vous êtes bien trop laide et squelettique pour être une femelle yéti ! Et maintenant, vous m\'énervez vraiment…' WHERE entry=2000000236; /* content: You\'re too ugly and scrawny to be a female yeti! And now you\'re making me mad... */
UPDATE db_script_string SET content_loc2='Merci encore, $n. Maintenant je vais attendre ici jusqu\'à ce qu\'il n\'y ait plus de danger.' WHERE entry=2000000237; /* content: Thank again, $n.  Now I\'ll just wait here until it\'s safe to leave. */
UPDATE db_script_string SET content_loc2='Ils rangent leurs outils dans des boîtes qui ressemblent à celle-ci. Vous les trouverez près des pavillons.' WHERE entry=2000000238; /* content: They keep their tools in boxes that look like this. Look for them near the pavilions. */
UPDATE db_script_string SET content_loc2='Je ne vais graver que les runes qui, à mon avis, vous donneront le contrôle d\'un géant.' WHERE entry=2000000239; /* content: I\'m only going to carve the runes I think will give you command over a giant. */
UPDATE db_script_string SET content_loc2='Plus vous attendrez, moins l\'avenir que je pourrai vous prédire sera long.' WHERE entry=2000000240; /* content: The longer you wait, the less future you have for me to foretell. */
UPDATE db_script_string SET content_loc2='Voilà. La rune de commandement. Enfin j\'espère. Loin d\'être aussi funeste que sa cousine des nains de fer, mais efficace.' WHERE entry=2000000241; /* content: There we go, the Rune of Command... hopefully. Not nearly as vicious as its Iron Dwarf cousin, but functional. */
UPDATE db_script_string SET content_loc2='Debout, les damnés, de la terre !' WHERE entry=2000000242; /* content: Arise from the earth, my brothers! */
UPDATE db_script_string SET content_loc2='Nos maîtres de fer ont une mission pour nous !' WHERE entry=2000000243; /* content: Our iron masters have a mission for us! */
UPDATE db_script_string SET content_loc2='Suivez-moi vers les montagnes, allons mettre à exécution le plan de nos maîtres !' WHERE entry=2000000244; /* content: Follow me into the mountains to carry out the plan of the masters! */
UPDATE db_script_string SET content_loc2='Nous ne connaîtrons pas l\'échec !' WHERE entry=2000000245; /* content: We will not fail! */
UPDATE db_script_string SET content_loc2='Non ! Je vous en supplie ! Ne me tuez pas !' WHERE entry=2000000246; /* content: No! I beg you! Please don\'t kill me! */
UPDATE db_script_string SET content_loc2='Comment ça, mon heure est venue ? Je vais vous tuer sur-le-champ !' WHERE entry=2000000247; /* content: What do you mean my time has come?! I\'ll kill you where you stand! */
UPDATE db_script_string SET content_loc2='Le Maître a dû consentir à de très gros efforts pour obtenir ces informations sur l\'emplacement de l\'Exodar. Tu as failli compromettre toute l\'opération, y compris la couverture de notre espion de l\'intérieur.' WHERE entry=2000000248; /* content: The Master has gone to great lengths to secure information about the whereabouts of the Exodar. You could have blown the entire operation, including the cover of our spy on the inside. */
UPDATE db_script_string SET content_loc2='Qu\'est-ce que ça signifie ? Stop ! Je ne sais pas combien on vous donne, mais je vous en offre le double !' WHERE entry=2000000248; /* content: What is the meaning of this?! Stop! I\'ll double whatever they\'re paying you! */
UPDATE db_script_string SET content_loc2='Vous ne voulez pas dire que… Vous allez voir, je vais vous coucher par écrit, $c !' WHERE entry=2000000249; /* content: You can\'t possibly mean to.... I\'ll write you up for this, $c! */
UPDATE db_script_string SET content_loc2='Vous avez raison, Eitrigg. Le moment est mal choisi. Assurez-vous que nos meilleurs ambassadeurs soient choisis pour nous représenter auprès de Hurlenfer.' WHERE entry=2000000250; /* content: You are right, Eitrigg.  The timing is unfavorable.  Make sure our best ambassadors are sent as envoys to Hellscream. */
UPDATE db_script_string SET content_loc2='S\'il est un tant soit peu comme son père, une alliance avec les Mag\'har marquera l\'aube d\'une nouvelle ère pour la Horde.' WHERE entry=2000000252; /* content: If he\'s anything like his father, allying with the Mag\'har will mark the dawn of a new age for the Horde. */
UPDATE db_script_string SET content_loc2='… une décoction puissante !' WHERE entry=2000000254; /* content: ...a potent concoction! */
UPDATE db_script_string SET content_loc2='Où diable pourrais-je trouver un cobaye adéquat ?' WHERE entry=2000000255; /* content: But where could I ever find a suitable test subject? */
UPDATE db_script_string SET content_loc2='Hummm... On dirait bien que je vais avoir besoin d\'un nouveau cobaye.' WHERE entry=2000000258; /* content: Hmmm... looks like I\'m going to need a new test subject. */
UPDATE db_script_string SET content_loc2='C\'était bien, $n. Poursuivez votre quête de savoir et souvenez-vous de l\'histoire que vous avez apprise.' WHERE entry=2000000259; /* content: You have done well, $N. Continue your search for knowledge, and remember the history you have learned. */
UPDATE db_script_string SET content_loc2='Terenthis, Raene m\'envoie pour te trouver. Les Sentinelles ont besoin d\'aide en Orneval.' WHERE entry=2000000260; /* content: Terenthis, Raene sent me to find you. The Sentinels are in need of help in Ashenvale. */
UPDATE db_script_string SET content_loc2='Je n\'ai peur de rien ! Viens donc !' WHERE entry=2000000261; /* content: I\'m not afraid of anything -- bring it on! */
UPDATE db_script_string SET content_loc2='Ça ne va pas du tout, $n. Dans l\'histoire que m\'a racontée la dompteuse des vents Barah, Sabot-tempête était un héros, pas un méchant.' WHERE entry=2000000262; /* content: This isn\'t right at all, $N. In the tale Wind Tamer Barah told me, Stormhoof was a hero, not a villain. */
UPDATE db_script_string SET content_loc2='Le Vent du Nord est un ancien ennemi des taunkas, un dirigeant des éléments, et la force même qui saigne la vie de ces peuples anciens.' WHERE entry=2000000263; /* content: The North Wind is the ancient enemy of the taunka, a ruler among the elements, and the very force which bleeds the life from these ancient people. */
UPDATE db_script_string SET content_loc2='Ils préféreraient mourir plutôt que se soumettre au Vent du Nord. Quelque chose ne va pas.' WHERE entry=2000000264; /* content: They would rather die than bow before the North Wind. Something is amiss here. */
UPDATE db_script_string SET content_loc2='Le marteau de la connaissance…' WHERE entry=2000000265; /* content: The Lorehammer... */
UPDATE db_script_string SET content_loc2='Ceci confirme nos soupçons. Les fils du Temps ont été coupés et retissés en suivant un modèle dénaturé. Mais comment ?' WHERE entry=2000000266; /* content: It confirms our suspicions. The threads of time have been severed and rewoven in the wrong pattern. But, how? */
UPDATE db_script_string SET content_loc2='Mon frère, Sabot-tempête, était un guerrier bien plus puissant que moi. Alors que je m\'entraînais dans le but de diriger notre clan, lui poursuivait une quête bien plus ambitieuse.' WHERE entry=2000000267; /* content: My brother, Stormhoof, was a far greater warrior than I. While I trained to be the chieftain of our clan, he prepared for a larger quest. */
UPDATE db_script_string SET content_loc2='Comment pourrait-il être menacé ? Tout ce qu\'il voulait, c\'était nous donner une emprise sur les éléments qui nous mènent la vie si dure ici.' WHERE entry=2000000268; /* content: How could he be in danger? All he wanted was to give us power over the elements that made life harsh for us here. */
UPDATE db_script_string SET content_loc2='Il était à la recherche d\'une relique… Une corne, je crois. Il a quitté le village et entrepris un long voyage pour cette quête.' WHERE entry=2000000269; /* content: He sought an artifact... a horn, I think. He left the village on a long journey in search of it. */
UPDATE db_script_string SET content_loc2='Je sais qu\'un ennemi terrifiant est parti à sa poursuite après qu\'il s\'est emparé de cette corne, mais… Je ne me rappelle plus… Ce qui est arrivé ensuite.' WHERE entry=2000000270; /* content: I know that a terrible enemy pursued him after he won the horn, but I... I don\'t recall... what happened... next. */
UPDATE db_script_string SET content_loc2='C\'est comme si mes souvenirs étaient enveloppés de brume. Je ne me souviens même plus de ce qu\'il est advenu de mon frère. Est-ce ce que vous vouliez dire quand vous m\'expliquiez qu\'il est en danger ?' WHERE entry=2000000271; /* content: It is as though my memories are shrouded in mist. I cannot even recall what became of my brother. Is this how you mean that he is in danger? */
UPDATE db_script_string SET content_loc2='Les perturbations qui agitent cette tombe… elles doivent y être liées. Regardez autour de vous, $n. Les voyez-vous ?' WHERE entry=2000000272; /* content: The disturbances in the tomb, they must be involved. Look around you, $N. Do you see them? */
UPDATE db_script_string SET content_loc2='Oui, ce sont elles les responsables, mais je n\'ai pas le pouvoir de les arrêter. Pourriez-vous avertir les gens de Tunka\'lo, $gétranger:étrangère; ? Dites-leur que leur passé et leurs ancêtres sont en danger.' WHERE entry=2000000273; /* content: Yes, they are at fault, but I am powerless to stop them. Will you warn the people of Tunka\'lo, stranger, that their past and their ancestors are in danger? */
UPDATE db_script_string SET content_loc2='Le marteau de la connaissance reste silencieux. Ça n\'est pas de l\'époque de Sabot-tempête.' WHERE entry=2000000275; /* content: The Lorehammer is silent. This isn\'t from Stormhoof\'s time. */
UPDATE db_script_string SET content_loc2='Misérable traître ! Sironas vous détruira, vous et tout votre peuple !' WHERE entry=2000000276; /* content: Treacherous whelp! Sironas will destroy you and your people! */
UPDATE db_script_string SET content_loc2='Morae… c\'est toi ?' WHERE entry=2000000277; /* content: Morae... Is that you? */
UPDATE db_script_string SET content_loc2='Non, vous n\'êtes pas Morae, mais je sens que vous la connaissez. Lorsque vous la verrez, dites-lui que j\'ai survécu à l\'accident, mais que les infiltrateurs m\'ont achevé.' WHERE entry=2000000278; /* content: No... you\'re not Morae, but I sense that you have met. When you see her, tell her that I survived the crash, only to be done in by the infiltrators. */
UPDATE db_script_string SET content_loc2='Qu\'elle sache… que ma dernière pensée a été pour elle…' WHERE entry=2000000279; /* content: Let her know... my last thought was of her... */
UPDATE db_script_string SET content_loc2='Que la meilleure gagne !' WHERE entry=2000000281; /* content: May the best win! */
UPDATE db_script_string SET content_loc2='Vous ne me vaincrez pas !' WHERE entry=2000000282; /* content: You will not defeat me! */
UPDATE db_script_string SET content_loc2='Tu n\'es pas digne de Thorim !' WHERE entry=2000000283; /* content: You\'re not worthy of Thorim! */
UPDATE db_script_string SET content_loc2='Le sectateur bégaie.' WHERE entry=2000000286; /* content: The cultist practically sputters. */
UPDATE db_script_string SET content_loc2='Hum… Je me demande…' WHERE entry=2000000286; /* content: Hmm... I wonder... */
UPDATE db_script_string SET content_loc2='Madame.' WHERE entry=2000000287; /* content: My lady. */
UPDATE db_script_string SET content_loc2='Bon, où est-ce ?' WHERE entry=2000000287; /* content: Now, where is it? */
UPDATE db_script_string SET content_loc2='J\'écoute.' WHERE entry=2000000288; /* content: Report. */
UPDATE db_script_string SET content_loc2='Stupides mages… Ils passent leur temps à vider les réserves de composants et ne les remplacent jamais.' WHERE entry=2000000288; /* content: Stupid mages... always using the last of a reagent and never replacing it. */
UPDATE db_script_string SET content_loc2='Des nouvelles de Jotunheim. Les gardiens du sommeil pensent avoir trouvé un personnage de grande importance.' WHERE entry=2000000289; /* content: There is word from Jotunheim. The sleep-watchers there believe that they have found someone of significance. */
UPDATE db_script_string SET content_loc2='Décris-le, ce vrykul. À quoi est-ce qu\'il ressemble ?' WHERE entry=2000000290; /* content: Describe this vrykul. What does he look like? */
UPDATE db_script_string SET content_loc2='À quoi il ressemble, madame ? À un vrykul, je suppose. Ils ne me l\'ont pas montré. Depuis l\'incident du caveau des Ombres…' WHERE entry=2000000291; /* content: Look like, my lady? A vrykul, I suppose. They did not actually show him to me. Ever since The Shadow Vault.... */
UPDATE db_script_string SET content_loc2='Tes excuses ne m\'intéressent pas. Peut-être t\'ont-ils donné un nom ?' WHERE entry=2000000292; /* content: I am not interested in excuses. Perhaps they gave you a name? */
UPDATE db_script_string SET content_loc2='Me détendre ? Tu sais ce que Kael\'thas fait à ceux qui échouent, Geezle ? Souffrances et douleurs éternelles... Faut PAS que tu te plantes, crétin.' WHERE entry=2000000292; /* content: Relax? Do you know what Kael\'thas does to those that fail him, Geezle? Eternal suffering and pain... Do NOT screw this up, fool. */
UPDATE db_script_string SET content_loc2='Un nom ? Ah oui, le nom ! Je crois que c\'était Iskalder.' WHERE entry=2000000293; /* content: A name? Oh, yes, the name! I believe it was Iskalder. */
UPDATE db_script_string SET content_loc2='Nos éclaireurs à Brume-sang ont trouvé notre contact. Cet imbécile de Velen se trouvera bientôt sans défense... Assez longtemps pour nous permettre de frapper ! Maintenant, hors de ma vue avant que je te vaporise...' WHERE entry=2000000293; /* content: Our Bloodmyst scouts have located our contact. The fool, Velen, will soon leave himself open and defenseless -- long enough for us to strike! Now get out of my sight before I vaporize you... */
UPDATE db_script_string SET content_loc2='Retourne à Jotunheim, et dis-leur de le garder endormi jusqu\'à mon arrivée. Je veux voir ce que vaut ce vrykul de mes propres yeux.' WHERE entry=2000000295; /* content: Return to Jotunheim and tell them to keep him asleep until I arrive. I will judge this vrykul with my own eyes. */
UPDATE db_script_string SET content_loc2='Tout de suite, madame.' WHERE entry=2000000296; /* content: Right away, my lady. */
UPDATE db_script_string SET content_loc2='%s ramasse le drapeau naga.' WHERE entry=2000000297; /* content: %s picks up the naga flag. */
UPDATE db_script_string SET content_loc2='C\'est quoi, la grande idée, l\'Étincelle ? Pourquoi avoir demandé cette réunion ?' WHERE entry=2000000298; /* content: What\'s the big idea, Spark? Why\'d you call for this meeting? */
UPDATE db_script_string SET content_loc2='Je me sens… différent.' WHERE entry=2000000299; /* content: I feel... different. */
UPDATE db_script_string SET content_loc2='La grande idée, c\'est d\'éviter de griller ma couverture, bougre d\'idiot ! Je t\'avais dit de mettre le compas et les cartes marines à l\'abri, pas de les laisser traîner là où n\'importe quel crétin pouvait les trouver.' WHERE entry=2000000299; /* content: What\'s the big idea? You nearly blew my cover, idiot! I told you to put the compass and navigation maps somewhere safe - not out in the open for any fool to discover. */
UPDATE db_script_string SET content_loc2='Le Maître a dû consentir à de très gros efforts pour obtenir ces informations sur l\'emplacement de l\'Exodar. Tu as failli compromettre toute l\'opération, y compris la couverture de notre espion de l\'intérieur.' WHERE entry=2000000300; /* content: The Master has gone to great lengths to secure information about the whereabouts of the Exodar. You could have blown the entire operation, including the cover of our spy on the inside. */
UPDATE db_script_string SET content_loc2='Allez, l\'Étincelle, on se détend ! Tout est sous contrôle. Nous allons dépouiller l\'Exodar sous leur nez, et devenir très, très riches, tous les deux.' WHERE entry=2000000301; /* content: Relax, Spark! I have it all under control. We\'ll strip mine the Exodar right out from under \'em - making both you and I very, very rich in the process. */
UPDATE db_script_string SET content_loc2='Me détendre ? Tu sais ce que Kael\'thas fait à ceux qui échouent, Geezle ? Souffrances et douleurs éternelles... Faut PAS que tu te plantes, crétin.' WHERE entry=2000000302; /* content: Relax? Do you know what Kael\'thas does to those that fail him, Geezle? Eternal suffering and pain... Do NOT screw this up, fool. */
UPDATE db_script_string SET content_loc2='Nos éclaireurs à Brume-sang ont trouvé notre contact. Cet imbécile de Velen se trouvera bientôt sans défense... Assez longtemps pour nous permettre de frapper ! Maintenant, hors de ma vue avant que je te vaporise...' WHERE entry=2000000303; /* content: Our Bloodmyst scouts have located our contact. The fool, Velen, will soon leave himself open and defenseless -- long enough for us to strike! Now get out of my sight before I vaporize you... */
UPDATE db_script_string SET content_loc2='Oui, monsieur. Ça n\'arrivera plus...' WHERE entry=2000000304; /* content: Yes, sir. It won\'t happen again... */
UPDATE db_script_string SET content_loc2='%s rit.' WHERE entry=2000000305; /* content: %s laughs. */
UPDATE db_script_string SET content_loc2='Cela vous effraye-t-il de savoir qu\'il existe des serviteurs si dévoués à la Légion qu\'ils sont prêts à demeurer d\'indéfectibles citoyens de vos civilisations ridicules jusqu\'au jour où ils seront mis à contribution ?' WHERE entry=2000000306; /* content: Does it frighten you to know that there are those that would serve the Legion with such devotion as to remain unwavering citizens of your pointless civilizations until called upon? */
UPDATE db_script_string SET content_loc2='Vivre dans la peur, mourir par la volonté de Kael\'thas... Cela revient au même.' WHERE entry=2000000307; /* content: Live in fear, die by the will of Kael\'thas... It\'s all the same. */
UPDATE db_script_string SET content_loc2='Et maintenant, je découpe !' WHERE entry=2000000308; /* content: And now, I cut you! */
UPDATE db_script_string SET content_loc2='Mourez !' WHERE entry=2000000309; /* content: DIE! */
UPDATE db_script_string SET content_loc2='Grek essayer !' WHERE entry=2000000310; /* content: Grek try! */
UPDATE db_script_string SET content_loc2='Cette huile pas bien pour Grek ! Vous penser que Grek mauviette en robe ?' WHERE entry=2000000311; /* content: This oil no good for Grek! What Grek look like to you, some weakling in robes? */
UPDATE db_script_string SET content_loc2='Merci de m\'avoir sauvée, $n ! Mon père va être fou de joie !' WHERE entry=2000000313; /* content: Thank you for saving me, $N! My father will be delighted! */
UPDATE db_script_string SET content_loc2='Vous ne nous empêcherez pas de révéler la lâcheté de Jaina !' WHERE entry=2000000314; /* content: You can\'t stop us from exposing the truth about Jaina\'s cowardice! */
UPDATE db_script_string SET content_loc2='Ces gens sauront que l\'amiral Portvaillant était un vrai héros !' WHERE entry=2000000316; /* content: These people will know Admiral Proudmoore for the true hero he was! */
UPDATE db_script_string SET content_loc2='Imbécile ! Vous vouliez dérober le sang de Zelemar ? Vous allez le payer… avec le vôtre !' WHERE entry=2000000317; /* content: Insolent fool! You thought to steal Zelemar\'s blood? You shall pay with your own! */
UPDATE db_script_string SET content_loc2='J\'espère qu\'il verra la Lumière et qu\'il consacrera son énergie à la guérison de Quel\'Thalas, qui fut si belle.' WHERE entry=2000000322; /* content: I can only hope that he will see the Light and instead turn his energies to restoring once-beautiful Quel\'Thalas. */
UPDATE db_script_string SET content_loc2='Sortez-le de là !' WHERE entry=2000000323; /* content: Get him out of there! */
UPDATE db_script_string SET content_loc2='Peut-être Ograbisi utilisera-t-il votre tête comme petit chapeau quand j\'en aurai fini avec vous.' WHERE entry=2000000324; /* content: Perhaps Ograbisi will use your head as a tiny little hat when I\'m through with you. */
UPDATE db_script_string SET content_loc2='Là d\'où je viens, on dit merci aux gens qui ouvrent la porte d\'un détenu !' WHERE entry=2000000325; /* content: Where I come from, you get shanked for opening another inmate\'s cell door! */
UPDATE db_script_string SET content_loc2='La pierre de glace a fondu !' WHERE entry=2000000327; /* content: The Ice Stone has melted! */
UPDATE db_script_string SET content_loc2='Ahune, ta puissance ne grandira plus !' WHERE entry=2000000328; /* content: Ahune, your strength grows no more! */
UPDATE db_script_string SET content_loc2='Tu ne règneras pas de ta main de glace !' WHERE entry=2000000329; /* content: Your frozen reign will not come to pass! */
UPDATE db_script_string SET content_loc2='Sa mort est proche ! La deuxième partie de la Formule de damnation est à nous. Je…' WHERE entry=2000000330; /* content: It is dying! The second part of the Cipher of Damnation is ours. I... */
UPDATE db_script_string SET content_loc2='Je disparais… Retournez voir Ar\'tor… Ret… our… voi…' WHERE entry=2000000331; /* content: I am fading... Return to Ar\'tor... Ret... rn... to... */
UPDATE db_script_string SET content_loc2='Quelle bataille ! Z\'avez vu ça, Rhydian ?' WHERE entry=2000000332; /* content: What a battle! Did you see that, Rhydian?! */
UPDATE db_script_string SET content_loc2='Nous avons reçu le message de Brann, et nous avons entamé les préparatifs.' WHERE entry=2000000333; /* content: We received Brann\'s message, and we have begun preparations. */
UPDATE db_script_string SET content_loc2='Juste à temps. Finissons-en avec ça une bonne fois pour toutes.' WHERE entry=2000000334; /* content: Just in time. Let\'s see this thing off, then. */
UPDATE db_script_string SET content_loc2='Citoyens de Dalaran ! Levez les yeux vers le ciel et regardez bien !' WHERE entry=2000000335; /* content: Citizens of Dalaran! Raise your eyes to the skies and observe! */
UPDATE db_script_string SET content_loc2='En ce jour, la destruction de notre monde a été évitée, au grand dam de nos propres créateurs !' WHERE entry=2000000336; /* content: Today our world\'s destruction has been averted in defiance of our very makers! */
UPDATE db_script_string SET content_loc2='Algalon l\'Observateur, héraut des titans, a été vaincu par nos braves camarades dans les profondeurs de la cité d\'Ulduar.' WHERE entry=2000000337; /* content: Algalon the Observer, herald of the titans, has been defeated by our brave comrades in the depths of the titan city of Ulduar. */
UPDATE db_script_string SET content_loc2='Algalon avait été envoyé parmi nous pour juger du sort du monde.' WHERE entry=2000000338; /* content: Algalon was sent here to judge the fate of our world. */
UPDATE db_script_string SET content_loc2='Il a trouvé un monde où les peuples s\'étaient écartés du modèle des titans. Un monde sur lequel tout ne s\'était pas déroulé conformément au plan.' WHERE entry=2000000339; /* content: He found a planet whose races had deviated from the titans\' blueprints. A planet where not everything had gone according to plan. */
UPDATE db_script_string SET content_loc2='L\'implacable logique voulait que notre monde ne mérite pas d\'être sauvé. Mais cette logique ne prend pas en compte la puissance du libre arbitre. C\'est à chacun de nous qu\'il revient de prouver que ce monde mérite son salut.' WHERE entry=2000000340; /* content: Cold logic deemed our world not worth saving. Cold logic, however, does not account for the power of free will. It\'s up to each of us to prove this is a world worth saving. */
UPDATE db_script_string SET content_loc2='Que nos vies... Méritent d\'être vécues.' WHERE entry=2000000341; /* content: That our lives... our lives are worth living. */
UPDATE db_script_string SET content_loc2='ILS ARRIVENT ! Ne les laissez pas passer ! Concentrez votre tir ! FEU, FEU, FEU !' WHERE entry=2000000344; /* content: INCOMING! Do not let them through! Focus fire! NOW, NOW, NOW! */
UPDATE db_script_string SET content_loc2='Le pouvoir de la Larme des lunes est à moi ! À moi !' WHERE entry=2000000345; /* content: The power of the Tear of the Moons is mine! Mine I say! */
UPDATE db_script_string SET content_loc2='%s commence à frotter la Larme des lunes.' WHERE entry=2000000346; /* content: %s begins to rub the Tear of the Moons. */
UPDATE db_script_string SET content_loc2='Puissance ! Glorieuse puissance !' WHERE entry=2000000347; /* content: Power! Glorious power! */
UPDATE db_script_string SET content_loc2='Debout, les damnés, de la terre !' WHERE entry=2000000348; /* content: Arise from the earth, my brothers! */
UPDATE db_script_string SET content_loc2='%s se met à pousser des grognements étranges. La Larme des lunes se fracasse au sol.' WHERE entry=2000000348; /* content: %s begins to make strange grunting noises. The Tear of the Moons drops to the ground and shatters. */
UPDATE db_script_string SET content_loc2='Nos maîtres de fer ont une mission pour nous !' WHERE entry=2000000349; /* content: Our iron masters have a mission for us! */
UPDATE db_script_string SET content_loc2='Suivez-moi vers les montagnes, allons mettre à exécution le plan de nos maîtres !' WHERE entry=2000000350; /* content: Follow me into the mountains to carry out the plan of the masters! */
UPDATE db_script_string SET content_loc2='J\'ai mal… souffrance insupportable… mettez-y fin… Je vous en supplie !' WHERE entry=2000000350; /* content: I hurt... suffering unbearable... end my pain... I beg of you! */
UPDATE db_script_string SET content_loc2='Je reviens de la tombe pour obéir à mon maître.' WHERE entry=2000000352; /* content: I return from the grave to do my master\'s bidding. */
UPDATE db_script_string SET content_loc2='Encore un échec…' WHERE entry=2000000353; /* content: Another failure... */
UPDATE db_script_string SET content_loc2='Celui-ci s\'est réveillé trop tôt. Il a gardé des émotions et des souvenirs…' WHERE entry=2000000354; /* content: This one has awoken too soon. It retains emotion and memory... */
UPDATE db_script_string SET content_loc2='Le roi-liche sera satisfait par cet initié.' WHERE entry=2000000355; /* content: The Lich King will be pleased with this initiate. */
UPDATE db_script_string SET content_loc2='Inutile…' WHERE entry=2000000356; /* content: Useless... */
UPDATE db_script_string SET content_loc2='Oui, instructeur.' WHERE entry=2000000360; /* content: Yes, instructor. */
UPDATE db_script_string SET content_loc2='Tout de suite, instructeur.' WHERE entry=2000000361; /* content: Right away, instructor. */
UPDATE db_script_string SET content_loc2='Levez-vous, serviteurs. Levez-vous et repaissez-vous de la chair des faibles !' WHERE entry=2000000362; /* content: Rise, minions. Rise and feast upon the weak! */
UPDATE db_script_string SET content_loc2='Victoire au Fléau !' WHERE entry=2000000365; /* content: Victory to the Scourge! */
UPDATE db_script_string SET content_loc2='Mort à ceux qui s\'opposent à nous !' WHERE entry=2000000366; /* content: Death to all that oppose us! */
UPDATE db_script_string SET content_loc2='Pathétique…' WHERE entry=2000000368; /* content: Pathetic... */
UPDATE db_script_string SET content_loc2='Et qu\'est-ce que vous imaginez, au juste ? Vous osez déranger l\'expérience de mon maître ?' WHERE entry=2000000369; /* content: And just what do you think you are doing?  You dare to interfere with my master\'s experiment? */
UPDATE db_script_string SET content_loc2='Vous appréciez notre travail ici ? Peut-être ferons-nous tomber ces cristaux du ciel sur toute l\'Outreterre.' WHERE entry=2000000370; /* content: Do you like what we\'ve done here?  Perhaps we will drop these crystals from the sky all over Outland. */
UPDATE db_script_string SET content_loc2='Vos tentatives de guérir cette terre et d\'endiguer les énergies qui invoquent les colosses et les rendent fous commencent à m\'ennuyer profondément. Goliathon, roi des colosses, Kael\'thas et moi-même t\'ordonnons de défendre le cristal !' WHERE entry=2000000371; /* content: I grow bored with your attempt to heal the land and quell the energies summoning and driving the colossi mad.  Goliathon, King of the Colossi, Prince Kael\'thas and I demand that you defend the crystal! */
UPDATE db_script_string SET content_loc2='Nous nous reverrons bientôt.' WHERE entry=2000000372; /* content: We will meet again soon. */
UPDATE db_script_string SET content_loc2='D\'où est-ce que ça sort, tout ça ? Je ferais mieux de tout descendre aux bateaux, avant que le contremaître ne voie ça !' WHERE entry=2000000373; /* content: Where\'d this come from? I better get this down to the ships before the foreman sees it! */
UPDATE db_script_string SET content_loc2='J\'ai qu\'à tout laisser ici, personne n\'en saura rien !' WHERE entry=2000000374; /* content: I\'ll just leave it here and nobody will be the wiser! */
UPDATE db_script_string SET content_loc2='Restez près de moi, $n. Je vous protégerai si quelque chose devait mal se passer.' WHERE entry=2000000376; /* content: Stand near me, $N. I will protect you should anything go wrong. */
UPDATE db_script_string SET content_loc2='Malfurion !' WHERE entry=2000000377; /* content: Malfurion! */
UPDATE db_script_string SET content_loc2='Il était voilé de cauchemars, Malfurion. Que se passe-t-il dans le rêve ? Qu\'est-ce qui pourrait causer de telles atrocités ?' WHERE entry=2000000379; /* content: It was shrouded in nightmares, Malfurion. What is happening in the Dream? What could cause such atrocities? */
UPDATE db_script_string SET content_loc2='Je le sens aussi, Malfurion. Des jours sombres nous attendent.' WHERE entry=2000000381; /* content: I sensed as much, Malfurion. Dark days loom ahead. */
UPDATE db_script_string SET content_loc2='Adieu, mon ami… Adieu…' WHERE entry=2000000386; /* content: Farewell, old friend... Farewell... */
UPDATE db_script_string SET content_loc2='Retournons dans le bosquet, $gmortel:mortelle;.' WHERE entry=2000000387; /* content: Let us return to the grove, mortal. */
UPDATE db_script_string SET content_loc2='Ok, allons-y. Je connais justement un endroit idéal !' WHERE entry=2000000388; /* content: Ok, let\'s go.  I know just the perfect spot! */
UPDATE db_script_string SET content_loc2='Quoi... Qu\'est-ce qui... m\'arrive ? Sortez ce chat de ma vue !' WHERE entry=2000000389; /* content: What... is happening... to me?  Get this cat away from me! */
UPDATE db_script_string SET content_loc2='Espiègle regarde Viera Murmesoleil avec des yeux affamés.' WHERE entry=2000000390; /* content: Twinkle looks at Viera Sunwhisper with very hungry eyes. */
UPDATE db_script_string SET content_loc2='Sortez de là, petits zoziaux ! Mug\'gok veut jouer !' WHERE entry=2000000392; /* content: Come out, little birdies! Mug\'gok want to play! */
UPDATE db_script_string SET content_loc2='Hommes-oiseaux pas rendre Mug\'gok triste !' WHERE entry=2000000393; /* content: No hurt Mug\'gok\'s feelings, bird-men! */
UPDATE db_script_string SET content_loc2='D\'accord ! Zoziaux font comme ils veulent ! Mug\'gok va chercher des nouveaux amis !' WHERE entry=2000000394; /* content: Fine! Birdies be dat way! Mug\'gok find new friends! */
UPDATE db_script_string SET content_loc2='%s époussète la base de l\'artéfact.' WHERE entry=2000000395; /* content: %s dusts the base of the artifact. */
UPDATE db_script_string SET content_loc2='%s examine attentivement le grand vase.' WHERE entry=2000000396; /* content: %s meticulously examines the large vase. */
UPDATE db_script_string SET content_loc2='Pauvre Dorius. Si jamais je mets la main sur ces Sombrefer, je vous jure que...' WHERE entry=2000000397; /* content: Poor Dorius. If I ever get my hands on those Dark Irons, so help me... */
UPDATE db_script_string SET content_loc2='Donnez aux œuvres de bienfaisance qui essaient d\'aider les victimes de ces temps difficiles ! S\'il vous plaît.' WHERE entry=2000000398; /* content: Give to the charities who seek to help the victims of these hard times! Please. */
UPDATE db_script_string SET content_loc2='N\'oubliez pas les orphelins de Hurlevent !' WHERE entry=2000000399; /* content: Don\'t forget the orphans of Stormwind! */
UPDATE db_script_string SET content_loc2='Donnez un peu de votre cœur et de votre bourse ! Donnez aux enfants de Hurlevent qui ont perdu leurs parents.' WHERE entry=2000000400; /* content: Give of your hearts and your purses! Give to the children of Stormwind who have lost their parents. */
UPDATE db_script_string SET content_loc2='Aidez les enfants de Hurlevent... victimes de la guerre et de la peste !' WHERE entry=2000000401; /* content: Help the children of Stormwind... victims of the war and plague! */
UPDATE db_script_string SET content_loc2='%s en appelle aux puissantes armées du Fléau !' WHERE entry=2000000402; /* content: %s calls upon the mighty armies of the Scourge! */
UPDATE db_script_string SET content_loc2='Armées du Fléau, répondez à mon appel ! L\'apocalypse écarlate a commencé ! Semez la dévastation sur ces terres et ne laissez que la mort derrière vous !' WHERE entry=2000000403; /* content: Armies of the Scourge, hear my call! The scarlet apocalypse has begun! Tear this land asunder and leave only death in your wake! */
UPDATE db_script_string SET content_loc2='Je vais aussi vite que je peux, capitaine !' WHERE entry=2000000407; /* content: I\'m workin\' as fast as I can, cap\'n! */
UPDATE db_script_string SET content_loc2='Je ne pourrai pas tenir ce rythme, chef ! Si le Fléau ne me tue pas, ce sera l\'épuisement !' WHERE entry=2000000408; /* content: I can\'t keep this pace up, sir! If the Scourge don\'t get me, exhaustion will! */
UPDATE db_script_string SET content_loc2='Souffrez bien, chevalier de la mort…' WHERE entry=2000000409; /* content: Suffer well, death knight... */
UPDATE db_script_string SET content_loc2='Le monde des ombres attend…' WHERE entry=2000000410; /* content: The realm of shadows awaits... */
UPDATE db_script_string SET content_loc2='Qu\'allons-nous faire ? Nous avons tout perdu !' WHERE entry=2000000411; /* content: What are we to do? We\'ve lost everything! */
UPDATE db_script_string SET content_loc2='Nous allons nous battre ! On les repousse, comme on l\'a toujours fait !' WHERE entry=2000000413; /* content: We fight! We push them back, just as we have always done! */
UPDATE db_script_string SET content_loc2='%s se réveille en sursaut.' WHERE entry=2000000467; /* content: %s wakes up, startled. */
UPDATE db_script_string SET content_loc2='Mais à quoi ils s\'attendent, à faire descendre les chauves-souris sous cet angle ? Nuques brisées et ragoût de chiroptère, c\'est tout ce qu\'ils en tireront.' WHERE entry=2000000467; /* content: What do they expect, making the bats come in at that angle? Broken necks and gamey bat stew, that\'s what they get. */
UPDATE db_script_string SET content_loc2='Ce… C\'était un rêve ? Brume-azur, Brume-sang, le marécage de Zangar, Ombrelune… Et même Zul\'Aman… Jane ! Jane, où es-tu ?!' WHERE entry=2000000468; /* content: Was... Was it all a dream? Azuremyst, Bloodmyst, Zangarmarsh, Shadowmoon... even Zul\'Aman... Jane! Jane where are you! */
UPDATE db_script_string SET content_loc2='Je suis là, Papa. Tu as dormi toute la journée. Tu devais vraiment être fatigué !' WHERE entry=2000000469; /* content: I\'m right here, daddy. You\'ve been asleep all day long. You sure must be tired! */
UPDATE db_script_string SET content_loc2='Ah, heureusement, tout cela n\'était qu\'un rêve...' WHERE entry=2000000470; /* content: Ah, wonderful. It was all just a dream... */
UPDATE db_script_string SET content_loc2='%s retourne à ses jeux sur la plage.' WHERE entry=2000000471; /* content: %s resumes playing on the beach. */
UPDATE db_script_string SET content_loc2='%s range les bouteilles et pousse un soupir.' WHERE entry=2000000474; /* content: Putting the bottles away, %s sighs. */
UPDATE db_script_string SET content_loc2='Quel joli coup !' WHERE entry=2000000482; /* content: What a beautiful shot! */
UPDATE db_script_string SET content_loc2='Ha ! En plein dans le mille !' WHERE entry=2000000483; /* content: Hah! Bullseye! */
UPDATE db_script_string SET content_loc2='Agh… on vient de marcher sur ma tombe.' WHERE entry=2000000484; /* content: Agh... a chill ran up my spine. */
UPDATE db_script_string SET content_loc2='Ils verront la lumière, j\'espère.' WHERE entry=2000000485; /* content: They\'ll see the light, I hope. */
UPDATE db_script_string SET content_loc2='Je... J\'en ai vu assez... Si épuisant...' WHERE entry=2000000522; /* content: I... I\'ve seen enough I think... so very draining... */
UPDATE db_script_string SET content_loc2='%s effleure les pages du livre.' WHERE entry=2000000524; /* content: %s skims the pages of the book. */
UPDATE db_script_string SET content_loc2='Superbe, Maltrake. On dirait bien que mes plans ont fonctionné à la perfection, n\'est-ce pas, mortel ?' WHERE entry=2000000526; /* content: Glorious, indeed, Maltrake. It looks as if my plan worked perfectly. Wouldn\'t you say so, mortal? */
UPDATE db_script_string SET content_loc2='%s lâche un rire reptilien... Enfin, vous pensez que c\'est un rire.' WHERE entry=2000000527; /* content: %s lets loose a reptilian laugh... at least you think it\'s a laugh. */
UPDATE db_script_string SET content_loc2='Vous n\'auriez pas aidé la légion de Rochenoire si nous vous avions juste demandé de l\'aide, pas vrai ?' WHERE entry=2000000528; /* content: You would not have helped the legion of Blackrock had we just asked for your assistance, now would you? */
UPDATE db_script_string SET content_loc2='Qu\'allez-vous faire d\'eux, maître ? Ils ne sont plus d\'aucune utilité, maintenant.' WHERE entry=2000000529; /* content: What will we do with them, master? They are of no use to us anymore. */
UPDATE db_script_string SET content_loc2='SILENCE, IMBÉCILE !' WHERE entry=2000000530; /* content: SILENCE FOOL! */
UPDATE db_script_string SET content_loc2='Je vous laisse maintenant, mortels. Vivants et avec ces babioles. Maltrake ! Montrez-leur les babioles !' WHERE entry=2000000532; /* content: I leave you now, mortals. Alive and with these trinkets. Maltrake! Present them with the trinkets! */
UPDATE db_script_string SET content_loc2='Tout de suite, maître !' WHERE entry=2000000533; /* content: Right away, master! */
UPDATE db_script_string SET content_loc2='%s bat plus rapidement l\'air de ses puissantes ailes. Il se prépare à l\'envol.' WHERE entry=2000000534; /* content: %s begins to flap his massive wings faster. He is preparing for flight. */
UPDATE db_script_string SET content_loc2='Oh oui, l\'écaille, ne la perdez pas. Certains de mes frères de race dans les steppes Ardentes font des objets que seule l\'écaille des dragons noirs peut fortifier.' WHERE entry=2000000535; /* content: Oh yes, the molt - do not lose it. There are those of my kin in the Burning Steppes that would craft items that only the molt of the black dragonflight could fortify. */
UPDATE db_script_string SET content_loc2='La légion de Rochenoire arrive, bande de nabots ! Nous allons réduire la terre en cendres et consumer les cieux ! Nul ne survivra…' WHERE entry=2000000536; /* content: The legion of Blackrock comes, dwarflings! We shall scorch the earth and set fire to the heavens. None shall survive... */
UPDATE db_script_string SET content_loc2='Nous devons nous hâter !' WHERE entry=2000000537; /* content: We must hurry. */
UPDATE db_script_string SET content_loc2='Esprits de la Forêt, montrez-vous et obéissez à l\'appel de la Nature !' WHERE entry=2000000538; /* content: Spirits of the forest come forth and heed nature\'s call! */
UPDATE db_script_string SET content_loc2='Esprits de la forêt, nous avons besoin de vous ! Venez vite au Val calciné !' WHERE entry=2000000539; /* content: Spirits of the forest, you are needed! Make haste to the Charred Vale! */
UPDATE db_script_string SET content_loc2='Bon, voyons... Si je ne me trompe pas... Heu...' WHERE entry=2000000540; /* content: Now, let\'s see... If I am correct, then... Hmmm... */
UPDATE db_script_string SET content_loc2='Bleu plus rouge... Je prédis...' WHERE entry=2000000541; /* content: Blue plus red... I predict... */
UPDATE db_script_string SET content_loc2='$n, c\'est ça ! J\'ai enfin compris !' WHERE entry=2000000542; /* content: $N, that\'s it! I have finally figured it out! */
UPDATE db_script_string SET content_loc2='Merci, mon $gami:amie;.' WHERE entry=2000000558; /* content: Thank you, friend. */
UPDATE db_script_string SET content_loc2='J\'aurai toujours une dette envers vous, mon $gami:amie;.' WHERE entry=2000000559; /* content: I am forever indebted to you, friend. */
UPDATE db_script_string SET content_loc2='Je vous remercie, $gétranger:étrangère;.' WHERE entry=2000000561; /* content: You have my thanks, stranger. */
UPDATE db_script_string SET content_loc2='%s barrit son approbation.' WHERE entry=2000000562; /* content: %s trumpets in approval! */
UPDATE db_script_string SET content_loc2='Mes frères et mes sœurs. Écoutez-moi. Nous devons parler de la survie de notre peuple. Nous avons difficilement réussi à survivre à une catastrophe, mais je crains que la prochaine ne nous détruise.' WHERE entry=2000000571; /* content: My brothers and sisters. Listen to me. We must speak of the survival of our people. We have narrowly survived one calamity, but I fear the next shall destroy us. */
UPDATE db_script_string SET content_loc2='Ne voyez-vous pas ce qui se passe ? Ouvrez les yeux. Cela nous crève la vue. Quelle est cette nouvelle alliance ? Pourquoi avons-nous trahi nos anciens alliés, pour nous ranger aux côtés des ennemis de tous les êtres vivants ?' WHERE entry=2000000572; /* content: Can you not see what is happening? Open your eyes. It is laid bare for all to see. What is this new alliance? Why have we betrayed our allies of old, only to take up with the enemies of all that live? */
UPDATE db_script_string SET content_loc2='Non, vous avez tort ! C\'est nous qui avons été trahis !' WHERE entry=2000000573; /* content: No, you\'re wrong! We were the ones betrayed! */
UPDATE db_script_string SET content_loc2='Et depuis quand les nains sont nos amis ?' WHERE entry=2000000574; /* content: When have the dwarves ever been our friends? */
UPDATE db_script_string SET content_loc2='Les humains nous ont abandonnés à l\'heure la plus grave ! Leur prince lui-même a conduit l\'attaque sur nos terres.' WHERE entry=2000000575; /* content: The humans abandoned us in our darkest hour! Their prince himself led the assault on our lands. */
UPDATE db_script_string SET content_loc2='Idiot ! Les serviteurs du prince étaient les Réprouvés, ceux-là même auxquels nous sommes alliés aujourd\'hui, et maintenant le prince nous a trahi !' WHERE entry=2000000576; /* content: You fool! The prince\'s minions were the very Forsaken we have now allied ourselves with, and now he has betrayed us! */
UPDATE db_script_string SET content_loc2='Le général des forestiers de Lune-d\'Argent a été un allié loyal !' WHERE entry=2000000577; /* content: The Ranger-General of Silvermoon will be a loyal ally! */
UPDATE db_script_string SET content_loc2='Vous parlez d\'elle comme d\'une amie ? Vous êtes un imbécile. Cette « dame noire » n\'est pas Sylvanas.' WHERE entry=2000000578; /* content: You speak of her as friend? You are a fool. This "Dark Lady" is not Sylvanas. */
UPDATE db_script_string SET content_loc2='Le sang parle. Nous ne pouvons faire confiance à personne, sauf à nos proches !' WHERE entry=2000000579; /* content: Blood will be true. We can trust in none but our own! */
UPDATE db_script_string SET content_loc2='Du sang ? Et ces orcs, ces alliés de circonstance ? Nous aideront-ils ?' WHERE entry=2000000580; /* content: Blood? And these orcs, these fair weather friends? They will aid us? */
UPDATE db_script_string SET content_loc2='La Croisade d\'argent vous invite à prendre la mesure de votre talent contre d\'autres héros d\'Azeroth au tournoi d\'Argent.' WHERE entry=2000000580; /* content: The Argent Crusade invites you to test your skills in honorable combat against other heroes of Azeroth at the Argent Tournament. */
UPDATE db_script_string SET content_loc2='Nos terres sont marquées par un grand chancre qui les ronge un peu plus chaque jour ! Notre besoin de magie nous affaiblit, et nous conduira à notre perte, comme autrefois !' WHERE entry=2000000581; /* content: A great blight lies across our land! It grows every day. Our need for magic weakens us, and will bring us to destruction, just as it has before! */
UPDATE db_script_string SET content_loc2='Les champions des plus grandes nations du monde sont rassemblés sur le site du tournoi d\'Argent, au nord-est de la Couronne de glace, pour y affronter tous les défis.' WHERE entry=2000000581; /* content: The champions of the world\'s great nations have gathered at the Argent Tournament Grounds in northeastern Icecrown to face all challengers. */
UPDATE db_script_string SET content_loc2='Comment vivre sans magie ?' WHERE entry=2000000582; /* content: How are we to live without magic? */
UPDATE db_script_string SET content_loc2='Contemplez les merveilles de cette cité ! C\'est de la magie !' WHERE entry=2000000583; /* content: Look at the wonders of this city! This is magic. */
UPDATE db_script_string SET content_loc2='Vous nous traitez de traîtres ?' WHERE entry=2000000585; /* content: You call us traitors? */
UPDATE db_script_string SET content_loc2='Ce sont les magistères les traîtres ! Ils ont scellé notre destin !' WHERE entry=2000000586; /* content: The magisters are the traitors! They have sealed our doom. */
UPDATE db_script_string SET content_loc2='Sornettes d\'idéaliste. Retournez à vos forêts !' WHERE entry=2000000587; /* content: Idealistic blather. Go back to your forests! */
UPDATE db_script_string SET content_loc2='L\'oracle a donc décidé de vous aider, $gétranger:étrangère;. Très bien, je vais vous donner ce que je sais.' WHERE entry=2000000587; /* content: So the oracle has decided to aid you, outlander. Very well, I will offer you what i know. */
UPDATE db_script_string SET content_loc2='Vous voulez que nous vivions comme des sauvages ?' WHERE entry=2000000588; /* content: You would have us live like the savages! */
UPDATE db_script_string SET content_loc2='Votre compagnon recherche le patriarche cornéclat, Farunn, qui se tient à l\'écart de ses frères.' WHERE entry=2000000588; /* content: Your companion seeks the shardhorn patriarch Farunn, who holds himself aloof from his brothers. */
UPDATE db_script_string SET content_loc2='Il n\'est pas question de sauvagerie, c\'est une question de conscience. Les excès de notre peuple vont nous détruire, nous et tout ce que nous avons bâti au prix de tant de luttes.' WHERE entry=2000000589; /* content: No, not savagery! Conscience. The excesses of our people will destroy us, and all that we have struggled to build. */
UPDATE db_script_string SET content_loc2='Après tout ce que nous avons traversé pour survivre, nous n\'allons pas provoquer notre extinction.' WHERE entry=2000000590; /* content: We have not struggled so hard to survive, only to bring about the true destruction. */
UPDATE db_script_string SET content_loc2='Retournez voir votre compagnon. Dites-lui d\'aller saisir ce qu\'il recherche auprès de Farunn.' WHERE entry=2000000590; /* content: Return to your companion. Tell him to go forth and claim his prize in Farunn. */
UPDATE db_script_string SET content_loc2='Nous sommes au bord de l\'anéantissement. Nous devons changer de direction avant qu\'il ne soit trop tard.' WHERE entry=2000000591; /* content: We stand on the brink of destruction. We must reconsider this rash course of action. */
UPDATE db_script_string SET content_loc2='Quand ce sera fait, dites-lui de quitter cette terre en paix. Sa place n\'est pas ici.' WHERE entry=2000000591; /* content: Once he has done so, tell him to leave this land in peace. He does not belong here. */
UPDATE db_script_string SET content_loc2='Si nous sommes unis, il n\'est plus possible de nous ignorer !' WHERE entry=2000000592; /* content: If we speak as one, we cannot be ignored! */
UPDATE db_script_string SET content_loc2='Soutenez-nous ! Nous devons agir dès maintenant pour sauver notre peuple.' WHERE entry=2000000593; /* content: Lend us your voices! Now is the time we must act to save our people. */
UPDATE db_script_string SET content_loc2='Lor\'themar doit nous entendre ! On ne nous fera pas taire !' WHERE entry=2000000594; /* content: Lor\'themar must hear us! We will not be silenced! */
UPDATE db_script_string SET content_loc2='Cela devrait suffire. Si vous avez de nouveau besoin de mes services, rappelez-moi.' WHERE entry=2000000595; /* content: That should take care of that. Summon me if you have further need of my services. */
UPDATE db_script_string SET content_loc2='L\'Alliance payera très cher sa trahison à notre égard. Nous n\'oublierons jamais, nous ne pardonnerons jamais.' WHERE entry=2000000596; /* content: We will repay the betrayal of the Alliance! We shall never forget, and never forgive! */
UPDATE db_script_string SET content_loc2='Comment pouvez-vous nier que notre vie est meilleure ? Comment pouvez-vous refuser nos progrès ?' WHERE entry=2000000597; /* content: How can you say our lives are not better now? How can you deny the betterment of our lives? */
UPDATE db_script_string SET content_loc2='Lor\'themar a remodelé Lune-d\'Argent. Nous pouvons vraiment être fiers du résultat.' WHERE entry=2000000598; /* content: Lor\'themar has remade Silvermoon into something we may truly be proud of. */
UPDATE db_script_string SET content_loc2='Oh, ce sont des jacinthes d\'hiver ? Pour moi ?' WHERE entry=2000000599; /* content: Are those winter hyacinths? For me? */
UPDATE db_script_string SET content_loc2='Nos nouveaux alliés nous aideront à retrouver notre gloire passée. Ces liens résisteront au temps, et nous ne serons plus jamais trahis !' WHERE entry=2000000599; /* content: Our new allies will bring us back to our former glory. These bonds will be true, and we will not be betrayed again! */
UPDATE db_script_string SET content_loc2='Lorsque le produit neutralisant se mélange à l\'eau du lac, l\'eau irradiée par le cristal semble s\'épurer.' WHERE entry=2000000633; /* content: As the neutralizing agent mixes into the waters of the lake, the water that was irradiated by the crystal seems to clear. */
UPDATE db_script_string SET content_loc2='Tedon, allez-y, essayez le produit neutralisant sur la créature mutante.' WHERE entry=2000000634; /* content: Tedon, go ahead and try the neutralizing agent on the mutated creature. */
UPDATE db_script_string SET content_loc2='On dirait qu\'il est temps d\'ouvrir cette cage.' WHERE entry=2000000635; /* content: Looks like it\'s time to let you out of this cage. */
UPDATE db_script_string SET content_loc2='Alors, on applique le produit neutralisant…' WHERE entry=2000000636; /* content: Now, to put the neutralizing agent on it... */
UPDATE db_script_string SET content_loc2='Victoire !' WHERE entry=2000000637; /* content: Success! */
UPDATE db_script_string SET content_loc2='Ça ne marchera pas du tout ! Les bâtiments vont s\'effondrer si on ne renforce pas les poutres de soutènement.' WHERE entry=2000000645; /* content: This won\'t work at all! The buildings will surely collapse if we do not shore up the support beams. */
UPDATE db_script_string SET content_loc2='Ça ne va pas du tout ! Il faut stabiliser les fondations, sinon tout l\'édifice va s\'ébouler !' WHERE entry=2000000646; /* content: This won\'t do at all! We have to secure the foundation or the entire structure will crumble! */
UPDATE db_script_string SET content_loc2='Cet édifice est en infraction d\'au moins 8 ordonnances différentes !' WHERE entry=2000000647; /* content: This structure violates at least 8 different ordinances! */
UPDATE db_script_string SET content_loc2='Hum, c\'est déjà beaucoup mieux. Allons jeter un œil à l\'intérieur.' WHERE entry=2000000649; /* content: Hrm, this looks much better. Let\'s have a look on the inside. */
UPDATE db_script_string SET content_loc2='Par les vilains tétons du torse pourri de Kil\'Jaeden, tout l\'arrière est explosé ! Comment voulez-vous que quelqu\'un puisse vivre ici ? J\'en ai assez vu !' WHERE entry=2000000650; /* content: By the foul teat of Kil\'Jaeden\'s rotted torso, the entire backside is blown out! How can someone be expected to live in here? I\'ve seen enough! */
UPDATE db_script_string SET content_loc2='Cela devrait aller…' WHERE entry=2000000655; /* content: This should do it... */
UPDATE db_script_string SET content_loc2='Réveillez-vous, petit être. Sortez de votre léthargie. Vous êtes en sécurité, à présent.' WHERE entry=2000000656; /* content: Wake up little creature. Wake up from your slumber... You are safe now. */
UPDATE db_script_string SET content_loc2='%s grogne.' WHERE entry=2000000657; /* content: %s groans. */
UPDATE db_script_string SET content_loc2='%s s\'assied.' WHERE entry=2000000658; /* content: %s sits up. */
UPDATE db_script_string SET content_loc2='Où… où suis-je ? Quel est cet endroit ?' WHERE entry=2000000659; /* content: Whe... Where am I? What... What is this place? */
UPDATE db_script_string SET content_loc2='%s retombe dans le coma.' WHERE entry=2000000661; /* content: %s falls back into a coma. */
UPDATE db_script_string SET content_loc2='Alors là, c\'est la guerre !' WHERE entry=2000000661; /* content: This mean war! */
UPDATE db_script_string SET content_loc2='Comment puis-je espérer accomplir quoi que ce soit avec cette chose qui répand son hérésie ?' WHERE entry=2000000663; /* content: How can I be expected to get any work done with that thing spreading its heresy? */
UPDATE db_script_string SET content_loc2='Je suis le héraut de Mécazod. Vous allez être dé-maudits !' WHERE entry=2000000663; /* content: I am the herald of Mechazod. You will be decursed! */
UPDATE db_script_string SET content_loc2='Vos bricolages ne vont plus durer. Quand j\'en aurai terminé avec vous, Mécazod vous guérira de cette malédiction.' WHERE entry=2000000664; /* content: Your meddling is at an end. Mechazod will relieve your curse once I am done with you. */
UPDATE db_script_string SET content_loc2='Nous n\'avons pas besoin de ceux de votre espèce ici.' WHERE entry=2000000665; /* content: We do not need your kind here. */
UPDATE db_script_string SET content_loc2='ED-210 en ligne !' WHERE entry=2000000665; /* content: ED-210 online! */
UPDATE db_script_string SET content_loc2='%s vous exhorte à le suivre.' WHERE entry=2000000666; /* content: %s urges you to follow him. */
UPDATE db_script_string SET content_loc2='Twonky !' WHERE entry=2000000666; /* content: Twonky! */
UPDATE db_script_string SET content_loc2='%s désigne le totem de Coo.' WHERE entry=2000000667; /* content: %s points to the totem of Coo. */
UPDATE db_script_string SET content_loc2='%s vous fait signe de le suivre.' WHERE entry=2000000668; /* content: %s motions for you to follow him. */
UPDATE db_script_string SET content_loc2='[Furbolg] Ritk kin\'chikx azul azur sommet...' WHERE entry=2000000669; /* content: [Furbolg] Ritk kin\'chikx azul azure summit... */
UPDATE db_script_string SET content_loc2='[Furbolg] Coo xip vol... Sauter ilos rivière. Trouver Tikti.' WHERE entry=2000000670; /* content: [Furbolg] Coo xip fly... Jump ilos river. Find Tikti. */
UPDATE db_script_string SET content_loc2='%s montre l\'autre rive du fleuve.' WHERE entry=2000000671; /* content: %s points across the river. */
UPDATE db_script_string SET content_loc2='%s vous demande de le suivre.' WHERE entry=2000000672; /* content: %s asks that you follow him. */
UPDATE db_script_string SET content_loc2='[Furbolg] Loin vous dixik tak venir. Observer le fleuve kitch\'kal. Suivre. Ilog à Yor.' WHERE entry=2000000673; /* content: [Furbolg] Far you mixik tak come. Gaze upon the kitch\'kal river. Follow. Ilog to Yor. */
UPDATE db_script_string SET content_loc2='[Furbolg] Aller... Chercher yitix\'kil fond rivière. Sud !' WHERE entry=2000000674; /* content: [Furbolg] Go... Search yitix\'kil bottom river. South! */
UPDATE db_script_string SET content_loc2='[Furbolg] Venez, $n. Sortons ensemble et purifiés de l\'eau.' WHERE entry=2000000675; /* content: [Furbolg] Come, $n. Let us leave the water together, purified. */
UPDATE db_script_string SET content_loc2='Avez-vous libéré Drull ? Je ne sais pas où il est détenu.' WHERE entry=2000000676; /* content: Have you freed Drull yet? I don\'t know where he\'s being held. */
UPDATE db_script_string SET content_loc2='[Furbolg] Nous partons maintenant, ensemble. À la recherche de Vark.' WHERE entry=2000000676; /* content: [Furbolg] We go now, together. We will seek Vark. */
UPDATE db_script_string SET content_loc2='Je ne ferai que vous ralentir, mais ce ne sont pas des humains au teint porcin qui m\'empêcheront de recouvrer ma liberté !' WHERE entry=2000000677; /* content: I will only slow you down, but no pink-skinned humans will keep me from freedom! */
UPDATE db_script_string SET content_loc2='[Furbolg] Suivez-moi vers Vark.' WHERE entry=2000000677; /* content: [Furbolg] Follow me to Vark. */
UPDATE db_script_string SET content_loc2='[Furbolg] Vite, $n. Vark vous attend.' WHERE entry=2000000678; /* content: [Furbolg] Hurry, $n. Vark awaits you. */
UPDATE db_script_string SET content_loc2='[Furbolg] Merci, $n !' WHERE entry=2000000680; /* content: [Furbolg] Thank you, $n! */
UPDATE db_script_string SET content_loc2='[Furbolg] La prophétie est vraie !' WHERE entry=2000000681; /* content: [Furbolg] The prophecy is true! */
UPDATE db_script_string SET content_loc2='[Furbolg] Les furbolgs Calmepins n\'oublieront pas votre bravoure de sitôt !' WHERE entry=2000000682; /* content: [Furbolg] The Stillpine furbolgs will not soon forget your bravery! */
UPDATE db_script_string SET content_loc2='[Furbolg] Ceux qui vivent encore au repaire des Calmepins vous accueilleront en héros !' WHERE entry=2000000683; /* content: [Furbolg] Those remaining at Stillpine Hold will welcome you as a hero! */
UPDATE db_script_string SET content_loc2='Adamant, auriez-vous l\'amabilité de jeter un œil sur cette tablette et d\'en déchiffrer les runes ?' WHERE entry=2000000691; /* content: Adamant, could you please take a look at this tablet and try to decipher the runes? */
UPDATE db_script_string SET content_loc2='Mais naturellement, tout ce que vous voudrez, chérie !' WHERE entry=2000000692; /* content: Of course! Anything for you, darling! */
UPDATE db_script_string SET content_loc2='Très bien, laissez-moi jeter un œil sur cette tablette.' WHERE entry=2000000693; /* content: Alright, let me take a look at that tablet. */
UPDATE db_script_string SET content_loc2='%s lit lentement la tablette, en articulant chaque mot dans leur langue d\'origine.' WHERE entry=2000000694; /* content: %s slowly reads the tablet, mouthing the words in their native tongue. */
UPDATE db_script_string SET content_loc2='Un chef de guerre naga ! Diantre...' WHERE entry=2000000695; /* content: Naga warlord! Oh dear... */
UPDATE db_script_string SET content_loc2='Eh bien, vous n\'allez pas aimer ce que j\'ai à vous dire, jeune demoiselle. Cette tablette est un courrier naga. Leur dirigeant, le chef de guerre Sriss\'tiz, a donné l\'ordre à ses troupes de prendre le reste de l\'île par la force.' WHERE entry=2000000696; /* content: Well, you aren\'t going to like what I have to tell you, young missy. This tablet is a naga communication. Their leader, Warlord Sriss\'tiz, has ordered his naga to take the rest of the island by force. */
UPDATE db_script_string SET content_loc2='Et on dirait qu\'il a des renforts qui arrivent de Nazjatar !' WHERE entry=2000000697; /* content: And it looks like he\'s got reinforcements comin\' from Nazjatar! */
UPDATE db_script_string SET content_loc2='Nous devons intervenir !' WHERE entry=2000000698; /* content: We\'ve got to do something about this! */
UPDATE db_script_string SET content_loc2='Merci, Adamant. Je crois connaître la personne idéale pour ce travail.' WHERE entry=2000000699; /* content: Thank you, Adamant. I think I know just the person for the job. */
UPDATE db_script_string SET content_loc2='Parlez-moi pour écouter mon histoire.' WHERE entry=2000000813; /* content: Speak with me to hear my tale. */
UPDATE db_script_string SET content_loc2='Vous ne regardez jamais votre courrier ?' WHERE entry=2000000827; /* content: Don\'t you people ever check the mail? */
UPDATE db_script_string SET content_loc2='Et comment suis-je censé remettre tous ces messages ?' WHERE entry=2000000828; /* content: How am I expected to deliver all of these messages? */
UPDATE db_script_string SET content_loc2='Il faut que je me fasse muter avant que ce boulot ne me fasse complètement craquer…' WHERE entry=2000000829; /* content: I need to be re-assigned. This job is pushing me over the edge... */
UPDATE db_script_string SET content_loc2='Il s\'imagine qu\'il est important. Il n\'arrête pas de me bousculer… Je lui montrerai. Je leur montrerai à tous !' WHERE entry=2000000830; /* content: He thinks he\'s so damned important. Always pushing me around... I\'ll show him. I\'ll show all of them! */
UPDATE db_script_string SET content_loc2='Si seulement je pouvais retourner dans le fourré aux champignons. Je n\'avais jamais vu un aussi beau cercle de ma vie !' WHERE entry=2000000831; /* content: If only I could get back to the mushroom thicket. I have not seen such a mature cluster in my lifetime! */
UPDATE db_script_string SET content_loc2='Oh non ! Je le perds !' WHERE entry=2000001010; /* content: Oh no!  I\'m losing this one! */
UPDATE db_script_string SET content_loc2='Opus veut l\'or de Cuergo... avec des vers.' WHERE entry=2000005001; /* content: Opus wants some Cuergo\'s Gold... with worm. */
UPDATE db_script_string SET content_loc2='La SgPA mène une enquête sur Kibler.' WHERE entry=2000005003; /* content: Kibler is being investigated by G.E.T.A. */
UPDATE db_script_string SET content_loc2='Bijou sait où les trésors sont cachés.' WHERE entry=2000005004; /* content: Bijou knows where treasures are hidden. */
UPDATE db_script_string SET content_loc2='Des dragons contrôlant des orcs !? GROA !' WHERE entry=2000005005; /* content: Dragons controlling orcs!? SQUAWK! */
UPDATE db_script_string SET content_loc2='Demandez à Kibler pour le trésor ! GROA !' WHERE entry=2000005007; /* content: Ask Kibler about the treasure! SQUAWK! */
UPDATE db_script_string SET content_loc2='GROA !! *tousse* *tousse* Faut que j\'arrête de fumer.' WHERE entry=2000005008; /* content: SQUAWK!! *cough* *cough* I need to quit smoking! */
UPDATE db_script_string SET content_loc2='Bien joué, soldats. Repos !' WHERE entry=2000005009; /* content: Well done, soldiers! At ease! */
UPDATE db_script_string SET content_loc2='Zog zog ! Content de rendre service !' WHERE entry=2000005010; /* content: Zug zug! Happy to help! */
UPDATE db_script_string SET content_loc2='De mystérieuses empreintes de sabots, un bouclier brûlé, des insignes égarés… comment donner un sens à tout ça ?' WHERE entry=2000005012; /* content: Mysterious hoofprints, scorched shields, stray badges... how do we make sense of all this? */
UPDATE db_script_string SET content_loc2='Je ne pense pas que Drazzit réussira à trouver quelqu\'un pour tuer son raptor. Ça fait quand même deux fois qu\'il augmente la récompense…' WHERE entry=2000005013; /* content: I don\'t think Drazzit\'s going to have any luck finding anyone to kill this raptor. I mean, he\'s already raised the reward twice... */
UPDATE db_script_string SET content_loc2='Mais c\'est le seul à la taille de Brogg ! Gobelins construire trop petit !' WHERE entry=2000005014; /* content: But this the only building that fit Brogg! Goblins make buildings too small! */
UPDATE db_script_string SET content_loc2='Brogg, on en a déjà parlé. Tu ne peux pas rester dans ce bâtiment. Bien sûr, il est à ta taille, mais il n\'y a plus de place pour rien d\'autre !' WHERE entry=2000005015; /* content: I thought we talked about this, Brogg. You can\'t stay in this building. Sure, it can house you, but there\'s no room for anyone else! */
UPDATE db_script_string SET content_loc2='Oui, on a dit que tu pouvais rester, mais ce bâtiment est notre quartier général. C\'est là qu\'on garde tous nos manuels et tous nos plans !' WHERE entry=2000005016; /* content: Yes, we said you can stay here, but this building is our headquarters! All of our manuals and blueprints are stored here! */
UPDATE db_script_string SET content_loc2='Question de point de vue. Je pense que quelqu\'un a construit les ogres trop grands. Bon, je ne vais pas te convaincre de bouger, mais ne t\'imagine pas que c\'est définitif !' WHERE entry=2000005017; /* content: I\'m inclined to say they made ogres too big! I can see I\'m not going to convince you to move now, but don\'t think this is permanent! */
UPDATE db_script_string SET content_loc2='Vous devriez reculer. C\'est salissant, de vider des poissons.' WHERE entry=2000005019; /* content: You might wanna stand back. Fish guttin\' is a dirty job. */
UPDATE db_script_string SET content_loc2='C\'est bon de voir que vous êtes fier de défendre Theramore, mon ami.' WHERE entry=2000005020; /* content: It\'s good to see you defending Theramore with pride, friend. */
UPDATE db_script_string SET content_loc2='Allons à l\'auberge lorsque vous ne serez plus de service. Je vous payerai un verre et nous pourrons nous remémorer le bon vieux temps.' WHERE entry=2000005021; /* content: When you\'re off duty, let\'s go to the inn. I\'ll buy a round and we can reminisce about the good old days. */
UPDATE db_script_string SET content_loc2='Venez me voir, et ce qui vous semblait nébuleux prendra la clarté du cristal.' WHERE entry=2000005026; /* content: Come speak with me, and what once was cloudy shall become crystal clear. */
UPDATE db_script_string SET content_loc2='Nous commencerons demain l\'entraînement de nos dragons les plus prometteurs, n\'oubliez pas de leur amener des jouets à ronger.' WHERE entry=2000005028; /* content: Tomorrow we will begin training of our promising dragons, so don\'t forget your chew toys. */
UPDATE db_script_string SET content_loc2='Je viens des terres souterraines... Là où les femmes sont malades et les hommes en décomposition...' WHERE entry=2000005031; /* content: I come from the land down under... where women plague and men sunder. */
UPDATE db_script_string SET content_loc2='Il s\'enfuit !' WHERE entry=2000005035; /* content: It\'s getting away! */
UPDATE db_script_string SET content_loc2='Il se pourrait que je décide d\'aller chasser ce Déshérité bestial, maintenant que sa tête est mise à prix.' WHERE entry=2000005037; /* content: I might just have to go hunting for that Wretched beast now that there\'s a bounty on his head. */
UPDATE db_script_string SET content_loc2='Je suis partante pour aller en chasse après ce tour de garde.' WHERE entry=2000005046; /* content: I\'m up to looking into that bounty after this shift. */
UPDATE db_script_string SET content_loc2='Vous vous amusez bien ?' WHERE entry=2000005048; /* content: Having a good time? */
UPDATE db_script_string SET content_loc2='J\'ai un autre relevé du néant.' WHERE entry=2000005049; /* content: I have another reading from the nether. */
UPDATE db_script_string SET content_loc2='Par le grand Gazleu !' WHERE entry=2000005051; /* content: Great Gazlowe! */
