UPDATE script_texts SET content_loc2='Imbéciles heureux ! Le temps est le brasier dans lequel vous brûlerez !' WHERE entry=-1532092; /* content: Simple fools! Time is the fire in which you\'ll burn! */
UPDATE script_texts SET content_loc2='Vous n\'avez quand même pas pensé que vous pouviez gagner.' WHERE entry=-1532097; /* content: Surely you did not think you could win. */
UPDATE script_texts SET content_loc2='Votre avidité, votre folie ont provoqué votre fin.' WHERE entry=-1532098; /* content: Your greed, your foolishness has brought you to this end. */
