UPDATE script_texts SET content_loc2='Je n\'ai pas le choix je dois obéir !' WHERE entry=-1533062; /* content: I- I have no choice but to obey! */
UPDATE script_texts SET content_loc2='Pardonnez-moi !' WHERE entry=-1533063; /* content: Forgive me! */
UPDATE script_texts SET content_loc2='C\'est... comme cela doit être.' WHERE entry=-1533064; /* content: It is... as it should be. */
UPDATE script_texts SET content_loc2='Vous cherchez la mort ?' WHERE entry=-1533065; /* content: You seek death? */
UPDATE script_texts SET content_loc2='On ne passe pas !' WHERE entry=-1533066; /* content: None shall pass! */
UPDATE script_texts SET content_loc2='Ne bougez pas !' WHERE entry=-1533067; /* content: Be still! */
