UPDATE script_texts SET content_loc2='À genoux devant la puissance du Fléau !' WHERE entry=-1533070; /* content: Bow to the might of the Highlord! */
UPDATE script_texts SET content_loc2='À genoux devant la puissance du Fléau !' WHERE entry=-1533070; /* content: Bow to the might of the scourge! */
