UPDATE script_texts SET content_loc2='Conservez votre colère ! Guidez votre rage ! Vous pourrez bientôt ventiler votre frustration !' WHERE entry=-1533072; /* content: Conserve your anger! Harness your rage! You will all have outlets for your frustration soon enough. */
