UPDATE script_texts SET content_loc2='Levez vous, soldats ! Levez vous et combattez une fois encore !' WHERE entry=-1533078; /* content: Rise, my soldiers! Rise and fight once more! */
UPDATE script_texts SET content_loc2='Ma tâche est accomplie !' WHERE entry=-1533079; /* content: My task is done! */
UPDATE script_texts SET content_loc2='Expire !' WHERE entry=-1533080; /* content: Breathe no more! */
UPDATE script_texts SET content_loc2='Je servirai le maître... dans... la mort !' WHERE entry=-1533081; /* content: I will serve the master... in... death! */
