UPDATE script_texts SET content_loc2='Il reste plus beaucoup d\'temps, étrangers. Bientôt, z\'allez rejoindre les âmes de ceux que z\'avez pas pu sauver.' WHERE entry=-1568074; /* content: Time be running low, strangers. Soon you gonna join da souls of dem ya failed to save. */
UPDATE script_texts SET content_loc2='Plus vite, mes prêtres ! Les rituels, faut pas qu\'ils soient interrompus !' WHERE entry=-1568075; /* content: Make haste, ma priests! Da rituals must not be interrupted! */
