UPDATE script_texts SET content_loc2='Indestructible.' WHERE entry=-1576008; /* content: Indestructible. */
UPDATE script_texts SET content_loc2='Bien sûr.' WHERE entry=-1576010; /* content: Of course. */
UPDATE script_texts SET content_loc2='Noon !' WHERE entry=-1576011; /* content: Noo! */
UPDATE script_texts SET content_loc2='Aargh !' WHERE entry=-1576015; /* content: Aaggh! */
UPDATE script_texts SET content_loc2='La préserver ? Pourquoi ? La vérité n\'est pas là. Non, non, non, non... seulement dans l\'emprise ! Je le vois maintenant !' WHERE entry=-1576016; /* content: Preserve? Why? There\'s no truth in it. No no no... only in the taking! I see that now! */
UPDATE script_texts SET content_loc2='Restez. Profitez de vos derniers instants.' WHERE entry=-1576017; /* content: Stay. Enjoy your final moments. */
UPDATE script_texts SET content_loc2='Finissons-en ! Finissons-en ! Tuez-moi ou je jure sur la reine-dragon que vous ne verrez plus jamais la lumière du jour !' WHERE entry=-1576018; /* content: Finish it! Finish it! Kill me, or I swear by the Dragonqueen you\'ll never see daylight again! */
UPDATE script_texts SET content_loc2='Voilà enfin la vérité !' WHERE entry=-1576019; /* content: Now we\'ve come to the truth! */
UPDATE script_texts SET content_loc2='Reine-dragon... Lieuse-de-vie... préserve... moi.' WHERE entry=-1576020; /* content: Dragonqueen... Life-Binder... preserve... me. */
UPDATE script_texts SET content_loc2='%s ouvre une faille chaotique !' WHERE entry=-1576021; /* content: %s opens a Chaotic Rift! */
UPDATE script_texts SET content_loc2='Mes familiers ont visiblement échoué. Peut-être qu\'une autre démonstration s\'impose.' WHERE entry=-1578001; /* content: Clearly my pets failed. Perhaps another demonstration is in order. */
UPDATE script_texts SET content_loc2='Encore et toujours, vous luttez. Encore et toujours, vous vous raccrochez à des principes fourvoyés. Si vous survivez, vous me trouverez dans l\'anneau central.' WHERE entry=-1578002; /* content: Still you fight. Still you cling to misguided principles. If you survive, you\'ll find me in the center ring. */
UPDATE script_texts SET content_loc2='Pauvres crétins aveugles !' WHERE entry=-1578003; /* content: Poor blind fools! */
UPDATE script_texts SET content_loc2='Une prémice... à peine un avant-goût... de la puissance du Tisse-sort !' WHERE entry=-1578004; /* content: A taste... just a small taste... of the Spell-Weaver\'s power! */
UPDATE script_texts SET content_loc2='Tant d\'énergie si instable... Mais le jeu en vaut la chandelle, pour vous détruire !' WHERE entry=-1578005; /* content: So much unstable energy... but worth the risk to destroy you! */
UPDATE script_texts SET content_loc2='Si seulement vous compreniez !' WHERE entry=-1578006; /* content: If only you understood! */
UPDATE script_texts SET content_loc2='Voyez-vous, maintenant ? Voyez-vous ?!' WHERE entry=-1578007; /* content: Now do you see? Do you?! */
UPDATE script_texts SET content_loc2='Malheureux, mais nécessaire.' WHERE entry=-1578008; /* content: Unfortunate, but necessary. */
UPDATE script_texts SET content_loc2='Ahuris ! Vous ne comprenez rien aux forces que vous avez libérées. Le couloir tellurique ne peut pas être barré ! Votre défaite sera absolue !' WHERE entry=-1578010; /* content: Simpletons! You cannot comprehend the forces you have set in motion. The ley line conduit will not be disrupted! Your defeat shall be absolute! */
UPDATE script_texts SET content_loc2='Bravaches importuns, vous n\'êtes pas dans votre élément ! Je vais vous mettre à terre !' WHERE entry=-1578011; /* content: You brash interlopers are out of your element! I will ground you! */
UPDATE script_texts SET content_loc2='Éteindre votre feu est enfantin !' WHERE entry=-1578013; /* content: It is trivial to extinguish your fire! */
UPDATE script_texts SET content_loc2='La magie de la nature ne peut plus rien pour vous !' WHERE entry=-1578014; /* content: No magic of nature will help you now! */
UPDATE script_texts SET content_loc2='Tant d\'insolence... tant d\'arrogance... doivent être PUNIES !' WHERE entry=-1578015; /* content: Such insolence... such arrogance... must be PUNISHED! */
UPDATE script_texts SET content_loc2='Retournez à la terre !' WHERE entry=-1578017; /* content: Back to the earth with you! */
UPDATE script_texts SET content_loc2='Bonne descente !' WHERE entry=-1578018; /* content: Enjoy the fall! */
UPDATE script_texts SET content_loc2='Il n\'y aura pas de pitié !' WHERE entry=-1578020; /* content: There will be no mercy! */
UPDATE script_texts SET content_loc2='Explosez-les ! Détruisez-les !' WHERE entry=-1578021; /* content: Blast them! Destroy them! */
UPDATE script_texts SET content_loc2='Pas de prisonniers ! À l\'attaque !' WHERE entry=-1578022; /* content: Take no prisoners! Attack! */
UPDATE script_texts SET content_loc2='Frappez maintenant ! Éradiquez-les !' WHERE entry=-1578023; /* content: Strike now! Obliterate them! */
UPDATE script_texts SET content_loc2='Des anomalies apparaissent au passage |2 %s dans le plan astral !' WHERE entry=-1578024; /* content: Anomalies form as %s shifts into the Astral Plane! */
UPDATE script_texts SET content_loc2='%s incante une explosion des Arcanes surpuissante !' WHERE entry=-1578025; /* content: %s begins to cast Empowered Arcane Explosion! */
UPDATE script_texts SET content_loc2='Vous étiez prévenus !' WHERE entry=-1578026; /* content: You were warned! */
UPDATE script_texts SET content_loc2='L\'Oculus est à nous !' WHERE entry=-1578027; /* content: The Oculus is ours! */
UPDATE script_texts SET content_loc2='Ils sont... trop forts ! Sous-estimé leur... résistance.' WHERE entry=-1578028; /* content: They are... too strong! Underestimated their... fortitude. */
UPDATE script_texts SET content_loc2='%s appelle un capitaine de l\'anneau azur !' WHERE entry=-1578029; /* content: %s calls an Azure Ring Captain! */
UPDATE script_texts SET content_loc2='%s s\'envole.' WHERE entry=-1578030; /* content: %s flies away. */
UPDATE script_texts SET content_loc2='Vous allez expier !' WHERE entry=-1580001; /* content: I will purge you! */
UPDATE script_texts SET content_loc2='La souffrance ne fait que commencer !' WHERE entry=-1580002; /* content: Your pain has only begun! */
UPDATE script_texts SET content_loc2='Au nom de Kil\'jaeden !' WHERE entry=-1580003; /* content: In the name of Kil\'jaeden! */
UPDATE script_texts SET content_loc2='Vous étiez prévenus !' WHERE entry=-1580004; /* content: You were warned! */
UPDATE script_texts SET content_loc2='Je suis enfin réveillé ! Vous allez tous mourir !' WHERE entry=-1580005; /* content: My awakening is complete! You shall all perish! */
UPDATE script_texts SET content_loc2='Vite ! C\'est en train de m\'emporter !' WHERE entry=-1580008; /* content: Hurry! There is not much of me left! */
UPDATE script_texts SET content_loc2='Je vous suis redevable à jamais. Et lorsque nous aurons triomphé de Kil\'jaeden, le monde entier vous sera redevable également.' WHERE entry=-1580009; /* content: I am forever in your debt. Once we have triumphed over Kil\'jaeden, this entire world will be in your debt as well. */
UPDATE script_texts SET content_loc2='Je me délecte de votre détresse !' WHERE entry=-1580012; /* content: Your misery is my delight! */
UPDATE script_texts SET content_loc2='Que coule votre sang !' WHERE entry=-1580013; /* content: I will watch you bleed! */
UPDATE script_texts SET content_loc2='On ne vous avait pas dit ? Je gagne toujours !' WHERE entry=-1580015; /* content: Haven\'t you heard? I always win! */
UPDATE script_texts SET content_loc2='J\'ai assez joué avec vous !' WHERE entry=-1580016; /* content: I have toyed with you long enough! */
UPDATE script_texts SET content_loc2='Misérable lézard ! La mort est la seule réponse que tu trouveras ici !' WHERE entry=-1580017; /* content: Puny lizard! Death is the only answer you\'ll find here! */
UPDATE script_texts SET content_loc2='Je vais vous écraser !' WHERE entry=-1580019; /* content: I will crush you! */
UPDATE script_texts SET content_loc2='C\'était marrant, mais j\'attends toujours un réel défi !' WHERE entry=-1580020; /* content: That was fun, but I still await a true challenge! */
UPDATE script_texts SET content_loc2='Meurs, insecte !' WHERE entry=-1580023; /* content: Perish, insect! */
UPDATE script_texts SET content_loc2='Viande froide !' WHERE entry=-1580024; /* content: You are meat! */
UPDATE script_texts SET content_loc2='Trop facile !' WHERE entry=-1580025; /* content: Too easy! */
UPDATE script_texts SET content_loc2='Donnez-moi de l\'action !' WHERE entry=-1580026; /* content: Bring the fight to me! */
UPDATE script_texts SET content_loc2='À chaque jour son glorieux combat !' WHERE entry=-1580027; /* content: Another day, another glorious battle! */
UPDATE script_texts SET content_loc2='Ah, ça j\'aime !' WHERE entry=-1580028; /* content: I live for this! */
UPDATE script_texts SET content_loc2='Attendez, mes amis ! Ce démon doit nous renseigner avant de recevoir son dû !' WHERE entry=-1580031; /* content: Hold, friends! There is information to be had before this devil meets his fate! */
UPDATE script_texts SET content_loc2='Où est Anveena, démon ? Qu\'est-il advenu de Kalec ?' WHERE entry=-1580032; /* content: Where is Anveena, demon? What has become of Kalec? */
UPDATE script_texts SET content_loc2='Tu vas me dire où ils sont !' WHERE entry=-1580033; /* content: You will tell me where they are! */
UPDATE script_texts SET content_loc2='Gloire à Kil\'jaeden ! Mort aux opposants !' WHERE entry=-1580036; /* content: Glory to Kil\'jaeden! Death to all who oppose! */
UPDATE script_texts SET content_loc2='Je tue pour le maître !' WHERE entry=-1580037; /* content: I kill for the master! */
UPDATE script_texts SET content_loc2='Chaos…' WHERE entry=-1580049; /* content: Chaos... */
UPDATE script_texts SET content_loc2='L\'ombre au secours du feu !' WHERE entry=-1580052; /* content: Shadow to the aid of fire! */
UPDATE script_texts SET content_loc2='Alythess ! Ton feu brûle en moi !' WHERE entry=-1580053; /* content: Alythess! Your fire burns within me! */
UPDATE script_texts SET content_loc2='Ee-nok Kryul !!' WHERE entry=-1580055; /* content: Ee-nok Kryul! */
UPDATE script_texts SET content_loc2='Je… m\'estompe.' WHERE entry=-1580056; /* content: I... fade. */
UPDATE script_texts SET content_loc2='Le temps est un luxe qui ne vous est plus accessible.' WHERE entry=-1580057; /* content: Time is a luxury you no longer possess! */
UPDATE script_texts SET content_loc2='Le feu au secours de l\'ombre !' WHERE entry=-1580058; /* content: Fire to the aid of shadow! */
UPDATE script_texts SET content_loc2='Sacrocingle !' WHERE entry=-1580059; /* content: Sacrolash! */
UPDATE script_texts SET content_loc2='Votre chance est passée !' WHERE entry=-1580063; /* content: Your luck has run its course! */
UPDATE script_texts SET content_loc2='Tous mes plans aboutissent ici !' WHERE entry=-1580064; /* content: All my plans have led to this! */
UPDATE script_texts SET content_loc2='J\'ai assez attendu !' WHERE entry=-1580066; /* content: I have waited long enough! */
UPDATE script_texts SET content_loc2='Échouez et vous souffrirez pour l\'éternité !' WHERE entry=-1580067; /* content: Fail me and suffer for eternity! */
UPDATE script_texts SET content_loc2='Drainez la fille ! Drainez toute sa puissance, qu\'il n\'en reste qu\'une coquille vide !' WHERE entry=-1580068; /* content: Drain the girl! Drain her power until there is nothing but a vacant shell! */
UPDATE script_texts SET content_loc2='CHAOS !' WHERE entry=-1580074; /* content: Chaos! */
UPDATE script_texts SET content_loc2='DESTRUCTION !' WHERE entry=-1580075; /* content: Destruction! */
UPDATE script_texts SET content_loc2='OUBLI !' WHERE entry=-1580076; /* content: Oblivion! */
UPDATE script_texts SET content_loc2='Rien ne m\'arrêtera ! Ce monde va tomber !' WHERE entry=-1580077; /* content: I will not be denied! This world shall fall! */
UPDATE script_texts SET content_loc2='Arggghhh ! Les pouvoirs du Puits de soleil… se retournent... contre moi ! Qu\'avez-vous fait ? Qu\'avez-vous fait ??' WHERE entry=-1580079; /* content: Aggghh! The powers of the Sunwell... turn... against me! What have you done? What have you done??? */
UPDATE script_texts SET content_loc2='Je ne sers plus que le maître.' WHERE entry=-1580082; /* content: I serve only the Master now. */
UPDATE script_texts SET content_loc2='Mais... je suis perdue. Je ne trouve plus mon chemin.' WHERE entry=-1580084; /* content: But I\'m... lost. I cannot find my way back. */
UPDATE script_texts SET content_loc2='Kalec... Kalec ?' WHERE entry=-1580086; /* content: Kalec... Kalec? */
UPDATE script_texts SET content_loc2='J\'ai chargé un autre orbe ! Utilisez-le vite !' WHERE entry=-1580092; /* content: I have empowered another orb! Use it quickly! */
UPDATE script_texts SET content_loc2='Un autre orbe est prêt ! Hâtez-vous !' WHERE entry=-1580093; /* content: Another orb is ready! Make haste! */
UPDATE script_texts SET content_loc2='J\'ai envoyé tout ce que je pouvais ! La puissance est entre vos mains !' WHERE entry=-1580094; /* content: I have channeled all I can! The power is in your hands! */
UPDATE script_texts SET content_loc2='Héroïques mortels, votre présente victoire fut prophétisée il y a longtemps. Le cri d\'angoisse de mon frère vaincu va résonner dans tout l\'univers, apportant un nouvel espoir à tous ceux qui affrontent encore la Croisade ardente.' WHERE entry=-1580095; /* content: Mortal heroes - your victory here today was foretold long ago. My brother\'s anguished cry of defeat will echo across the universe - bringing renewed hope to all those who still stand against the Burning Crusade. */
UPDATE script_texts SET content_loc2='La créature Entropius, que vous avez dû détruire, était autrefois le noble M\'uru - un naaru qui de son vivant canalisait d\'intenses énergies de LUMIÈRE et d\'ESPOIR. Pendant un temps, certaines âmes égarées ont cherché à dérober ces énergies…' WHERE entry=-1580098; /* content: The creature Entropius, whom you were forced to destroy, was once the noble naaru, M\'uru. In life, M\'uru channeled vast energies of LIGHT and HOPE. For a time, a misguided few sought to steal those energies... */
UPDATE script_texts SET content_loc2='Notre arrogance a été impardonnable. Nous avons damné l\'un des êtres les plus nobles qui soient. Nous ne pourrons jamais expier ce péché.' WHERE entry=-1580099; /* content: Our arrogance was unpardonable. We damned one of the most noble beings of all. We may never atone for this sin. */
UPDATE script_texts SET content_loc2='Ancêtres bénis ! Je le sens… tant d\'amour… tant de grâce… Il n\'y a… pas de mots… c\'est indescriptible…' WHERE entry=-1580104; /* content: Blessed ancestors! I feel it... so much love... so much grace... there are... no words... impossible to describe... */
UPDATE script_texts SET content_loc2='Le salut, jeune fille. Il nous attend tous.' WHERE entry=-1580105; /* content: Salvation, young one. It waits for us all. */
UPDATE script_texts SET content_loc2='%s inspire profondément.' WHERE entry=-1580107; /* content: %s takes a deep breath. */
UPDATE script_texts SET content_loc2='Vous me faites perdre mon temps !' WHERE entry=-1585000; /* content: You only waste my time! */
UPDATE script_texts SET content_loc2='Ma faim n\'a pas de limite !' WHERE entry=-1585001; /* content: My hunger knows no bounds! */
UPDATE script_texts SET content_loc2='Assez de distractions !' WHERE entry=-1585003; /* content: Enough distractions! */
UPDATE script_texts SET content_loc2='Je suis invincible !' WHERE entry=-1585004; /* content: I am invincible! */
UPDATE script_texts SET content_loc2='Ir… ré… pressible.' WHERE entry=-1585008; /* content: Un...con...tainable. */
UPDATE script_texts SET content_loc2='Con… sumer.' WHERE entry=-1585010; /* content: Con...sume. */
UPDATE script_texts SET content_loc2='Là, ça commence à m\'exaspérer.' WHERE entry=-1585015; /* content: Now I\'m getting annoyed. */
UPDATE script_texts SET content_loc2='C\'est ce que j\'appelle un bon début.' WHERE entry=-1585017; /* content: I call that a good start. */
UPDATE script_texts SET content_loc2='Que ce nombre « un » est solitaire.' WHERE entry=-1585020; /* content: One is such a lonely number. */
UPDATE script_texts SET content_loc2='C\'était très sympa, vraiment.' WHERE entry=-1585021; /* content: It\'s been a kick, really. */
UPDATE script_texts SET content_loc2='La vengeance brûle !' WHERE entry=-1585024; /* content: Vengeance burns! */
UPDATE script_texts SET content_loc2='Que tous les soldats de Lordaeron se présentent immédiatement à l\'entrée de Stratholme pour y attendre les nouveaux ordres du prince Arthas.' WHERE entry=-1595000; /* content: All soldiers of Lordaeron should immediately report to the entrance of Stratholme, and await further orders from Prince Arthas. */
UPDATE script_texts SET content_loc2='Vous avez bien travaillé pour les caisses ! Venez me voir devant l\'entrée de Stratholme pour recevoir votre prochaine mission !' WHERE entry=-1595001; /* content: Good work with the crates! Come talk to me in front of Stratholme for your next assignment! */
UPDATE script_texts SET content_loc2='Des créatures du Fléau ont été aperçues près de la porte de l\'allée du Festival !' WHERE entry=-1595003; /* content: Scourge forces have been spotted near the Festival Lane Gate! */
UPDATE script_texts SET content_loc2='Des créatures du Fléau ont été aperçues près de la fontaine de la place du Roi !' WHERE entry=-1595004; /* content: Scourge forces have been spotted near the King\'s Square fountain! */
UPDATE script_texts SET content_loc2='Des créatures du Fléau ont été aperçues près de la porte de l\'allée du Marché !' WHERE entry=-1595005; /* content: Scourge forces have been spotted near the Market Row Gate! */
UPDATE script_texts SET content_loc2='Des créatures du Fléau ont été aperçues près de l\'Hôtel de ville !' WHERE entry=-1595006; /* content: Scourge forces have been spotted near the Town Hall! */
UPDATE script_texts SET content_loc2='Des créatures du Fléau ont été aperçues près de la porte de la place des Anciens !' WHERE entry=-1595007; /* content: Scourge forces have been spotted near the Elder\'s Square Gate! */
UPDATE script_texts SET content_loc2='Champions, retrouvez-moi tout de suite à l\'hôtel de ville. Nous devons prendre l\'initiative à Mal\'Ganis.' WHERE entry=-1595008; /* content: Champions, meet me at the Town Hall at once. We must take the fight to Mal\'Ganis. */
UPDATE script_texts SET content_loc2='Suivez-moi, je connais un passage.' WHERE entry=-1595009; /* content: Follow me, I know the way through. */
UPDATE script_texts SET content_loc2='Ah, vous voilà enfin, prince Arthas. Vous tombez à pic.' WHERE entry=-1595010; /* content: Ah, you\'ve finally arrived Prince Arthas. You\'re here just in the nick of time. */
UPDATE script_texts SET content_loc2='Oui, je suis heureux d\'avoir pu vous trouver avant la maladie.' WHERE entry=-1595011; /* content: Yes, I\'m glad I could get to you before the plague. */
UPDATE script_texts SET content_loc2='Quel est ce sortilège ?' WHERE entry=-1595012; /* content: What is this sorcery? */
UPDATE script_texts SET content_loc2='Vous n\'avez pas besoin de comprendre, Arthas. Tout ce que vous avez à faire, c\'est mourir.' WHERE entry=-1595013; /* content: There\'s no need for you to understand, Arthas. All you need to do is die. */
UPDATE script_texts SET content_loc2='Mal\'Ganis semble avoir plus que le fléau dans son arsenal. Nous devons nous hâter.' WHERE entry=-1595014; /* content: Mal\'Ganis appears to have more than Scourge in his arsenal. We should make haste. */
UPDATE script_texts SET content_loc2='Encore de la sorcellerie ! Soyez prêts à tout !' WHERE entry=-1595015; /* content: More vile sorcery! Be ready for anything! */
UPDATE script_texts SET content_loc2='Passons au suivant.' WHERE entry=-1595016; /* content: Let\'s move on. */
UPDATE script_texts SET content_loc2='Surveillez vos arrières ! Ici, nous sommes encerclés.' WHERE entry=-1595017; /* content: Watch your backs: they have us surrounded in this hall. */
UPDATE script_texts SET content_loc2='Mal\'Ganis ne nous facilite pas la tâche.' WHERE entry=-1595018; /* content: Mal\'Ganis is not making this easy. */
UPDATE script_texts SET content_loc2='Ils sont très tenaces.' WHERE entry=-1595019; /* content: They\'re very persistent. */
UPDATE script_texts SET content_loc2='Que va-t-il encore mettre en travers de mon chemin ?' WHERE entry=-1595020; /* content: What else can he put in my way? */
UPDATE script_texts SET content_loc2='Prince Arthas Menethil, en ce jour, votre âme est la proie de puissantes ténèbres. En ce jour, cette mort que vous êtes destiné à répandre autour de vous va devenir la vôtre.' WHERE entry=-1595021; /* content: Prince Arthas Menethil, on this day, a powerful darkness has taken hold of your soul. The death you are destined to visit upon others will this day be your own. */
UPDATE script_texts SET content_loc2='Je fais ce que je dois faire pour Lordaeron, et je ne me laisserai arrêter ni par vos paroles ni par vos actions.' WHERE entry=-1595022; /* content: I do what I must for Lordaeron, and neither your words nor your actions will stop me. */
UPDATE script_texts SET content_loc2='Le chemin le plus rapide pour aller jusqu\'à Mal\'Ganis se trouve derrière cette bibliothèque.' WHERE entry=-1595023; /* content: The quickest path to Mal\'Ganis lies behind that bookshelf ahead. */
UPDATE script_texts SET content_loc2='Ça ne prendra qu\'un instant.' WHERE entry=-1595024; /* content: This will only take a moment. */
UPDATE script_texts SET content_loc2='Je suis soulagé de voir que ce passage secret fonctionne encore.' WHERE entry=-1595025; /* content: I\'m relieved this secret passage still works. */
UPDATE script_texts SET content_loc2='Passons par là aussi vite que possible. Si les morts-vivants ne nous tuent pas, le feu pourrait y parvenir.' WHERE entry=-1595026; /* content: Let\'s move through here as quickly as possible. If the undead don\'t kill us, the fires might. */
UPDATE script_texts SET content_loc2='Reposez-vous un instant pour reprendre votre souffle, mais nous devrons repartir bientôt.' WHERE entry=-1595027; /* content: Rest a moment and clear your lungs, but we must move again soon. */
UPDATE script_texts SET content_loc2='Ça suffit, il faut y aller. Mal\'Ganis nous attend.' WHERE entry=-1595028; /* content: That\'s enough; we must move again. Mal\'Ganis awaits. */
UPDATE script_texts SET content_loc2='Enfin un coup de chance. L\'allée du Marché n\'a pas encore pris feu. Mal\'Ganis est censé être à la place des Croisés, droit devant nous. Dites-moi quand vous serez prêts à avancer.' WHERE entry=-1595029; /* content: At last some good luck. Market Row has not caught fire yet. Mal\'Ganis is supposed to be in Crusaders\' Square, which is just ahead. Tell me when you\'re ready to move forward. */
UPDATE script_texts SET content_loc2='Justice sera faite.' WHERE entry=-1595030; /* content: Justice will be done. */
UPDATE script_texts SET content_loc2='Nous allons en finir tout de suite, Mal\'Ganis.' WHERE entry=-1595031; /* content: We\'re going to finish this right now, Mal\'Ganis. Just you... and me. */
UPDATE script_texts SET content_loc2='Votre voyage ne fait que commencer, jeune prince. Rassemblez vos troupes et retrouvez-moi dans les terres arctiques du Norfendre. C\'est là-bas que nous règlerons nos comptes. C\'est là-bas que votre vraie destinée vous attend.' WHERE entry=-1595032; /* content: Your journey has just begun, young prince. Gather your forces and meet me in the arctic land of Northrend. It is there that we shall settle the score between us. It is there that your true destiny will unfold. */
UPDATE script_texts SET content_loc2='Je vous pourchasserai aux confins de la terre si je le dois ! Vous m\'entendez ? Jusqu\'aux confins de la terre !' WHERE entry=-1595033; /* content: I\'ll hunt you to the ends of the earth if I have to! Do you hear me? To the ends of the earth! */
UPDATE script_texts SET content_loc2='Vous avez fait du beau travail. Prenez ce que Mal\'Ganis a laissé derrière lui. Que ce soit votre récompense. Quant à moi, je dois aller préparer une expédition pour le Norfendre.' WHERE entry=-1595034; /* content: You performed well this day. Anything that Mal\'Ganis has left behind is yours. Take it as your reward. I must now begin plans for an expedition to Northrend. */
UPDATE script_texts SET content_loc2='Protégez votre prince, soldats de Lordaeron ! J\'ai besoin de votre aide !' WHERE entry=-1595035; /* content: Protect your prince, soldiers of Lordaeron! I am in need of aid! */
UPDATE script_texts SET content_loc2='Mal\'Ganis va payer pour ça.' WHERE entry=-1595037; /* content: Mal\'Ganis will pay for this. */
UPDATE script_texts SET content_loc2='Je ne peux pas me permettre de vous épargner.' WHERE entry=-1595038; /* content: I can\'t afford to spare you. */
UPDATE script_texts SET content_loc2='Un obstacle de moins sur ma route.' WHERE entry=-1595039; /* content: One less obstacle to deal with. */
UPDATE script_texts SET content_loc2='La chair ne vaut rien face au fer !' WHERE entry=-1599002; /* content: Flesh is no match for iron! */
UPDATE script_texts SET content_loc2='Les armées de fer vont étrangler le monde !' WHERE entry=-1599003; /* content: Armies of iron will smother the world! */
UPDATE script_texts SET content_loc2='Pourquoi ça doit finir comme ça ?' WHERE entry=-1599006; /* content: Why must it be this way? */
UPDATE script_texts SET content_loc2='Vous l\'avez bien mérité !' WHERE entry=-1599007; /* content: You had it coming! */
UPDATE script_texts SET content_loc2='Tellement de temps perdu… Que vous ne retrouverez jamais !' WHERE entry=-1599010; /* content: So much lost time... that you\'ll never get back! */
UPDATE script_texts SET content_loc2='J\'espère que ça en vaut la peine !' WHERE entry=-1599019; /* content: I hope this is all worth it! */
UPDATE script_texts SET content_loc2='Il est temps de trouver des réponses ! En avant la musique !' WHERE entry=-1599020; /* content: Time to get some answers! Let\'s get this show on the road! */
