UPDATE script_texts SET content_loc2='Alors regardez-moi dans les yeux quand…' WHERE entry=-1609028; /* content: You\'ll look me in the eyes when... */
UPDATE script_texts SET content_loc2='Ouais, eh ben la fille de Forgefer que je suis aimerait…' WHERE entry=-1609029; /* content: Well this son o\' Ironforge would like... */
UPDATE script_texts SET content_loc2='N\'est-ce pas ironique ? Me faire tuer…' WHERE entry=-1609030; /* content: Ironic, isn\'t it? To be killed... */
UPDATE script_texts SET content_loc2='Si vous pouviez juste m\'accorder…' WHERE entry=-1609031; /* content: If you\'d allow me just one... */
UPDATE script_texts SET content_loc2='J\'aimerais être debout pour…' WHERE entry=-1609032; /* content: I\'d like to stand for... */
UPDATE script_texts SET content_loc2='Je veux mourir comme une orque…' WHERE entry=-1609033; /* content: I want to die like an orc... */
UPDATE script_texts SET content_loc2='Moi, je voudrais être debout pour…' WHERE entry=-1609034; /* content: Dis troll gonna stand for da... */
UPDATE script_texts SET content_loc2='$n ?' WHERE entry=-1609035; /* content: $N? */
UPDATE script_texts SET content_loc2='$n ? Mec ?' WHERE entry=-1609036; /* content: $N? Mon? */
UPDATE script_texts SET content_loc2='$n, je reconnaîtrais ce gros nez vert entre mille. Que… Qu\'est-ce qu\'ils t\'ont fait, $n ?' WHERE entry=-1609037; /* content: $N, I\'d recognize that face anywhere... What... What have they done to you, $N? */
UPDATE script_texts SET content_loc2='$n, je reconnaîtrais ces tentacules entre mille… Que… Qu\'est-ce qu\'ils vous ont fait, $n ?' WHERE entry=-1609038; /* content: $N, I\'d recognize those face tentacles anywhere... What... What have they done to you, $N? */
UPDATE script_texts SET content_loc2='$n, je reconnaîtrais ce nez putréfié entre mille… Que… Qu\'est-ce qu\'ils vous ont fait, $n ?' WHERE entry=-1609040; /* content: $N, I\'d recognize that decay anywhere... What... What have they done to you, $N? */
UPDATE script_texts SET content_loc2='$n, ces défenses, je les r\'connaîtrais entre mille, mec… Que… Qu\'est-ce qu\'ils vous ont fait, mec ?' WHERE entry=-1609042; /* content: $N, I\'d recognize dem tusks anywhere... What... What have dey done ta you, mon? */
UPDATE script_texts SET content_loc2='Vous ne vous souvenez pas de moi, hein ? Maudit Fléau… Ils ont voulu vous enlever tout ce qui faisait de vous une valeureuse force de jugement. Chaque parcelle de bien en vous, jusqu\'à la dernière… Tout ce qui faisait de vous $gun:une; draeneï !' WHERE entry=-1609043; /* content: You don\'t remember me, do you? Blasted Scourge... They\'ve tried to drain you of everything that made you a righteous force of reckoning. Every last ounce of good... Everything that made you a draenei! */
UPDATE script_texts SET content_loc2='Vous ne vous souvenez pas de moi, hein ? Maudit Fléau… Ils ont voulu vous enlever tout ce qui faisait de vous une minuscule force de jugement. Chaque parcelle de bien en vous, jusqu\'à la dernière… Tout ce qui faisait de vous $gun:une; gnome !' WHERE entry=-1609046; /* content: You don\'t remember me, do you? Blasted Scourge... They\'ve tried to drain you of everything that made you a pint-sized force of reckoning. Every last ounce of good... Everything that made you a gnome! */
UPDATE script_texts SET content_loc2='Vous ne vous souvenez pas de moi, hein ? Maudit Fléau… Ils ont voulu vous enlever tout ce qui faisait de vous une valeureuse force de jugement. Chaque parcelle de bien en vous, jusqu\'à la dernière… Tout ce qui faisait de vous $gun tauren:une taurène; !' WHERE entry=-1609050; /* content: You don\'t remember me, do you? Blasted Scourge... They\'ve tried to drain you of everything that made you a righteous force of reckoning. Every last ounce of good... Everything that made you a tauren! */
UPDATE script_texts SET content_loc2='Écoute-moi, $n. Tu dois combattre l\'influence du roi-liche. C\'est un monstre qui veut anéantir le monde, notre monde. Ne le laisse pas t\'utiliser pour ça. Tu as été $gun héros:une héroïne;, tu as même été riche, et tu peux le redevenir. Bats-toi, bon sang ! Rejette-le !' WHERE entry=-1609062; /* content: Listen to me, $N. You must fight against the Lich King\'s control. He is a monster that wants to see this world - our world - in ruin. Don\'t let him use you to accomplish his goals. You were once a hero and you can be again. Fight, damn you! Fight his control! */
UPDATE script_texts SET content_loc2='Qu\'est-ce qui se passe là-dedans ? Qu\'est-ce qui prend si longtemps, $n ?' WHERE entry=-1609066; /* content: What\'s going on in there? What\'s taking so long, $N? */
UPDATE script_texts SET content_loc2='Je… Je n\'en ai plus pour très longtemps. Je suis condamné. Achevez-moi, $n. Faites-le, ou ils nous tueront tous les deux. $n… Souvenez-vous d\'Argus. Il y a tant à sauver en ce monde.' WHERE entry=-1609068; /* content: There... There\'s no more time for me. I\'m done for. Finish me off, $N. Do it or they\'ll kill us both. $N... Remember Argus. Don\'t let that happen to this world. */
UPDATE script_texts SET content_loc2='Je… Je n\'en ai plus pour très longtemps. Je suis condamnée. Achevez-moi, $n. Faites-le, ou ils nous tueront tous les deux. $n… Souvenez-vous de Tirisfal ! Il y a tant à sauver en ce monde.' WHERE entry=-1609070; /* content: There... There\'s no more time for me. I\'m done for. Finish me off, $N. Do it or they\'ll kill us both. $N... Remember Tirisfal! This world is worth saving! */
UPDATE script_texts SET content_loc2='Je… Je n\'en ai plus pour très longtemps. Je suis condamnée. Achevez-moi, $n. Faites-le, ou ils nous tueront tous les deux. $n… Souvenez-vous de Gnomeregan. Il y a tant à bricoler en ce monde.' WHERE entry=-1609071; /* content: There... There\'s no more time for me. I\'m done for. Finish me off, $N. Do it or they\'ll kill us both. $N... Remember Gnomeregan! This world is worth saving. */
UPDATE script_texts SET content_loc2='Je… Je n\'en ai plus pour très longtemps. Je suis condamnée. Achevez-moi, $n. Faites-le, ou ils nous tueront tous les deux. $n… Souvenez-vous de Teldrassil, notre cher foyer. Il y a tant à sauver en ce monde.' WHERE entry=-1609073; /* content: There... There\'s no more time for me. I\'m done for. Finish me off, $N. Do it or they\'ll kill us both. $N... Remember Teldrassil, our beloved home. This world is worth saving. */
UPDATE script_texts SET content_loc2='Je… Je n\'en ai plus pour très longtemps. Je suis condamnée. Achevez-moi, $n. Faites-le, ou ils nous tueront tous les deux. $n… Pour la Horde ! Il y a tant à sauver en ce monde.' WHERE entry=-1609074; /* content: There... There\'s no more time for me. I\'m done for. Finish me off, $N. Do it or they\'ll kill us both. $N... For the Horde! This world is worth saving. */
UPDATE script_texts SET content_loc2='Je… Je n\'en ai plus pour très longtemps. Je suis condamné. Achevez-moi, $n. Faites-le, ou ils nous tueront tous les deux. $n… Souvenez-vous de Mulgore. Il y a tant à sauver en ce monde.' WHERE entry=-1609075; /* content: There... There\'s no more time for me. I\'m done for. Finish me off, $N. Do it or they\'ll kill us both. $N... Remember Mulgore. This world is worth saving. */
UPDATE script_texts SET content_loc2='Fais-le, $n ! Mets fin à mes souffrances !' WHERE entry=-1609077; /* content: Do it, $N! Put me out of my misery! */
UPDATE script_texts SET content_loc2='Il va me falloir ma lame runique et mon armure… J\'ai juste besoin d\'un peu plus de temps.' WHERE entry=-1609079; /* content: I\'ll need to get my runeblade and armor... Just need a little more time. */
UPDATE script_texts SET content_loc2='Je suis encore affaibli, mais je pense pouvoir lever une barrière anti-magie. Restez bien dedans, ou vous serez $gdétruit:détruite; par leurs sorts.' WHERE entry=-1609080; /* content: I\'m still weak, but I think I can get an anti-magic barrier up. Stay inside it or you\'ll be destroyed by their spells. */
UPDATE script_texts SET content_loc2='Maintenir cette barrière va demander toute ma concentration. Tuez-les tous !' WHERE entry=-1609081; /* content: Maintaining this barrier will require all of my concentration. Kill them all! */
UPDATE script_texts SET content_loc2='En voilà d\'autres. Défendez-vous ! Ne sortez pas du champ anti-magie ! Sans sa protection, ils vous mettraient en pièces !' WHERE entry=-1609082; /* content: There are more coming. Defend yourself! Don\'t fall out of the anti-magic field! They\'ll tear you apart without its protection! */
UPDATE script_texts SET content_loc2='Le grand inquisiteur arrive ! Préparez-vous, chevalier de la mort ! Ne le laissez pas vous attirer en dehors de mon champ protecteur ! Tuez-le, et prenez sa tête !' WHERE entry=-1609084; /* content: The High Inquisitor comes! Be ready, death knight! Do not let him draw you out of the protective bounds of my anti-magic field! Kill him and take his head! */
UPDATE script_texts SET content_loc2='Ne sortez pas du champ protecteur ! Faites-les venir à vous !' WHERE entry=-1609085; /* content: Stay in the anti-magic field! Make them come to you! */
UPDATE script_texts SET content_loc2='La mort du grand inquisiteur de la Nouvelle-Avalon ne va pas passer inaperçue. Il faut que vous sortiez d\'ici tout de suite ! Partez avant qu\'il n\'en arrive d\'autres. Je vais me débrouiller tout seul, tout ira bien.' WHERE entry=-1609086; /* content: The death of the High Inquisitor of New Avalon will not go unnoticed. You need to get out of here at once! Go, before more of them show up. I\'ll be fine on my own. */
UPDATE script_texts SET content_loc2='Je vais détourner leur attention, passez derrière moi.' WHERE entry=-1609087; /* content: I\'ll draw their fire, you make your escape behind me. */
UPDATE script_texts SET content_loc2='Votre grand inquisiteur n\'est rien d\'autre qu\'un tas de chair, croisés ! Nul n\'est à l\'abri de l\'étreinte du Fléau !' WHERE entry=-1609088; /* content: Your High Inquisitor is nothing more than a pile of meat, Crusaders! There are none beyond the grasp of the Scourge! */
UPDATE script_texts SET content_loc2='Maman ?' WHERE entry=-1609091; /* content: Mommy? */
UPDATE script_texts SET content_loc2='CERVEAAAAUUUX !' WHERE entry=-1609092; /* content: GIVE ME BRAINS! */
UPDATE script_texts SET content_loc2='Me nourrir…' WHERE entry=-1609093; /* content: Must feed... */
UPDATE script_texts SET content_loc2='Si faim…' WHERE entry=-1609094; /* content: So hungry... */
UPDATE script_texts SET content_loc2='On met la goule dans la fosse, ou alors on va tâter du fouet !' WHERE entry=-1609096; /* content: It puts the ghoul in the pit or else it gets the lash! */
UPDATE script_texts SET content_loc2='%s se dresse et vous fait signe de monter sur son dos.' WHERE entry=-1609097; /* content: %s rears up, beckoning you to ride it. */
UPDATE script_texts SET content_loc2='Impressionnant, chevalier de la mort. Revenez me voir dans le royaume des vivants pour obtenir votre récompense.' WHERE entry=-1609098; /* content: Impressive, death knight. Return to me in the world of the living for your reward. */
UPDATE script_texts SET content_loc2='Soldats du Fléau, à vos armes ! Préparez-vous à déchaîner votre fureur contre l\'Aube d\'argent !' WHERE entry=-1609201; /* content: Soldiers of the Scourge, stand ready! Prepare to unleash your fury upon the Argent Dawn! */
UPDATE script_texts SET content_loc2='Chevaliers de la mort d\'Achérus, que la marche de mort commence !' WHERE entry=-1609203; /* content: Death knights of Acherus, the death march begins! */
UPDATE script_texts SET content_loc2='Soldats du Fléau, chevaliers de la mort d\'Achérus, serviteurs des ténèbres : entendez l\'appel du généralissime !' WHERE entry=-1609204; /* content: Soldiers of the Scourge, death knights of Acherus, minions of the darkness: hear the call of the Highlord! */
UPDATE script_texts SET content_loc2='RELEVEZ-VOUS !' WHERE entry=-1609205; /* content: RISE! */
UPDATE script_texts SET content_loc2='Le ciel rougeoie du sang des morts ! Le roi-liche veille sur nous, serviteurs ! Ne laissez que cendre et affliction dans le sillon de votre carnage !' WHERE entry=-1609206; /* content: The skies turn red with the blood of the fallen! The Lich King watches over us, minions! Leave only ashes and misery in your destructive wake! */
UPDATE script_texts SET content_loc2='Armées du Fléau en approche !' WHERE entry=-1609207; /* content: Scourge armies approach! */
UPDATE script_texts SET content_loc2='Tenez bon, mes frères et sœurs ! La Lumière triomphera !' WHERE entry=-1609208; /* content: Stand fast, brothers and sisters! The Light will prevail! */
UPDATE script_texts SET content_loc2='À genoux devant le généralissime !' WHERE entry=-1609209; /* content: Kneel before the Highlord! */
UPDATE script_texts SET content_loc2='Vous n\'avez aucune chance !' WHERE entry=-1609210; /* content: You stand no chance! */
UPDATE script_texts SET content_loc2='Le Fléau va raser cet endroit !' WHERE entry=-1609211; /* content: The Scourge will destroy this place! */
UPDATE script_texts SET content_loc2='Vous le paierez de votre vie !' WHERE entry=-1609212; /* content: Your life is forfeit. */
UPDATE script_texts SET content_loc2='Combien de temps allez-vous encore tenir ?' WHERE entry=-1609214; /* content: How much longer will your forces hold out? */
UPDATE script_texts SET content_loc2='Pas de quartier !' WHERE entry=-1609216; /* content: Spare no one! */
UPDATE script_texts SET content_loc2='Qu\'est-ce qui se passe ?! Ma… Je ne peux pas frapper…' WHERE entry=-1609217; /* content: What is this?! My... I cannot strike... */
UPDATE script_texts SET content_loc2='LAME, OBÉIS !' WHERE entry=-1609218; /* content: Obey me, blade! */
UPDATE script_texts SET content_loc2='Tu feras comme je l\'ordonne ! C\'est moi qui commande !' WHERE entry=-1609219; /* content: You will do as I command! I am in control here! */
UPDATE script_texts SET content_loc2='Qu\'est-ce qui m\'arrive ?' WHERE entry=-1609221; /* content: What is happening to me? */
UPDATE script_texts SET content_loc2='Porte-cendres me défie…' WHERE entry=-1609223; /* content: Ashbringer defies me... */
UPDATE script_texts SET content_loc2='Serviteurs, venez à mon secours !' WHERE entry=-1609224; /* content: Minions, come to my aid! */
UPDATE script_texts SET content_loc2='Vous ne pouvez pas gagner, Darion !' WHERE entry=-1609225; /* content: You cannot win, Darion! */
UPDATE script_texts SET content_loc2='Amenez-les devant la chapelle !' WHERE entry=-1609226; /* content: Bring them before the chapel! */
UPDATE script_texts SET content_loc2='Ce que vous sentez là est la détresse d\'un millier d\'âmes perdues ! Des âmes que vous et votre maître avez amenées ici ! La Lumière va vous déchiqueter, Darion !' WHERE entry=-1609230; /* content: What you are feeling right now is the anguish of a thousand lost souls! Souls that you and your master brought here! The Light will tear you apart, Darion! */
UPDATE script_texts SET content_loc2='Économisez votre souffle, vieillard. Ce pourrait être le dernier.' WHERE entry=-1609231; /* content: Save your breath, old man. It might be the last you ever draw. */
UPDATE script_texts SET content_loc2='Mon fils ! Mon cher et tendre fils !' WHERE entry=-1609232; /* content: My son! My dear, beautiful boy! */
UPDATE script_texts SET content_loc2='Père !' WHERE entry=-1609233; /* content: Father! */
UPDATE script_texts SET content_loc2='Père, tu es revenu !' WHERE entry=-1609235; /* content: Father, you have returned! */
UPDATE script_texts SET content_loc2='Tu es parti depuis si longtemps, père. Je croyais…' WHERE entry=-1609236; /* content: You have been gone a long time, father. I thought... */
UPDATE script_texts SET content_loc2='Rien n\'aurait pu me tenir à distance, Darion. Pas de ma famille et de mon foyer.' WHERE entry=-1609237; /* content: Nothing could have kept me away from here, Darion. Not from my home and family. */
UPDATE script_texts SET content_loc2='Père, je veux me joindre à ton combat contre les morts-vivants. Je veux me battre ! Je ne peux plus rester à ne rien faire !' WHERE entry=-1609238; /* content: Father, I wish to join you in the war against the undead. I want to fight! I can sit idle no longer! */
UPDATE script_texts SET content_loc2='N\'oublie pas…' WHERE entry=-1609242; /* content: Do not forget... */
UPDATE script_texts SET content_loc2='Émouvant…' WHERE entry=-1609243; /* content: Touching... */
UPDATE script_texts SET content_loc2='Pathétique…' WHERE entry=-1609246; /* content: Pathetic... */
UPDATE script_texts SET content_loc2='Tu n\'es qu\'un monstre impie, Arthas !' WHERE entry=-1609247; /* content: You\'re a damned monster, Arthas! */
UPDATE script_texts SET content_loc2='Vous aviez raison, Fordring. Je les ai bien envoyés à la mort. Leurs vies ne signifient rien, mais la vôtre…' WHERE entry=-1609248; /* content: You were right, Fordring. I did send them in to die. Their lives are meaningless, but yours... */
UPDATE script_texts SET content_loc2='À L\'ATTAQUE !!!' WHERE entry=-1609250; /* content: ATTACK!!! */
UPDATE script_texts SET content_loc2='APOCALYPSE !' WHERE entry=-1609251; /* content: APOCALYPSE! */
UPDATE script_texts SET content_loc2='Ce jour n\'est pas venu…' WHERE entry=-1609252; /* content: That day is not today... */
UPDATE script_texts SET content_loc2='Tirion !' WHERE entry=-1609253; /* content: Tirion! */
UPDATE script_texts SET content_loc2='ARTHAS !!!!' WHERE entry=-1609254; /* content: ARTHAS!!!! */
UPDATE script_texts SET content_loc2='Qu\'est-ce que c\'est ?' WHERE entry=-1609255; /* content: What is this? */
UPDATE script_texts SET content_loc2='Votre fin.' WHERE entry=-1609256; /* content: Your end. */
UPDATE script_texts SET content_loc2='Impossible…' WHERE entry=-1609257; /* content: Impossible... */
UPDATE script_texts SET content_loc2='Je vous fais une promesse, mes frères et sœurs : le roi-liche sera vaincu ! En ce jour, j\'en appelle à une union.' WHERE entry=-1609264; /* content: I make a promise to you now, brothers and sisters: The Lich King will be defeated! On this day, I call for a union. */
UPDATE script_texts SET content_loc2='%s s\'agenouille, vaincu, devant Tirion Fordring.' WHERE entry=-1609273; /* content: %s kneels in defeat before Tirion Fordring. */
UPDATE script_texts SET content_loc2='%s étreint son père.' WHERE entry=-1609276; /* content: %s hugs his father. */
UPDATE script_texts SET content_loc2='%s essaie de respirer.' WHERE entry=-1609280; /* content: %s gasps for air. */
UPDATE script_texts SET content_loc2='%s s\'effondre.' WHERE entry=-1609283; /* content: %s collapses. */
UPDATE script_texts SET content_loc2='Je n\'ai peur de rien ! Et surtout pas de vous !' WHERE entry=-1615000; /* content: I fear nothing! Least of all you! */
UPDATE script_texts SET content_loc2='À genoux !' WHERE entry=-1615007; /* content: On your knees! */
UPDATE script_texts SET content_loc2='Un disciple d\'Obscuron apparaît dans le crépuscule !' WHERE entry=-1615008; /* content: A Shadron Disciple appears in the Twilight! */
UPDATE script_texts SET content_loc2='Aucun défi.' WHERE entry=-1615010; /* content: No contest. */
UPDATE script_texts SET content_loc2='Je n\'aurais pas dû... me retenir…' WHERE entry=-1615012; /* content: I should not... have held back... */
UPDATE script_texts SET content_loc2='Ces œufs sont sous ma responsabilité. Je vous ferai brûler avant de vous laisser y toucher !' WHERE entry=-1615018; /* content: It is my charge to watch over these eggs. I will see you burn before any harm comes to them! */
UPDATE script_texts SET content_loc2='Ce pitoyable petit siège s\'achève MAINTENANT !' WHERE entry=-1615019; /* content: This pathetic siege ends NOW! */
UPDATE script_texts SET content_loc2='Brûlez, misérables vermines !' WHERE entry=-1615020; /* content: Burn, you miserable wretches! */
UPDATE script_texts SET content_loc2='Ténébron ! Toi aussi tu dois protéger les œufs !' WHERE entry=-1615022; /* content: Tenebron! The eggs are yours to protect as well! */
UPDATE script_texts SET content_loc2='Des petits insectes inflammables...' WHERE entry=-1615025; /* content: Such flammable little insects.... */
UPDATE script_texts SET content_loc2='Vos ossements noircis joncheront le sol !' WHERE entry=-1615026; /* content: Your charred bones will litter the floor! */
UPDATE script_texts SET content_loc2='Jusqu\'où pouvez-vous supporter la chaleur ?' WHERE entry=-1615027; /* content: How much heat can you take? */
UPDATE script_texts SET content_loc2='Tout sera réduit en cendres !' WHERE entry=-1615028; /* content: All will be reduced to ash! */
UPDATE script_texts SET content_loc2='Vous ferez un bon repas pour les petits.' WHERE entry=-1615029; /* content: You will make a fine meal for the hatchlings. */
UPDATE script_texts SET content_loc2='La lave qui entoure %s bouillonne !' WHERE entry=-1615032; /* content: The lava surrounding %s churns! */
UPDATE script_texts SET content_loc2='C\'était vraiment ce que vous pouviez faire de mieux ?' WHERE entry=-1615035; /* content: Was that the best you can do? */
UPDATE script_texts SET content_loc2='Je vais me curer les dents avec vos ossements !' WHERE entry=-1615037; /* content: I will pick my teeth with your bones! */
UPDATE script_texts SET content_loc2='Un disciple de Vespéron apparaît dans le crépuscule !' WHERE entry=-1615041; /* content: A Vesperon Disciple appears in the Twilight! */
UPDATE script_texts SET content_loc2='%s commence à ouvrir un portail du Crépuscule !' WHERE entry=-1615042; /* content: %s begins to open a Twilight Portal! */
UPDATE script_texts SET content_loc2='Des êtres inférieurs qui s\'introduisent ici ! Dommage que votre témérité ne rattrape pas votre bêtise !' WHERE entry=-1616000; /* content: Lesser beings, intruding here! A shame that your excess courage does not compensate for your stupidity! */
UPDATE script_texts SET content_loc2='Rien ne peut m\'arrêter !' WHERE entry=-1616007; /* content: I AM UNSTOPPABLE! */
UPDATE script_texts SET content_loc2='Peu de gens ont connu la douleur que je vais vous infliger !' WHERE entry=-1616012; /* content: Few have experienced the pain I will now inflict upon you! */
UPDATE script_texts SET content_loc2='Vous n\'arriverez à rien tant qu\'il me restera un souffle !' WHERE entry=-1616013; /* content: You will not succeed while I draw breath! */
UPDATE script_texts SET content_loc2='Je suis le Tisse-sort ! Ma puissance est infinie !' WHERE entry=-1616016; /* content: I am the spell-weaver! My power is infinite! */
UPDATE script_texts SET content_loc2='Votre esprit va languir ici pour toujours !' WHERE entry=-1616017; /* content: Your spirit will linger here forever! */
UPDATE script_texts SET content_loc2='Soumettez-vous !' WHERE entry=-1616020; /* content: SUBMIT! */
UPDATE script_texts SET content_loc2='Les puissances à l\'œuvre ici dépassent tout ce que vous pourriez imaginer !' WHERE entry=-1616024; /* content: The powers at work here exceed anything you could possibly imagine! */
UPDATE script_texts SET content_loc2='Votre cause est perdue !' WHERE entry=-1616026; /* content: Your cause is lost! */
UPDATE script_texts SET content_loc2='Vos frêles esprits vont être fracassés !' WHERE entry=-1616027; /* content: Your fragile mind will be shattered! */
UPDATE script_texts SET content_loc2='J\'ai fait ce que j\'avais à faire, mon frère. Tu ne m\'as laissé aucune alternative.' WHERE entry=-1616029; /* content: I did what I had to, brother. You gave me no alternative. */
UPDATE script_texts SET content_loc2='Cette résolution m\'attriste énormément, mais la destruction, le gâchis monumental de vie devait cesser. Malgré les transgressions récentes de Malygos, je pleurerai sa perte. Il était autrefois un gardien, un protecteur. Un des grands de ce monde est tombé aujourd\'hui.' WHERE entry=-1616031; /* content: This resolution pains me deeply, but the destruction, the monumental loss of life had to end. Regardless of Malygos\' recent transgressions, I will mourn his loss. He was once a guardian, a protector. This day, one of the world\'s mightiest has fallen. */
UPDATE script_texts SET content_loc2='Une étincelle de puissance surgit d\'une faille proche !' WHERE entry=-1616033; /* content: A Power Spark forms from a nearby rift! */
UPDATE script_texts SET content_loc2='%s inspire profondément.' WHERE entry=-1616034; /* content: %s takes a deep breath. */
UPDATE script_texts SET content_loc2='Les secrets des profondeurs resteront scellés.' WHERE entry=-1619000; /* content: The secrets of the deep shall remain hidden. */
UPDATE script_texts SET content_loc2='Shhhad ak kereeesshh chak-k-k !' WHERE entry=-1619002; /* content: Shhhad ak kereeesshh chak-k-k! */
UPDATE script_texts SET content_loc2='Pour le roi-liche !' WHERE entry=-1619004; /* content: For the Lich King! */
UPDATE script_texts SET content_loc2='Nous serons peut-être bientôt alliés.' WHERE entry=-1619005; /* content: Perhaps we will be allies soon. */
UPDATE script_texts SET content_loc2='Un gardien ahn\'kahar éclot !' WHERE entry=-1619007; /* content: An Ahn\'kahar Guardian hatches! */
UPDATE script_texts SET content_loc2='Qui d\'entre vous est dévoué ?' WHERE entry=-1619018; /* content: Who among you is devoted? */
UPDATE script_texts SET content_loc2='Vous, là ! Avancez !' WHERE entry=-1619019; /* content: You there! Step forward! */
UPDATE script_texts SET content_loc2='Maître, un présent pour vous !' WHERE entry=-1619021; /* content: Master, a gift for you! */
UPDATE script_texts SET content_loc2='Gloire à Yogg-Saron !' WHERE entry=-1619022; /* content: Glory to Yogg-Saron! */
UPDATE script_texts SET content_loc2='Debout ! Vous n\'avez pas assez souffert.' WHERE entry=-1619024; /* content: Get up! You haven\'t suffered enough. */
UPDATE script_texts SET content_loc2='Ne croyez pas que votre sacrilège... restera... impuni.' WHERE entry=-1619025; /* content: Do not expect your sacrilege... to go unpunished. */
UPDATE script_texts SET content_loc2='Les éléments eux-mêmes se soulèveront contre le monde civilisé ! Seuls les fidèles seront épargnés !' WHERE entry=-1619026; /* content: The elements themselves will rise up against the civilized world! Only the faithful will be spared! */
UPDATE script_texts SET content_loc2='J\'ai été choisie !' WHERE entry=-1619031; /* content: I have been chosen! */
