UPDATE script_texts SET content_loc2='Pour vous, c\'est le début ET la fin, mortels. Personne ne pénètre dans le sanctuaire du maître !' WHERE entry=-1631001; /* content: This is the beginning AND the end, mortals. None may enter the master\'s sanctum! */
UPDATE script_texts SET content_loc2='Le Fléau va déferler sur ce monde dans un torrent de mort et de destruction !' WHERE entry=-1631002; /* content: The Scourge will wash over this world as a swarm of death and destruction! */
UPDATE script_texts SET content_loc2='TEMPÊTE DANS UN VERRE D\'OS !' WHERE entry=-1631003; /* content: BONE STORM! */
UPDATE script_texts SET content_loc2='Liés par les os !' WHERE entry=-1631004; /* content: Bound by bone! */
UPDATE script_texts SET content_loc2='Ne vous sauvez pas !' WHERE entry=-1631005; /* content: Stick around! */
UPDATE script_texts SET content_loc2='La seule issue est la mort !' WHERE entry=-1631006; /* content: The only escape is death! */
UPDATE script_texts SET content_loc2='Toujours plus d\'os pour les offrandes !' WHERE entry=-1631007; /* content: More bones for the offering! */
UPDATE script_texts SET content_loc2='Morfondez-vous dans la damnation !' WHERE entry=-1631008; /* content: Languish in damnation! */
UPDATE script_texts SET content_loc2='LA RAGE DU MAÎTRE COULE DANS MES VEINES !' WHERE entry=-1631010; /* content: THE MASTER\'S RAGE COURSES THROUGH ME! */
UPDATE script_texts SET content_loc2='Vous êtes arrivés ici car vous faites partie des rares élus dotés d\'une vraie vision dans un monde affligé par l\'aveuglement.' WHERE entry=-1631011; /* content: You have found your way here, because you are among the few gifted with true vision in a world cursed with blindness. */
UPDATE script_texts SET content_loc2='Vous voyez au-delà de la brume qui pèse sur ce monde comme un lourd voile, et comprenez où se trouve la vraie puissance.' WHERE entry=-1631012; /* content: You can see through the fog that hangs over this world like a shroud, and grasp where true power lies. */
UPDATE script_texts SET content_loc2='Prenez le temps de regarder vos si grossières mains : les tendons, cette chair si molle, le sang qui s\'y écoule.' WHERE entry=-1631013; /* content: Fix your eyes upon your crude hands: the sinew, the soft meat, the dark blood coursing within. */
UPDATE script_texts SET content_loc2='Plus vite vous accepterez les lacunes de votre condition, plus vite il vous sera possible de la transcender.' WHERE entry=-1631015; /* content: The sooner you come to accept your condition as a defect, the sooner you will find yourselves in a position to transcend it. */
UPDATE script_texts SET content_loc2='Grâce à notre maître, tout est possible. Son pouvoir est sans limite, et sa volonté inflexible.' WHERE entry=-1631016; /* content: Through our Master, all things are possible. His power is without limit, and his will unbending. */
UPDATE script_texts SET content_loc2='Ceux qui s\'opposent à lui seront anéantis, et ceux qui le servent, sans retenue ni hésitation, avec une totale dévotion de l\'âme et de l\'esprit... Ceux-là seront élevés à des hauteurs qui vous dépassent.' WHERE entry=-1631017; /* content: Those who oppose him will be destroyed utterly, and those who serve -- who serve wholly, unquestioningly, with utter devotion of mind and soul -- elevated to heights beyond your ken. */
UPDATE script_texts SET content_loc2='Assez ! Je vois qu\'il faut que je prenne la situation en mains !' WHERE entry=-1631019; /* content: Enough! I see I must take matters into my own hands! */
UPDATE script_texts SET content_loc2='Loyal partisan, je te libère de la malédiction de la chair !' WHERE entry=-1631021; /* content: I release you from the curse of flesh! */
UPDATE script_texts SET content_loc2='Vous êtes faibles, impuissants devant ma volonté !' WHERE entry=-1631023; /* content: You are weak, powerless to resist my will! */
UPDATE script_texts SET content_loc2='PAR LA PUISSANCE DU ROI-LICHE !' WHERE entry=-1631028; /* content: BY THE MIGHT OF THE LICH KING! */
UPDATE script_texts SET content_loc2='La terre est rouge de votre sang !' WHERE entry=-1631029; /* content: The ground runs red with your blood! */
UPDATE script_texts SET content_loc2='Repaissez-vous, mes serviteurs !' WHERE entry=-1631030; /* content: Feast, my minions! */
UPDATE script_texts SET content_loc2='Vous n\'êtes rien !' WHERE entry=-1631031; /* content: You are nothing! */
UPDATE script_texts SET content_loc2='Ici, pas de salut pour les âmes !' WHERE entry=-1631032; /* content: Your soul will find no redemption here! */
UPDATE script_texts SET content_loc2='Bon allez, on se bouge ! En route -' WHERE entry=-1631035; /* content: Let\'s get a move on then! Move ou... */
UPDATE script_texts SET content_loc2='Et le pire est à venir. Venez goûter à la puissance que m\'a donnée le roi-liche !' WHERE entry=-1631037; /* content: Things are about to get much worse. Come, taste the power that the Lich King has bestowed upon me! */
UPDATE script_texts SET content_loc2='Chargez !' WHERE entry=-1631039; /* content: Charge! */
UPDATE script_texts SET content_loc2='Rejoins-moi, père. Rejoins-moi, et ensemble nous écraserons ce monde au nom du Fléau, pour la gloire du roi-liche !' WHERE entry=-1631042; /* content: Join me, father. Join me and we will crush this world in the name of the Scourge -- for the glory of the Lich King! */
UPDATE script_texts SET content_loc2='Mon fils est mort au portail du Courroux. Je ne suis là que pour récupérer son corps.' WHERE entry=-1631043; /* content: My boy died at the Wrath Gate. I am here only to collect his body. */
UPDATE script_texts SET content_loc2='Vieil orc têtu. Quelles sont tes chances ? Je suis plus fort et plus puissant que tu ne l\'as jamais été.' WHERE entry=-1631044; /* content: Stubborn and old. What chance do you have? I am stronger, and more powerful than you ever were. */
UPDATE script_texts SET content_loc2='Avant qu\'elle ne meure, j\'ai juré à sa mère que je franchirais seul la Porte des ténèbres. Que je survive ou pas, mon fils serait sauvé. Sauvé de la corruption.' WHERE entry=-1631046; /* content: I made a promise to his mother before she died; that I would cross the Dark Portal alone - whether I lived or died, my son would be safe. Untainted... */
UPDATE script_texts SET content_loc2='Aujourd\'hui, je viens tenir cette promesse.' WHERE entry=-1631047; /* content: Today, I fulfill that promise. */
UPDATE script_texts SET content_loc2='C\'était le fils de Saurcroc. Le commandant de la Horde au portail du Courroux. Quelle fin tragique…' WHERE entry=-1631051; /* content: That was Saurfang\'s boy - the Horde commander at the Wrath Gate. Such a tragic end... */
UPDATE script_texts SET content_loc2='Écartez-vous, Muradin. Laissez passer un père éploré.' WHERE entry=-1631057; /* content: Stand down, Muradin. Let a grieving father pass. */
UPDATE script_texts SET content_loc2='%s pleure.' WHERE entry=-1631061; /* content: %s cries. */
UPDATE script_texts SET content_loc2='Ce n\'est rien, votre majesté. C\'est juste que… Je suis fière de mon roi.' WHERE entry=-1631063; /* content: It was nothing, your majesty. Just... I\'m proud of my king. */
UPDATE script_texts SET content_loc2='Tout d\'suite, votre majesté !' WHERE entry=-1631065; /* content: Right away, yer majesty! */
UPDATE script_texts SET content_loc2='%s tousse.' WHERE entry=-1631066; /* content: %s coughs. */
