UPDATE script_texts SET content_loc2='Merveilleuse nouvelle, mes amis ! La gelée coule à flots !' WHERE entry=-1631074; /* content: Great news, everyone! The slime is flowing again! */
UPDATE script_texts SET content_loc2='Grande nouvelle, mes amis ! J\'ai réparé le distributeur de poison !' WHERE entry=-1631075; /* content: Good news, everyone! I\'ve fixed the poison slime pipes! */
UPDATE script_texts SET content_loc2='Oh, c\'est cassé !' WHERE entry=-1631077; /* content: I brokes-ded it... */
UPDATE script_texts SET content_loc2='C\'EST L\'HEURE DU DODO !' WHERE entry=-1631078; /* content: Sleepy Time! */
UPDATE script_texts SET content_loc2='Mort, mort, mort !' WHERE entry=-1631088; /* content: Dead, dead, dead! */
UPDATE script_texts SET content_loc2='On arrête de jouer !' WHERE entry=-1631089; /* content: Fun time over! */
UPDATE script_texts SET content_loc2='Pa…pa…' WHERE entry=-1631090; /* content: Da ... Ddy... */
UPDATE script_texts SET content_loc2='Au bon goût de… Cerise ! Oups ! Pardon !' WHERE entry=-1631096; /* content: Tastes like... Cherry! Oh! Excuse me! */
UPDATE script_texts SET content_loc2='Un développement inattendu !' WHERE entry=-1631098; /* content: That was unexpected! */
UPDATE script_texts SET content_loc2='Fantastique nouvelle, mes amis !' WHERE entry=-1631099; /* content: Great news, everyone! */
UPDATE script_texts SET content_loc2='Naïfs mortels. Vous pensiez nous avoir vaincus si facilement ? Les San\'layn sont les soldats immortels du roi-liche ! Maintenant, vous allez subir leurs puissances réunies !' WHERE entry=-1631101; /* content: Foolish mortals. You thought us defeated so easily? The San\'layn are the Lich King\'s immortal soldiers! Now you shall face their might combined! */
UPDATE script_texts SET content_loc2='Quel incroyable pouvoir ! L\'orbe des Ténébrants m\'a rendu INVINCIBLE !' WHERE entry=-1631103; /* content: Such wondrous power! The Darkfallen Orb has made me INVINCIBLE! */
UPDATE script_texts SET content_loc2='Quelle menace posez-vous ?' WHERE entry=-1631105; /* content: Were you ever a threat? */
UPDATE script_texts SET content_loc2='Du sang et de l\'acier surgit la vérité.' WHERE entry=-1631106; /* content: Truth is found in death. */
UPDATE script_texts SET content_loc2='Tremblez devant Taldaram, mortels, car le pouvoir de l\'orbe coule en moi !' WHERE entry=-1631109; /* content: Tremble before Taldaram, mortals, for the power of the orb flows through me! */
UPDATE script_texts SET content_loc2='Savourez la douleur !' WHERE entry=-1631110; /* content: Delight in the pain! */
UPDATE script_texts SET content_loc2='À manger pour les asticots.' WHERE entry=-1631111; /* content: Worm food. */
UPDATE script_texts SET content_loc2='Implorez pitié !' WHERE entry=-1631112; /* content: Beg for mercy! */
UPDATE script_texts SET content_loc2='%s rit.' WHERE entry=-1631113; /* content: %s laughs. */
UPDATE script_texts SET content_loc2='Naxxanar ne fut qu\'un contretemps ! Grâce au pouvoir de l\'orbe, Valanar aura sa revanche !' WHERE entry=-1631115; /* content: Naxxanar was merely a setback! With the power of the orb, Valanar will have his vengeance! */
UPDATE script_texts SET content_loc2='Ma coupe déborde.' WHERE entry=-1631116; /* content: My cup runneth over. */
UPDATE script_texts SET content_loc2='Le dîner… est servi.' WHERE entry=-1631117; /* content: Dinner... is served. */
UPDATE script_texts SET content_loc2='Voyez-vous la puissance des Ténébrants, à présent ?' WHERE entry=-1631118; /* content: Do you see NOW the power of the Darkfallen? */
UPDATE script_texts SET content_loc2='À GENOUX DEVANT LES SAN\'LAYN !' WHERE entry=-1631119; /* content: BOW DOWN BEFORE THE SAN\'LAYN! */
UPDATE script_texts SET content_loc2='Ce n\'est pas une décision… très sage.' WHERE entry=-1631121; /* content: You have made an... unwise... decision. */
UPDATE script_texts SET content_loc2='Je veux juste goûter…' WHERE entry=-1631122; /* content: Just a taste... */
UPDATE script_texts SET content_loc2='Voyez ma soif !' WHERE entry=-1631123; /* content: Know my hunger! */
UPDATE script_texts SET content_loc2='Souffrez !' WHERE entry=-1631124; /* content: SUFFER! */
UPDATE script_texts SET content_loc2='Êtes-vous à la hauteur ?' WHERE entry=-1631125; /* content: Can you handle this? */
UPDATE script_texts SET content_loc2='On arrête MAINTENANT !' WHERE entry=-1631128; /* content: THIS ENDS NOW! */
UPDATE script_texts SET content_loc2='Fourbissez vos armes, mes frères d\'Argent. Les vrykuls donneront leurs vies pour la reine du Givre.' WHERE entry=-1631130; /* content: Ready your arms, my Argent Brothers. The Vrykul will protect the Frost Queen with their lives. */
UPDATE script_texts SET content_loc2='Assez bavardé ! Nos champions sont arrivés. Couvrez-les, nous allons nous frayer un chemin jusqu\'à l\'autre côté !' WHERE entry=-1631132; /* content: Enough idle banter! Our champions have arrived - support them as we push our way through the hall! */
UPDATE script_texts SET content_loc2='Tu as peut-être combattu à mes côtés jadis, Crok, mais désormais tu n\'es plus qu\'un traître. Viens goûter à ta seconde mort !' WHERE entry=-1631133; /* content: You may have once fought beside me, Crok, but now you are nothing more than a traitor. Come, your second death approaches! */
UPDATE script_texts SET content_loc2='Je ne tiendrai pas très longtemps.' WHERE entry=-1631143; /* content: I will not last much longer! */
UPDATE script_texts SET content_loc2='Voyez votre pitoyable magie vous trahir, mortels, et souffrez !' WHERE entry=-1631149; /* content: Suffer, mortals, as your pathetic magic betrays you! */
UPDATE script_texts SET content_loc2='Sentez-vous la main glacée de la mort sur vos cœurs ?' WHERE entry=-1631150; /* content: Can you feel the cold hand of death upon your heart? */
UPDATE script_texts SET content_loc2='Votre incursion s\'arrête ici ! Personne n\'en réchappera !' WHERE entry=-1631152; /* content: Your incursion ends here! None shall survive! */
UPDATE script_texts SET content_loc2='Mourez !' WHERE entry=-1631154; /* content: Perish! */
UPDATE script_texts SET content_loc2='Assez ! Ce petit jeu me fatigue !' WHERE entry=-1631156; /* content: Enough! I tire of these games! */
UPDATE script_texts SET content_loc2='Tout est dit. Champions, À L\'ATTAQUE !' WHERE entry=-1631161; /* content: So be it. Champions, attack! */
UPDATE script_texts SET content_loc2='Je vais te laisser en vie, que tu sois témoin de la fin, Fordring. Je ne voudrais pas priver le plus grand héros de la Lumière du spectacle de ce misérable monde refaçonné à mon image.' WHERE entry=-1631162; /* content: I\'ll keep you alive to witness the end, Fordring. I would not want the Light\'s greatest champion to miss seeing this wretched world remade in my image. */
UPDATE script_texts SET content_loc2='Voyez le monde qui vous entoure s\'écrouler !' WHERE entry=-1631165; /* content: Watch as the world around you collapses! */
UPDATE script_texts SET content_loc2='Val\'kyrs, votre maître vous appelle !' WHERE entry=-1631166; /* content: Val\'kyr, your master calls! */
UPDATE script_texts SET content_loc2='Deuillegivre a faim…' WHERE entry=-1631167; /* content: Frostmourne hungers... */
UPDATE script_texts SET content_loc2='Vous venez pour faire payer Arthas ? Pour anéantir le roi-liche ?' WHERE entry=-1631168; /* content: You have come to bring Arthas to justice? To see the Lich King destroyed? */
UPDATE script_texts SET content_loc2='Aidez-moi à détruire ces âmes torturées ! Ensemble, nous pouvons relâcher l\'étreinte de l\'épée et affaiblir le roi-liche de l\'intérieur !' WHERE entry=-1631170; /* content: Aid me in destroying these tortured souls! Together we will loosen Frostmourne\'s hold and weaken the Lich King from within! */
UPDATE script_texts SET content_loc2='Deuillegivre se repaît des âmes de vos alliés morts !' WHERE entry=-1631172; /* content: Frostmourne feeds on the soul of your fallen ally! */
UPDATE script_texts SET content_loc2='APOCALYPSE !' WHERE entry=-1631173; /* content: APOCALYPSE! */
UPDATE script_texts SET content_loc2='À genoux devant votre seigneur et maître !' WHERE entry=-1631174; /* content: Bow down before your lord and master! */
UPDATE script_texts SET content_loc2='La fin est venue !' WHERE entry=-1631177; /* content: The end has come! */
UPDATE script_texts SET content_loc2='Et maintenant, regarde-moi les relever de la mort pour qu\'ils deviennent les maîtres du Fléau. Ils vont plonger ce monde dans le chaos et la destruction. Azeroth va tomber sous leurs coups, et tu seras la première victime.' WHERE entry=-1631182; /* content: Watch now as I raise them from the dead to become masters of the Scourge. They will shroud this world in chaos and destruction. Azeroth\'s fall will come at their hands -- and you will be the first to die. */
UPDATE script_texts SET content_loc2='Quelle délicieuse ironie.' WHERE entry=-1631183; /* content: I delight in the irony. */
UPDATE script_texts SET content_loc2='Impossible…' WHERE entry=-1631185; /* content: Impossible... */
UPDATE script_texts SET content_loc2='C\'est fini, Arthas ! Ta haine ne dévorera pas une vie de plus !' WHERE entry=-1631186; /* content: No more, Arthas! No more lives will be consumed by your hatred! */
UPDATE script_texts SET content_loc2='Enfin libre ! C\'est terminé, mon fils. L\'heure du jugement est venue.' WHERE entry=-1631187; /* content: Free at last! It is over, my son. This is the moment of reckoning. */
UPDATE script_texts SET content_loc2='Debout, champions de la Lumière !' WHERE entry=-1631188; /* content: Rise up, champions of the Light! */
UPDATE script_texts SET content_loc2='Me voilà devant eux comme le loup devant les agneaux, et ils n\'ont pas peur.' WHERE entry=-1631190; /* content: Now I stand, the lion before the lambs... and they do not fear. */
UPDATE script_texts SET content_loc2='%s est pris de frénésie !' WHERE entry=-1631193; /* content: %s goes into a frenzy! */
UPDATE script_texts SET content_loc2='Les bêtes de sang |2 %s sentent l\'Odeur du sang !' WHERE entry=-1631194; /* content: %s\'s Blood Beasts gain the scent of blood! */
UPDATE script_texts SET content_loc2='Quel dommage...' WHERE entry=-1631196; /* content: Such a pity... */
UPDATE script_texts SET content_loc2='%s se met à incanter un Vortex de choc surpuissant !' WHERE entry=-1631198; /* content: %s begins casting Empowered Shock Vortex! */
UPDATE script_texts SET content_loc2='De nouvelles âmes pour alimenter le moteur !' WHERE entry=-1632000; /* content: More souls to power the engine! */
UPDATE script_texts SET content_loc2='Une nouvelle âme vient renforcer nos rangs.' WHERE entry=-1632003; /* content: Another soul to strengthen the host! */
UPDATE script_texts SET content_loc2='Mon âme vous appartient, maître.' WHERE entry=-1632004; /* content: My soul for you, master. */
UPDATE script_texts SET content_loc2='Le vortex des moissonnés vous appelle !' WHERE entry=-1632005; /* content: The vortex of the harvested calls to you! */
