UPDATE script_texts SET content_loc2='Le garde de destruction gagne en puissance.' WHERE entry=-1000664;
UPDATE script_texts SET content_loc2='Le garde de destruction est complètement chargé.' WHERE entry=-1000665;
UPDATE script_texts SET content_loc2='Je sens l\'OMBRE dans vos cœurs. Il ne peut y avoir de repos pour les vilains !' WHERE entry=-1000666;
UPDATE script_texts SET content_loc2='Vos âmes maléfiques nourriront ma puissance !' WHERE entry=-1000667;
UPDATE script_texts SET content_loc2='%s libère son ultime bouffée d\'énergie dans la pierre runique toute proche, ce qui la réactive.' WHERE entry=-1000668;
UPDATE script_texts SET content_loc2='Monstre ! Affronte la puissance de Cénarius !' WHERE entry=-1000676;
UPDATE script_texts SET content_loc2='%s éclate d\'un rire sinistre.' WHERE entry=-1000677;
UPDATE script_texts SET content_loc2='Ma rédemption ? Tu es courageux, petit être. Ma rédemption viendra par la volonté de mon dieu.' WHERE entry=-1000680;
UPDATE script_texts SET content_loc2='%s rugit furieusement.' WHERE entry=-1000681;
UPDATE script_texts SET content_loc2='Dépêchez-vous, $n ! Nous devons nous mettre à couvert !' WHERE entry=-1000682;
UPDATE script_texts SET content_loc2='Par pitié, champion, protégez notre peuple.' WHERE entry=-1000683;
UPDATE script_texts SET content_loc2='Levez-vous, serviteurs du Cauchemar ! Levez-vous et détruisez ce monde ! Ne laissez pas de survivants…' WHERE entry=-1000684;
UPDATE script_texts SET content_loc2='Vous avez vaincu mes serviteurs ? Alors affrontez-moi, mortels !' WHERE entry=-1000687;
UPDATE script_texts SET content_loc2='Tu vois, Remulos, comme ils tombent vite devant moi ? Tu ne peux pas m\'arrêter, imbécile. Remets-moi le druide et ce sera terminé…' WHERE entry=-1000688;
UPDATE script_texts SET content_loc2='Élune, entends mes prières. Accorde-nous la sérénité ! Veille sur ceux qui sont tombés…' WHERE entry=-1000689;
UPDATE script_texts SET content_loc2='Soignez les blessés, mes sœurs !' WHERE entry=-1000690;
UPDATE script_texts SET content_loc2='Vous serez pardonné, Eranikus… Élune vous aimera toujours. Libérez-vous des liens qui vous asservissent !' WHERE entry=-1000692;
UPDATE script_texts SET content_loc2='Je… je sens… je sens qu\'Élune me touche de nouveau… Elle me sourit… Oui… Je…' WHERE entry=-1000696;
UPDATE script_texts SET content_loc2='%s met un genou à terre.' WHERE entry=-1000698;
UPDATE script_texts SET content_loc2='Qu\'Élune soit louée… Eranikus est racheté.' WHERE entry=-1000699;
UPDATE script_texts SET content_loc2='Mais… Malfurion, Cénarius, Ysera… ils continuent à se battre. Ils ont besoin de moi. Je dois regagner le Rêve immédiatement.' WHERE entry=-1000701;
UPDATE script_texts SET content_loc2='Héros… j\'ai ce que vous cherchez. Remulos, veillez à ce que notre champion reçoive le fragment du Vol vert. Puisse-t-il servir, une fois encore, à vaincre le mal.' WHERE entry=-1000703;
UPDATE script_texts SET content_loc2='Ce sera fait, Eranikus. Reposez-vous, ancien.' WHERE entry=-1000704;
UPDATE script_texts SET content_loc2='Votre monde connaîtra une fin terrible. Le Cauchemar s\'abat sur vous !' WHERE entry=-1000706;
UPDATE script_texts SET content_loc2='Cette lumière bleue... C\'est étrange. Qu\'est-ce que ça veut dire, à votre avis ?' WHERE entry=-1000707;
UPDATE script_texts SET content_loc2='On l\'a trouvé !' WHERE entry=-1000708;
UPDATE script_texts SET content_loc2='C\'est l\'endroit. Allumons-le !' WHERE entry=-1000710;
UPDATE script_texts SET content_loc2='Cherchons le suivant.' WHERE entry=-1000711;
UPDATE script_texts SET content_loc2='Nous devons continuer, maintenant.' WHERE entry=-1000712;
UPDATE script_texts SET content_loc2='Il est temps de franchir la dernière étape. Nous devons activer l\'autel.' WHERE entry=-1000713;
UPDATE script_texts SET content_loc2='Je lirai les mots gravés dans la pierre et vous devrez trouver un moyen de l\'éclairer.' WHERE entry=-1000714;
UPDATE script_texts SET content_loc2='L\'autel luit ! Nous avons réussi !' WHERE entry=-1000715;
UPDATE script_texts SET content_loc2='Que se passe-t-il ? Regardez !' WHERE entry=-1000716;
UPDATE script_texts SET content_loc2='Cela fait bien des années...' WHERE entry=-1000717;
UPDATE script_texts SET content_loc2='Qui a dérangé l\'autel de la déesse ?' WHERE entry=-1000718;
UPDATE script_texts SET content_loc2='Prêtresses, veuillez pardonner notre intrusion. Nous n\'avons pas de mauvaise intention.' WHERE entry=-1000719;
UPDATE script_texts SET content_loc2='Nous voulons seulement savoir pourquoi les Indomptables gardent cette zone...' WHERE entry=-1000720;
UPDATE script_texts SET content_loc2='Cette gemme permettait naguère une communication directe avec Élune elle-même.' WHERE entry=-1000724;
UPDATE script_texts SET content_loc2='À travers la gemme, Élune canalisa sa sagesse infinie...' WHERE entry=-1000725;
UPDATE script_texts SET content_loc2='Réalisant que la gemme devait être protégée, nous nous sommes tournés vers la déesse en personne.' WHERE entry=-1000726;
UPDATE script_texts SET content_loc2='Peu après, nous avons commencé à avoir des visions d\'une créature... Une créature dotée des plumes d\'une chouette, mais avec la volonté et la puissance d\'un ours...' WHERE entry=-1000727;
UPDATE script_texts SET content_loc2='Les Indomptables sont magiques par nature. Ce pouvoir leur a été donné par la déesse.' WHERE entry=-1000732;
UPDATE script_texts SET content_loc2='Sachez que, où que vous les rencontriez dans ce monde, ils y sont pour protéger quelque chose d\'important, car telle est la mission qui leur a été confiée il y a longtemps.' WHERE entry=-1000733;
UPDATE script_texts SET content_loc2='Souvenez-vous de ce que nous vous avons montré...' WHERE entry=-1000734;
UPDATE script_texts SET content_loc2='Adieu.' WHERE entry=-1000735;
UPDATE script_texts SET content_loc2='Je veux rester ici et réfléchir à ce que nous avons vu. Allez voir Erelas et faites-lui part de ce que nous avons appris.' WHERE entry=-1000737;
UPDATE script_texts SET content_loc2='%s commence à psalmodier un sort étrange...' WHERE entry=-1000738;
UPDATE script_texts SET content_loc2='Il existe un moyen…' WHERE entry=-1000742;
UPDATE script_texts SET content_loc2='%s hoche la tête avec un air entendu.' WHERE entry=-1000743;
UPDATE script_texts SET content_loc2='%s échange un regard avec ses compagnons.' WHERE entry=-1000745;
UPDATE script_texts SET content_loc2='Ne vous éloignez pas.' WHERE entry=-1000754;
UPDATE script_texts SET content_loc2='Les sables du temps s\'arrêteront, mais seulement pour un moment ! Je vais invoquer la barrière.' WHERE entry=-1000755;
UPDATE script_texts SET content_loc2='ACHEVEZ LE SORT, FORTERAMURE ! JE NE MAINTIENDRAI PAS LES GLYPHES DE SAUVEGARDE EN PLACE PLUS LONGTEMPS ! INVOQUEZ LES RACINES !' WHERE entry=-1000756;
UPDATE script_texts SET content_loc2='Les Anciens guident ma main… Sortez de votre torpeur ! ÉVEILLEZ-VOUS ET SCELLEZ CET ENDROIT MAUDIT !' WHERE entry=-1000757;
UPDATE script_texts SET content_loc2='%s s\'agenouille, épuisé.' WHERE entry=-1000758;
UPDATE script_texts SET content_loc2='C\'est… c\'est terminé, seigneur Forteramure. Nous sommes victorieux. Mais c\'est une victoire chèrement payée.' WHERE entry=-1000759;
UPDATE script_texts SET content_loc2='Après tout ce que mon peuple a vu et souffert, dragon, vous voulez que j\'endosse un autre fardeau ? Vous avez perdu la raison.' WHERE entry=-1000763;
UPDATE script_texts SET content_loc2='%s jette le Sceptre des Sables changeants dans la barrière. Il se brise.' WHERE entry=-1000765;
UPDATE script_texts SET content_loc2='%s secoue la tête, l\'air désappointé.' WHERE entry=-1000768;
UPDATE script_texts SET content_loc2='On dirait que les ennuis ne sont pas loin. Gardez la tête haute, les voilà !' WHERE entry=-1000772;
UPDATE script_texts SET content_loc2='Des assassins qui viennent de ce culte que vous avez trouvé… Partons d\'ici avant que quelqu\'un d\'autre ne nous découvre ici.' WHERE entry=-1000773;
UPDATE script_texts SET content_loc2='Attendez ! Je sens une présence maléfique... Des morts-vivants !' WHERE entry=-1000774;
UPDATE script_texts SET content_loc2='Ils surgissent de partout, aujourd\'hui. Continuons notre route, sinon nous risquons de trouver encore d\'autres choses qui voudront ma mort.' WHERE entry=-1000776;
UPDATE script_texts SET content_loc2='Encore ces trois-là ?' WHERE entry=-1000777;
UPDATE script_texts SET content_loc2='Vous n\'êtes plus aussi sûr de vous sans le Purificateur, hein ?' WHERE entry=-1000778;
UPDATE script_texts SET content_loc2='Je vais vous achever pour de bon cette fois !' WHERE entry=-1000779;
UPDATE script_texts SET content_loc2='Bien joué ! Je vais me débrouiller sans aide à partir de maintenant. N\'oubliez pas de parler à Delgren quand vous retournerez au Poste de Maestra en Orneval.' WHERE entry=-1000780;
UPDATE script_texts SET content_loc2='Je savais que Lurielle m\'enverrait de l\'aide ! Merci mon $gami:amie;, et remerciez également Lurielle de ma part !' WHERE entry=-1000781;
UPDATE script_texts SET content_loc2='Merci. Je pensais mourir sans jamais revoir mes sœurs !' WHERE entry=-1000783;
UPDATE script_texts SET content_loc2='Nous avons réussi, mon $gami:amie; ! N\'oubliez pas d\'aller à la recherche de Terenthis pour lui annoncer que nous sommes sains et saufs. Merci encore.' WHERE entry=-1000790;
UPDATE script_texts SET content_loc2='Ils arrivent.' WHERE entry=-1000791;
UPDATE script_texts SET content_loc2='Nous ne mourrons pas si facilement !' WHERE entry=-1000793;
UPDATE script_texts SET content_loc2='Sommeil… tellement sommeil…' WHERE entry=-1000796;
UPDATE script_texts SET content_loc2='%s pousse un croassement et se dirige vers le Voile Shalas. Vite, suivez-le !' WHERE entry=-1000797;
UPDATE script_texts SET content_loc2='%s s\'arrête brièvement à côté de l\'arbre puis se dirige à l\'intérieur.' WHERE entry=-1000798;
UPDATE script_texts SET content_loc2='%s semble chercher quelque chose. Il veut que vous le suiviez.' WHERE entry=-1000799;
UPDATE script_texts SET content_loc2='%s s\'envole vers la plateforme en contrebas ! Sautez si vous voulez réussir à suivre. Vite !' WHERE entry=-1000800;
UPDATE script_texts SET content_loc2='%s pousse un grand braillement !' WHERE entry=-1000801;
UPDATE script_texts SET content_loc2='Me voilà enfin débarrassé de cette horrible malédiction ! Merci ! Pouvez-vous prévenir Rilak le Racheté que je vais bien ? Ma mission m\'appelle à Skettis. Terokk doit être vaincu !' WHERE entry=-1000802;
UPDATE script_texts SET content_loc2='Nous lancerons le combat à votre signal.' WHERE entry=-1000804;
UPDATE script_texts SET content_loc2='Nous libérerons les éléments de ton étreinte !' WHERE entry=-1000805;
UPDATE script_texts SET content_loc2='J\'entends ce que tu entends, mon frère. Regarde derrière toi…' WHERE entry=-1000807;
UPDATE script_texts SET content_loc2='Ils sont rachetés ! Nous avons donc gagné ?' WHERE entry=-1000808;
UPDATE script_texts SET content_loc2='Tout est maintenant comme il se doit, chaman. Vous avez bien travaillé.' WHERE entry=-1000809;
UPDATE script_texts SET content_loc2='Oui… Bien assez pour les éléments qui sont présents ici, mais un autre encore connaît la Formule… Les esprits du feu sont agités… Si cette force n\'est pas arrêtée, le monde d\'origine de ces mortels cessera d\'exister.' WHERE entry=-1000810;
UPDATE script_texts SET content_loc2='Au revoir, mortels… Le soigneterre sait ce que ressent le feu…' WHERE entry=-1000811;
UPDATE script_texts SET content_loc2='Les hommes de Cœur-fendu confient leurs armes au soigneterre Torlok.' WHERE entry=-1000813;
UPDATE script_texts SET content_loc2='Donnez ceci aux héros qui ont permis cette victoire.' WHERE entry=-1000814;
UPDATE script_texts SET content_loc2='Guéris du mal qui t\'habite !' WHERE entry=-1000815;
UPDATE script_texts SET content_loc2='Nous sommes sauvés ! Les paysans ont échappé au Fléau !' WHERE entry=-1000816;
UPDATE script_texts SET content_loc2='J\'ai échoué une fois de plus…' WHERE entry=-1000817;
UPDATE script_texts SET content_loc2='Je retourne d\'où je viens, et je me retrouve ici une fois de plus pour revivre la même tragédie épique.' WHERE entry=-1000818;
UPDATE script_texts SET content_loc2='Le Fléau est sur nous ! Fuyez ! Fuyez si vous voulez vivre !' WHERE entry=-1000819;
UPDATE script_texts SET content_loc2='Aidez-nous, par pitié ! Le prince est devenu fou !' WHERE entry=-1000820;
UPDATE script_texts SET content_loc2='Le signal est parti. Il devrait arriver incessamment.' WHERE entry=-1000822;
UPDATE script_texts SET content_loc2='Salut !' WHERE entry=-1000823;
UPDATE script_texts SET content_loc2='Je savais que vous viendriez, $n. C\'est un bonheur de vous revoir enfin, mon $gami:amie;.' WHERE entry=-1000824;
UPDATE script_texts SET content_loc2='En garde, mon $gami:amie; ! Cette femelle dragon n\'abandonnera pas sans combattre.' WHERE entry=-1000825;
UPDATE script_texts SET content_loc2='Cela fut prédit il y a bien longtemps à Karazhan, monstre ! Me voilà, qui apporte la justice !' WHERE entry=-1000826;
UPDATE script_texts SET content_loc2='Emparez-vous de lui ! Saisissez-vous de ce criminel et de ses alliés !' WHERE entry=-1000827;
UPDATE script_texts SET content_loc2='Reginald, vous savez que je ne peux pas vous laisser passer.' WHERE entry=-1000828;
UPDATE script_texts SET content_loc2='Agissez selon ce qui vous semble juste, Marcus. Nous servons tous les deux sous les ordres de Turalyon. C\'est lui qui a fait les hommes que nous sommes aujourd\'hui. S\'est-il trompé sur mon compte ? Croyez-vous vraiment que mon intention soit de briser notre alliance ? Faire honte à nos héros ?' WHERE entry=-1000829;
UPDATE script_texts SET content_loc2='Me retenir ici n\'est pas la bonne décision, Marcus.' WHERE entry=-1000830;
UPDATE script_texts SET content_loc2='%s semble perdu dans ses pensées.' WHERE entry=-1000831;
UPDATE script_texts SET content_loc2='La honte m\'envahit, je ne sais plus où j\'ai la tête. Ce n\'est pas vous qui plongerez dans la honte un héros de légende. Non, c\'est moi ! Et le reste des politiciens corrompus. Ils emplissent nos vies de promesses creuses, de mensonges sans fin.' WHERE entry=-1000832;
UPDATE script_texts SET content_loc2='Nous sommes une honte pour nos ancêtres. Pour ceux que nous avons perdus... Pardonnez-moi, Reginald.' WHERE entry=-1000833;
UPDATE script_texts SET content_loc2='Votre vigilance leur fait honneur. Vous êtes inébranlable dans votre allégeance. Je ne doute pas que vous puissiez faire un sacrifice aussi grand pour votre peuple que les héros que vous servez.' WHERE entry=-1000834;
UPDATE script_texts SET content_loc2='Il est désormais temps que son règne prenne fin.' WHERE entry=-1000835;
UPDATE script_texts SET content_loc2='Baissez vos armes ! Ne voyez-vous pas qu\'il y a des héros parmi nous ?' WHERE entry=-1000836;
UPDATE script_texts SET content_loc2='Faites place ! Laissez-les passer !' WHERE entry=-1000837;
UPDATE script_texts SET content_loc2='Reginald Windsor ne doit pas être blessé ! Il doit passer sans être touché !' WHERE entry=-1000838;
UPDATE script_texts SET content_loc2='En avant, Reginald. Que la Lumière guide votre main !' WHERE entry=-1000839;
UPDATE script_texts SET content_loc2='Merci mon ami. Vous avez agi avec raison.' WHERE entry=-1000840;
UPDATE script_texts SET content_loc2='Suivez-moi les amis ! Au Donjon de Hurlevent !' WHERE entry=-1000841;
UPDATE script_texts SET content_loc2='Que la lumière vous accompagne !' WHERE entry=-1000842;
UPDATE script_texts SET content_loc2='Nous ne sommes rien d\'autre que de la poussière sous vos pieds.' WHERE entry=-1000843;
UPDATE script_texts SET content_loc2='… nerfs de thorium.' WHERE entry=-1000844;
UPDATE script_texts SET content_loc2='Faites place !' WHERE entry=-1000845;
UPDATE script_texts SET content_loc2='Un moment dont je me souviendrai toute ma vie.' WHERE entry=-1000847;
UPDATE script_texts SET content_loc2='Vous êtes une inspiration pour nous tous.' WHERE entry=-1000848;
UPDATE script_texts SET content_loc2='Soyez braves, mes amis. Le reptile se battra sauvagement. Avec l\'énergie du désespoir... Quand vous serez prêts, faites-le-moi savoir.' WHERE entry=-1000849;
UPDATE script_texts SET content_loc2='EN AVANT !' WHERE entry=-1000850;
UPDATE script_texts SET content_loc2='Courez tant que vous pouvez, Majesté. Elle n\'est pas ce que vous croyez...' WHERE entry=-1000851;
UPDATE script_texts SET content_loc2='Dans le hall du coffre, sire.' WHERE entry=-1000852;
UPDATE script_texts SET content_loc2='La mascarade est terminée, dame Prestor. Ou dois-je désormais vous appeler par votre vrai nom... Onyxia...' WHERE entry=-1000853;
UPDATE script_texts SET content_loc2='%s rit.' WHERE entry=-1000854;
UPDATE script_texts SET content_loc2='Vous serez incarcéré et jugé pour trahison, Windsor. Je regarderai avec joie l\'exécution qui suivra votre inévitable condamnation à mort !' WHERE entry=-1000855;
UPDATE script_texts SET content_loc2='Vous n\'échapperez pas à votre destin, Onyxia. Il y a une prophétie, une vision qui vibre dans les grands halls de Karazhan. C\'est maintenant qu\'elle prend fin...' WHERE entry=-1000857;
UPDATE script_texts SET content_loc2='Les Sombrefers pensaient que ces tablettes étaient codées. Mais ce n\'est pas un code, c\'est la langue des anciens dragons.' WHERE entry=-1000859;
UPDATE script_texts SET content_loc2='Écoutez, dragon. Laissez la vérité se répandre dans ces halls.' WHERE entry=-1000860;
UPDATE script_texts SET content_loc2='%s s\'étrangle.' WHERE entry=-1000862;
UPDATE script_texts SET content_loc2='Curieux... Windsor, dans cette vision, est-ce que vous survivez ? Je pose la question parce que je peux vous assurer que vous allez mourir. Ici et maintenant.' WHERE entry=-1000863;
UPDATE script_texts SET content_loc2='Pourriture de dragon ! Gardes ! Gardes ! Saisissez-vous de ce monstre !' WHERE entry=-1000864;
UPDATE script_texts SET content_loc2='Ouiiii... Gardes, venez aider votre seigneur !' WHERE entry=-1000865;
UPDATE script_texts SET content_loc2='QU\'ELLE NE S\'ÉCHAPPE PAS !' WHERE entry=-1000866;
UPDATE script_texts SET content_loc2='Vous l\'avez trahi, mortels... Adieu !' WHERE entry=-1000868;
UPDATE script_texts SET content_loc2='Reginald... Je... Je regrette...' WHERE entry=-1000869;
UPDATE script_texts SET content_loc2='Bol... Bolvar... le médaillon... utilisez...' WHERE entry=-1000870;
UPDATE script_texts SET content_loc2='%s meurt.' WHERE entry=-1000871;
UPDATE script_texts SET content_loc2='Je connais la route, insecte. Pas besoin de me houspiller comme si j\'étais du bétail.' WHERE entry=-1000873;
UPDATE script_texts SET content_loc2='Vous ne pensez pas vous en sortir comme ça, hein ? Ils vont venir pour moi et vous allez payer pour votre insolence.' WHERE entry=-1000874;
UPDATE script_texts SET content_loc2='FONCEZ-LEUR DESSUS !' WHERE entry=-1000875;
UPDATE script_texts SET content_loc2='Je doute que vous ayez autant de chance la prochaine fois que vous rencontrerez mes frères.' WHERE entry=-1000876;
UPDATE script_texts SET content_loc2='Ils viennent pour vous, imbécile !' WHERE entry=-1000877;
UPDATE script_texts SET content_loc2='Que croyez-vous avoir accompli, imbécile ? A l\'heure où je parle, les armées Rochenoire se préparent à détruire votre monde.' WHERE entry=-1000878;
UPDATE script_texts SET content_loc2='Ils volent d\'une aile sombre. Préparez-vous à mourir !' WHERE entry=-1000879;
UPDATE script_texts SET content_loc2='Le pire reste à venir !' WHERE entry=-1000880;
UPDATE script_texts SET content_loc2='%s rit.' WHERE entry=-1000881;
UPDATE script_texts SET content_loc2='Votre heure est venue, misérable insecte.' WHERE entry=-1000882;
UPDATE script_texts SET content_loc2='%s lève sa lourde hache au-dessus de Grark.' WHERE entry=-1000885;
UPDATE script_texts SET content_loc2='%s lève le bras et le rabaisse.' WHERE entry=-1000886;
UPDATE script_texts SET content_loc2='Exécutez-le.' WHERE entry=-1000887;
UPDATE script_texts SET content_loc2='Soldat, allez faire votre rapport à Kargath immédiatement !' WHERE entry=-1000888;
UPDATE script_texts SET content_loc2='%s se soumet.' WHERE entry=-1000889;
UPDATE script_texts SET content_loc2='Vous êtes $gvenu:venue; jouer ? Alors jouons !' WHERE entry=-1000890;
UPDATE script_texts SET content_loc2='Allons-y… Couvrez-moi, et je livrerai le colis.' WHERE entry=-1000891;
UPDATE script_texts SET content_loc2='Un défenseur est tombé !' WHERE entry=-1000892;
UPDATE script_texts SET content_loc2='On m\'attaque ! Je répète, on m\'attaque !' WHERE entry=-1000892;
UPDATE script_texts SET content_loc2='Il faut que je change de boulot.' WHERE entry=-1000893;
UPDATE script_texts SET content_loc2='Par le second soleil de K\'aresh, regardez-moi cet endroit ! Je ne peux que trop imaginer ce que Salhadaar manigance. Venez, continuons.' WHERE entry=-1000894;
UPDATE script_texts SET content_loc2='Pour la Horde !' WHERE entry=-1000895;
UPDATE script_texts SET content_loc2='Défendez les fortins !' WHERE entry=-1000896;
UPDATE script_texts SET content_loc2='Nos ennemis mourront !' WHERE entry=-1000897;
UPDATE script_texts SET content_loc2='Protégez la canalisation ! Arrêtez les intrus !' WHERE entry=-1000898;
UPDATE script_texts SET content_loc2='C\'est fait ! Reculez ! Reculez !' WHERE entry=-1000899;
UPDATE script_texts SET content_loc2='Merci, mon $gami:amie;.M\'aiderez-vous à sortir d\'ici ?' WHERE entry=-1000901;
UPDATE script_texts SET content_loc2='Nous ne sommes pas très loin du poste de garde du Protectorat, $n. Par là !' WHERE entry=-1000902;
UPDATE script_texts SET content_loc2='Commandant ! Cette créature de chair est venue à mon secours !' WHERE entry=-1000903;
UPDATE script_texts SET content_loc2='%s hume l\'air. Il doit y avoir un tubercule tout près !' WHERE entry=-1000907;
UPDATE script_texts SET content_loc2='%s commence à creuser.' WHERE entry=-1000908;
UPDATE script_texts SET content_loc2='%s pousse des glapissements de plaisir pour sa découverte.' WHERE entry=-1000909;
UPDATE script_texts SET content_loc2='Commençons-nous, $gmon ami:mon amie; ?' WHERE entry=-1000910;
UPDATE script_texts SET content_loc2='Très bien, poursuivons.' WHERE entry=-1000912;
UPDATE script_texts SET content_loc2='Soyez les bienvenus, mes amis !' WHERE entry=-1000917;
UPDATE script_texts SET content_loc2='%s se tourne vers vous.' WHERE entry=-1000920;
UPDATE script_texts SET content_loc2='Le chamanisme vous a $gmené:menée; où vous êtes… L\'odeur en flotte dans l\'air. *Le roi-liche s\'esclaffe* Moi-même, je fus chaman, autrefois.' WHERE entry=-1000921;
UPDATE script_texts SET content_loc2='Devons-nous vous le préparer, mon seigneur ?' WHERE entry=-1000922;
UPDATE script_texts SET content_loc2='Non, mon serviteur. Celui-là n\'est pas prêt.' WHERE entry=-1000923;
UPDATE script_texts SET content_loc2='Le sentez-vous, $gmortel:mortelle; ? La mort ruisselle de moi, et enveloppe tout ce que je touche. Un seul claquement de doigts et votre âme dépérira, damnée pour l\'éternité.' WHERE entry=-1000924;
UPDATE script_texts SET content_loc2='Mais… Le temps n\'est pas encore venu pour vous de servir le roi-liche. Oui, une plus haute destinée vous attend. La puissance… Vous devez acquérir plus de puissance avant de venir me servir.' WHERE entry=-1000925;
UPDATE script_texts SET content_loc2='Regarde, val\'kyr. Vois ce qui se passe quand j\'exerce une pression. Vois-tu qu\'il n\'est pas encore mûr ? Regarde-le se percer et tomber au sol, sans vie.' WHERE entry=-1000926;
UPDATE script_texts SET content_loc2='Obstination ou stupidité ? Peu importe. Que cela vous serve de leçon, $gmortel:mortelle; !' WHERE entry=-1000927;
UPDATE script_texts SET content_loc2='%s fait geste à tout le monde de se taire.' WHERE entry=-1000928;
UPDATE script_texts SET content_loc2='Un sursaut d\'horreur parcourt la foule.' WHERE entry=-1000931;
