UPDATE locales_npc_text SET Text2_0_loc3='Ihr wollt einen Greifenritt unternehmen, was? Dungar hält seine Vögel oben auf dem Wall des Handelsdistrikts. Denkt aber immer daran, Euch festzuhalten!',Text2_1_loc3='Ihr wollt einen Greifenritt unternehmen, was? Dungar hält seine Vögel oben auf dem Wall des Handelsdistrikts. Denkt aber immer daran, Euch festzuhalten!' WHERE entry=879; /* Text2_0: Lookin\' to take a gryphon ride, eh? Dungar keeps his birds up on the rampart in the Trade District. Just remember to hold on tight! */ /* Text2_1: Lookin\' to take a gryphon ride, eh? Dungar keeps his birds up on the rampart in the Trade District. Just remember to hold on tight! */
