UPDATE locales_npc_text SET Text0_0_loc3='Geht zur Jägerhalle im Tal der Ehre, westlich des Kolosseums. Sprecht dort mit Ormak Grimmschlag.',Text0_1_loc3='Geht schnurstracks zum nordwestlichen Teil des Tals der Ehre. Dort findet Ihr die Jägerhalle.',Text1_0_loc3='Geht schnurstracks zum nordwestlichen Teil des Tals der Ehre. Dort findet Ihr die Jägerhalle.',Text1_1_loc3='Geht schnurstracks zum nordwestlichen Teil des Tals der Ehre. Dort findet Ihr die Jägerhalle.' WHERE entry=2559; /* Text0_0: Go to the Hunter\'s Hall in the Valley of Honor, west of the Coliseum.  Talk to Ormak Grimshot there. */ /* Text0_1: Shoot straight to the northwest section of the Valley of Honor.  There you will find the Hunter\'s Hall. */ /* Text1_0: Shoot straight to the northwest section of the Valley of Honor.  There you will find the Hunter\'s Hall. */ /* Text1_1: Shoot straight to the northwest section of the Valley of Honor.  There you will find the Hunter\'s Hall. */
