UPDATE locales_npc_text SET Text0_0_loc3='Ich habe kaum Vorräte für Aufrüstungen.' WHERE entry=6784; /* Text0_0: I barely have any supplies for upgrades. */
UPDATE locales_npc_text SET Text0_0_loc3='Unsere Truppen sind nicht aufgerüstet, und ich habe nicht genügend Vorräte für Aufrüstungen.' WHERE entry=6785; /* Text0_0: Our units are not upgraded, and I don\'t have enough supplies to upgrade them. */
UPDATE locales_npc_text SET Text0_0_loc3='Unsere Truppen sind zu \'Erfahren\' aufgerüstet, aber ich habe nicht genügend Vorräte, um sie zu \'Veteran\' auszubauen.' WHERE entry=6786; /* Text0_0: Our units are upgraded to Seasoned, but I don\'t have enough supplies to upgrade them to Veteran. */
