UPDATE locales_npc_text SET Text0_0_loc3='Ich liebe das Mondfest! Es ist eine klasse Gelegenheit, mal so richtig mit den Hufen zu stampfen!',Text0_1_loc3='Ich habe keine Ahnung wie diese Feuerwerke funktionieren, aber sie sind sehr hübsch!',Text1_0_loc3='Ich habe keine Ahnung wie diese Feuerwerke funktionieren, aber sie sind sehr hübsch!',Text1_1_loc3='Ich habe keine Ahnung wie diese Feuerwerke funktionieren, aber sie sind sehr hübsch!' WHERE entry=8222; /* Text0_0: I love the Lunar Festival!  It\'s a great place to let your hooves kick up dust! */ /* Text0_1: I don\'t know how these fireworks work, but they sure are pretty! */ /* Text1_0: I don\'t know how these fireworks work, but they sure are pretty! */ /* Text1_1: I don\'t know how these fireworks work, but they sure are pretty! */
