UPDATE locales_npc_text SET Text0_0_loc3='Es geht nichts über das Mondfest, um sich die Knochen aufzuwärmen!',Text0_1_loc3='Wenn man seine Finger nicht mehr fühlen kann, muss man mit dem Abfackeln von Feuerwerk ganz schön vorsichtig sein...',Text1_0_loc3='Wenn man seine Finger nicht mehr fühlen kann, muss man mit dem Abfackeln von Feuerwerk ganz schön vorsichtig sein...',Text1_1_loc3='Wenn man seine Finger nicht mehr fühlen kann, muss man mit dem Abfackeln von Feuerwerk ganz schön vorsichtig sein...' WHERE entry=8223; /* Text0_0: There\'s nothing like a little Lunar celebration to warm your bones! */ /* Text0_1: You have to be careful lighting fireworks when you can\'t feel your fingers... */ /* Text1_0: You have to be careful lighting fireworks when you can\'t feel your fingers... */ /* Text1_1: You have to be careful lighting fireworks when you can\'t feel your fingers... */
