UPDATE locales_npc_text SET Text0_0_loc3='<Der Arkanwächter deutet auf den nordwestlichen Teil des Unteren Viertels, wo die Arakkoa von Shattrath wohnen.>' WHERE entry=10516; /* Text0_0: <The Arcane Guardian gestures to the northwest portion of the lower city, where Shattrath\'s arakkoa dwell.> */
