UPDATE locales_page_text SET text_loc3='Bericht zur Lage der Frostmähnentrolle im Gebiet des Eisklammtalss$B$BVorgelegt von Grelin Weißbart, Sondergesandter des Senats$B$BNach eingehendem Studium der Bewegungen der Frostmähnentrolle im Gebiet um das Eisklammtal kann ich nun sagen, dass sie für die Zwergensiedlungen in dem Gebiet keine große Bedrohung darstellen. Mit ein wenig mehr Unterstützung durch die Armee können wir dieser Bedrohung leicht Herr werden. Mit Hilfe der bereits im' WHERE entry=90;
