UPDATE locales_page_text SET text_loc3='<HTML><BODY><BR/><BR/><H1 align="center">Eliza</H1><BR/><BR/><BR/><BR/><P align="center">Mein geliebtes Weib, möge die Erde deinen Hunger stillen.</P></BODY></HTML>' WHERE entry=750;
