UPDATE locales_page_text SET text_loc3='Beschafft Jade. Sie ist an vielen verschiedenen Orten verborgen. Sie ist rar, aber wenn Ihr einen Schmied oder Ingenieur fragt, hat er vielleicht ein oder zwei Stücke übrig.' WHERE entry=911;
