UPDATE locales_page_text SET text_loc3='$N, nachfolgend die Gegenstände, die Ihr zur Herstellung einer Rüstung benötigt:$B$BRauchige Eisenblöcke von den Kobolden der Scherwindmine im Steinkrallengebirge.$B$BPulverisierter Azurit von den Minenarbeitern im Hügelland.$B$BEisenbarren von Minenarbeitern oder von Eisenablagerungen, falls Ihr ein Minenarbeiter seid.$B$BEine Phiole mit Phlogiston von einem Stacheleber namens Roogug im Kral der Klingenhauer.' WHERE entry=931;
