UPDATE locales_page_text SET text_loc3='<HTML><BODY><H1 align="center">Kapitel 3: DER WESTLICHE PYLON</H1><BR/><BR/><IMG src="Interface\Pictures\11482_crystals_mini_west" align="left"/><P align="right">Der westliche<BR/>Pylon akzeptiert<BR/>rote, grüne<BR/>und blaue<BR/>Kristalle.</P><BR/><BR/><BR/><BR/><P align="left">Der Pylon fokussiert auf die Energie, die mit den roten Kristallen als Hauptenergiequelle erzeugt werden kann.</P></BODY></HTML>' WHERE entry=1531;
UPDATE locales_page_text SET text_loc3='<HTML><BODY><H1 align="center">Westliche Kristallkombinationen</H1><BR/><P align="left">Es gibt zwei mögliche Effekte: Einer erhöht Euren Schutz vor Schaden, einer schwächt die Verteidigung des Gegners.</P><BR/><P align="left">KRISTALLBARRIERE: Erfordert rote und grüne Kristalle.<BR/>Das verleiht Euch eine Erhöhung der Rüstung.</P><BR/><P align="left">KRISTALLSCHWÄCHER: Erfordert blaue und rote Kristalle.<BR/>Damit kann die Rüstung eines bestimmten Ziels verringert werden.</P></BODY></HTML>' WHERE entry=1532;
