UPDATE locales_page_text SET text_loc3='<HTML><BODY><H1 align="center">ABHANDLUNG MILITÄRISCHER RÄNGE</H1><BR/><BR/><IMG src="Interface\PvPRankBadges\PvPRankAlliance\" align="left"/><BR/><P align="right">Es folgen</P><P align="right">die militärischen Ränge</P><P align="right">der Allianz,</P><P align="right">gefolgt von</P><P align="right">Informationen die</P><P align="right">den Völkern</P><P align="right">der Horde bekannt sind. Alle</P><P align="right">Angaben sind in zwei</P><P align="right">Abschnitte unterteilt, Offiziere und Soldaten. Dabei werden ihre Ränge jeweils in absteigender Reihenfolge aufgelistet, vom höchsten bis zum niedrigsten Rang. Lang lebe die Allianz!</P></BODY></HTML>' WHERE entry=2654;
UPDATE locales_page_text SET text_loc3='<HTML><BODY><H1 align="center">OFFIZIERSRÄNGE DER ALLIANZ</H1><BR/><P align="center">Teil 1</P><IMG src="InterfacePvPRankBadgesPvPRank14" align="left"/><BR/><P align="right">Großmarschall</P><BR/><BR/><IMG src="InterfacePvPRankBadgesPvPRank13" align="left"/><BR/><P align="right">Feldmarschall</P><BR/><BR/><IMG src="InterfacePvPRankBadgesPvPRank12" align="left"/><BR/><P align="right">Marschall</P><BR/><BR/><IMG src="InterfacePvPRankBadgesPvPRank11" align="left"/><BR/><P align="right">Rittmeister</P><BR/><BR/><IMG src="InterfacePvPRankBadgesPvPRank10" align="left"/><BR/><P align="right">Feldkommandant</P><BR/><BR/></BODY></HTML>' WHERE entry=2655;
