UPDATE locales_page_text SET text_loc3='<HTML><BODY><H1 align="center">OFFIZIERSRÄNGE DER HORDE</H1><BR/><P align="center">Teil 1</P><IMG src="InterfacePvPRankBadgesPvPRank14" align="left"/><BR/><P align="right">Oberster Kriegsfürst</P><BR/><BR/><IMG src="InterfacePvPRankBadgesPvPRank13" align="left"/><BR/><P align="right">Kriegsfürst</P><BR/><BR/><IMG src="InterfacePvPRankBadgesPvPRank12" align="left"/><BR/><P align="right">Kriegsherr</P><BR/><BR/><IMG src="InterfacePvPRankBadgesPvPRank11" align="left"/><BR/><P align="right">Sturmreiter</P><BR/><BR/><IMG src="InterfacePvPRankBadgesPvPRank10" align="left"/><BR/><P align="right">Feldherr</P><BR/><BR/></BODY></HTML>' WHERE entry=2658;
UPDATE locales_page_text SET text_loc3='Blickt Ihr in das Auge, erfüllen Bilder eines großen Kampfes Euren Geist.$B$BDieser furchteinflößende Konflikt scheint irgendwo in Lordaeron stattzufinden.$B$BIhr könnt nur schwerlich eine einzelne, weibliche Silhouette ausmachen, die inmitten tausender von Leichen steht und eine Heerschar der Geißel abwehrt. Sie ist hoffnungslos unterlegen.$B$BFäden des Lichts entströmen ihren Händen und schneiden durch hunderte von Untoten.' WHERE entry=2659;
UPDATE locales_page_text SET text_loc3='<HTML><BODY><H1 align="center">SOLDATENRÄNGE DER HORDE</H1><BR/><IMG src="Interface\PvPRankBadges\PvPRank01"/><BR/><P align="right">Rottenmeister</P><BR/><BR/><IMG src="Interface\PvPRankBadges\PvPRank04"/><BR/><P align="right">Schlachtrufer</P><BR/><BR/><IMG src="Interface\PvPRankBadges\PvPRank03"/><BR/><P align="right">Waffenträger</P><BR/><BR/><IMG src="Interface\PvPRankBadges\PvPRank02"/><BR/><P align="right">Grunzer</P><BR/><BR/><IMG src="Interface\PvPRankBadges\PvPRank01"/><BR/><P align="right">Späher</P><BR/><BR/></BODY></HTML>' WHERE entry=2660;
UPDATE locales_page_text SET text_loc3='<HTML><BODY><H1 align="center">MILITÄRISCHE RÄNGE DER HORDE &amp; ALLIANZ</H1><BR/><BR/><IMG src="InterfacePvPRankBadgesPvPRankHorde" align="left"/><BR/><P align="right">Throm\'ka!  In diesem</P><P align="right">buch findet Ihr</P><P align="right">zahlreiche Auflistungen</P><P align="right">der militärischen Ränge</P><P align="right">der Horde und</P><P align="right">der erbärmlichen Allianz.</P><P align="right">Wir beginnen mit der</P><P align="right">Horde, dann folgt die Allianz,</P><P align="right">jeweils mit einer Liste der Offiziers- und Soldatenränge. Gebührenderweise werden die Stärksten zu Beginn und die Schwächeren darunter aufgelistet.</P></BODY></HTML>' WHERE entry=2661;
