UPDATE locales_page_text SET text_loc3='<HTML><BODY><BR/><P>In sehr freier Übersetzung...</P><BR/><P>Beim Licht, beim Licht der Sonne,</P><P>Kinder des Blutes,</P><P>Unsere Feinde fallen ein.</P><P>Kinder des Blutes,</P><P>Beim Licht,</P><P>Versagende Kinder des Blutes</P><P>Sie fallen ein.</P><P>Oh Kinder des Blutes,</P><P>Beim Licht der Sonne.</P><P>Versagende Kinder des Blutes,</P><P>Sie fallen ein.</P><P>Oh Kinder des Blutes,</P><P>Beim Licht der Sonne,</P><P>Der Sonne.</P></BODY></HTML>' WHERE entry=2991;
UPDATE locales_page_text SET text_loc3='<HTML><BODY><BR/><P>Thalassische Version...</P><BR/><P>Anar\'alah, anar\'alah belore,</P><P>Sin\'dorei,</P><P>Shindu fallah na.</P><P>Sin\'dorei,</P><P>Anar\'alah,</P><P>Shindu sin\'dorei,</P><P>Shindu fallah na.</P><P>Sin\'dorei,</P><P>Anar\'alah belore.</P><P>Shindu sin\'dorei,</P><P>Shindu fallah na.</P><P>Sin\'dorei,</P><P>Anar\'alah belore,</P><P>Belore.</P></BODY></HTML>' WHERE entry=2992;
UPDATE locales_page_text SET text_loc3='Mein verehrter Kanzler,$B$Bdie Aufgabe, die Riesen von Schattenmond für unsere Sache zu gewinnen, liegt nun in Euren fähigen Händen. Lasst Euren Blutelfencharme spielen, damit sie Dinge so wie ich sehen. Sollte dies nicht funktionieren, dann löscht sie alle ein für alle Mal aus...$B$B-Fürst Illidan Sturmgrimm' WHERE entry=2993;
