UPDATE creature_ai_texts SET content_loc3='Wenn einer von euch Peons eine Pause braucht, der Friedhof ist gleich dort drüben.' WHERE entry=-315; /* content: Any time any of you peons want to take a break, the graveyard is right over there. */
