UPDATE creature_ai_texts SET content_loc3='Die Bruderschaft der Defias wird siegen! Ein kleiner Abenteurer wie Ihr wird uns nicht aufhalten!' WHERE entry=-655; /* content: The Defias shall succeed! No meek adventurer will stop us! */
UPDATE creature_ai_texts SET content_loc3='Ich brauche Eure Lebensessenz, um meine Existenz in diesem Reich aufrechtzuerhalten.' WHERE entry=-657; /* content: I require your life essence to maintain my existence in this realm. */
