UPDATE creature_ai_texts SET content_loc3='Göttin, gewähre mir die Stärke, meine Feinde zu besiegen! Hör mich bitte an, ich bin verzweifelt in meiner Not und meine Sache ist gerecht!' WHERE entry=-659; /* content: Goddess, grant me the power to overcome my enemies! Hear me, please, my need is desperate and my cause is just! */
