UPDATE creature_ai_texts SET content_loc3='Das riecht wie...' WHERE entry=-961; /* content: It smells like... */
UPDATE creature_ai_texts SET content_loc3='... Sieg.' WHERE entry=-962; /* content: ...victory. */
