UPDATE creature_ai_texts SET content_loc3='Die Macht des Risses gehört uns!' WHERE entry=-965; /* content: The rift\'s power is ours! */
UPDATE creature_ai_texts SET content_loc3='Kernüberladung diagnostiziert. Fehlfunktion des Systems diagnostiziert...' WHERE entry=-966; /* content: Core overload detected. System malfunction detected... */
UPDATE creature_ai_texts SET content_loc3='Triumph oder Tod! Das ist das Gesetz der Legion!' WHERE entry=-967; /* content: Beat or be beaten! This is the way of the Legion! */
UPDATE creature_ai_texts SET content_loc3='Verkohlt ihre Körper, zerfetzt ihr Fleisch, erdrückt sie in ihren erbärmlichen Panzern!' WHERE entry=-968; /* content: Burn their bodies, shred their skins, crush their creaking carapaces! */
UPDATE creature_ai_texts SET content_loc3='Zerquetscht diese stinkenden Hülsen!' WHERE entry=-969; /* content: Crush these stinking husks! */
