UPDATE creature_ai_texts SET content_loc3='Ich gebe auf! Senkt Eure Waffen!' WHERE entry=-992; /* content: I surrender! Stay your blade! */
UPDATE creature_ai_texts SET content_loc3='Ich werde Euch begleiten, aber verschont meine Männer. Es sind gute Gefolgsleute, die einen Fehler begangen haben...' WHERE entry=-993; /* content: I will go with you. Just spare my men. They\'re good lads who\'ve only made a mistake... */
UPDATE creature_ai_texts SET content_loc3='Kälbermörder! Totschläger! Dafür werdet Ihr bezahlen!' WHERE entry=-994; /* content: Calf killer! Murderer! You will pay for this! */
UPDATE creature_ai_texts SET content_loc3='Wenn Ihr das Leben gerne in vollen Zügen genießt, dann müsst Ihr T\'chalis Voodoobräu kosten!' WHERE entry=-1001; /* content: If you wanna live life to the fullest, you gotta try T\'chali\'s Voodoo Brew! */
UPDATE creature_ai_texts SET content_loc3='Hey! Zuckerschnäuzchen, hierher!' WHERE entry=-1006; /* content: Hey! Sweet cheeks, over here! */
UPDATE creature_ai_texts SET content_loc3='Yo! Große Dame! Ob Ihr wohl groß genug seid, um mich bändigen zu können?' WHERE entry=-1009; /* content: Yo! Big lady! You think you\'re big enough to handle me? */
UPDATE creature_ai_texts SET content_loc3='Sie haben Hunderte umgebracht! Wir sind nur knapp mit dem Leben davongekommen! Hilfe!' WHERE entry=-1012; /* content: They killed hundreds! We barely escaped with our lives! Help! */
