UPDATE creature_ai_texts SET content_loc3='Habt Ihr vielleicht ein bisschen Kleingeld?' WHERE entry=-1014; /* content: Could you spare a gold? */
UPDATE creature_ai_texts SET content_loc3='Das Wolke sehn aus wie da Kriegshäup\'ling.' WHERE entry=-1015; /* content: Dat cloud look like da Warchief. */
