UPDATE creature_ai_texts SET content_loc3='Der Scharlachrote Kreuzzug darf nicht scheitern!' WHERE entry=-1030; /* content: The Scarlet Crusade shall not fail in it\'s mission! */
UPDATE creature_ai_texts SET content_loc3='Die Gräueltaten der Untoten werden ausgelöscht!' WHERE entry=-1031; /* content: These undead atrocities will be destroyed! */
UPDATE creature_ai_texts SET content_loc3='Dieses Land wird geläutert werden!' WHERE entry=-1032; /* content: These lands shall be cleansed! */
