UPDATE `db_script_string` SET `content_loc3`='Ich entscheide mich für die dritte Option: EUCH TÖTEN!' WHERE `entry`=2000000198;
UPDATE `db_script_string` SET `content_loc3`='Endlich frei! Der Geist des Wassers kehrt zu meinem Volk zurück. Er wird uns die Weisheit gewähren, die wir brauchen, um in diesem unwirtlichen Land überleben können. Ich stehe in Eurer Schuld, $N.' WHERE `entry`=2000000199;
UPDATE `db_script_string` SET `content_loc3`='Ihr habt mich befreit! Die Winde sprechen wieder zu meinem Volk und gewähren ihm Stärke. Ich danke Euch, $GFremder:Fremde;.' WHERE `entry`=2000000200;
