UPDATE `db_script_string` SET `content_loc3`='Es ist eine lange Zeit gewesen, Bethor, mein Freund' WHERE `entry`=2000000208;
UPDATE `db_script_string` SET `content_loc3`='Wenn wir Zeit haben, müssen wir ausfürhlicher reden. Wir haben viel zu besprechen.' WHERE `entry`=2000000209;
UPDATE `db_script_string` SET `content_loc3`='Und danke, $N. Ohne Eure Hilfe hätte ich nie den Weg zu den Verlassenen gefunden.' WHERE `entry`=2000000210;
UPDATE `db_script_string` SET `content_loc3`='Lebt wohl, mein Freund' WHERE `entry`=2000000211;
UPDATE `db_script_string` SET `content_loc3`='%s beschwört einen Boten...' WHERE `entry`=2000000212;
