UPDATE `db_script_string` SET `content_loc3`='Nachrichten aus Jotunheim. Die dortigen Schlafwächter glauben, jemanden gefunden zu haben, der wichtig sein könnte.' WHERE `entry`=2000000289;
UPDATE `db_script_string` SET `content_loc3`='Beschreibt diesen Vrykul. Wie sieht er aus?' WHERE `entry`=2000000290;
UPDATE `db_script_string` SET `content_loc3`='Aussehen, Mylady? Wie ein Vrykul, nehme ich an. Sie haben ihn mir nicht wirklich gezeigt. Seit der Sache mit dem Schattengewölbe...' WHERE `entry`=2000000291;
