UPDATE `db_script_string` SET `content_loc3`='Unverschämter Narr! Ihr wolltet Zelemars Blut stehlen? Dafür werdet Ihr mit Eurem eigenen bezahlen!' WHERE `entry`=2000000317;
UPDATE `db_script_string` SET `content_loc3`='Hoffen wir, dass er das Licht sieht und er seine Energien von nun ab für den Aufbau des einst so schönen Quel\'Thalas einsetzt.' WHERE `entry`=2000000322;
