UPDATE `db_script_string` SET `content_loc3`='Niemals wird Kälte herrschen!' WHERE `entry`=2000000329;
UPDATE `db_script_string` SET `content_loc3`='Es stirbt! Der zweite Teil des Wortes der Verdammnis gehört uns! Ich...' WHERE `entry`=2000000330;
UPDATE `db_script_string` SET `content_loc3`='Ich schwinde... Kehrt zu Ar\'tor zurück... Keh.. rt... zu...' WHERE `entry`=2000000331;
UPDATE `db_script_string` SET `content_loc3`='Was für ein Kampf! Habt Ihr das gesehen, Rhydian?!' WHERE `entry`=2000000332;
