UPDATE `db_script_string` SET `content_loc3`='Heute wurde die Zerstörung dieser Welt abgewendet, im Widerstand gegen unsere eigenen Schöpfer!' WHERE `entry`=2000000336;
UPDATE `db_script_string` SET `content_loc3`='Algalon der Beobachter, Herold der Titanen, wurde von unseren tapferen Kameraden in den Tiefen der Titanenstadt Ulduar bezwungen.' WHERE `entry`=2000000337;
UPDATE `db_script_string` SET `content_loc3`='Algalon wurde zu uns gesandt, um über diese Welt zu richten.' WHERE `entry`=2000000338;
UPDATE `db_script_string` SET `content_loc3`='Er fand eine Welt vor, deren Völker nicht mehr den Blaupausen der Titanen entsprachen. Eine Welt, in der nicht alles nach Plan gelaufen war.' WHERE `entry`=2000000339;
