UPDATE `db_script_string` SET `content_loc3`='Na gut, also zurück zum Zeichenbrett.' WHERE `entry`=2000005398;
UPDATE `db_script_string` SET `content_loc3`='Bitte entschuldigt mich, während ich meine Tests beginne...' WHERE `entry`=2000005400;
