UPDATE `db_script_string` SET `content_loc3`='Wenn mein Blut nicht schon vor langer Zeit geronnen wäre, würde es jetzt vor Zorn kochen...' WHERE `entry`=2000005404;
UPDATE `db_script_string` SET `content_loc3`='Genießen sie die komödiantischen Einlagen von Hel\'gesh Neider in ein paar Minuten!' WHERE `entry`=2000005407;
