UPDATE `db_script_string` SET `content_loc3`='Ihr habt sicher von Nefarians Fehlschlägen in Azeroth gehört... Doch auch wenn er gescheitert ist, gehen die Versuche weiter. Mein Meister... er führt die Arbeit fort, die sein Nachkomme begonnen hat.' WHERE `entry`=2000005704;
